#define AA 3.

#define pi 3.141592
#define time iTime

// https://lospec.com/palette-list/1bit-monitor-glow
vec3 col1 = vec3(.133, .137, .137);
vec3 col2 = vec3(.941, .965, .941);

mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float fill(float d) {
    return 1. - smoothstep(0., AA / iResolution.x, d);
}

float rect(vec2 p, vec2 size) {
  vec2 d = abs(p) - size;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float linearstep(float begin, float end, float t) {
    return clamp((t - begin) / (end - begin), 0., 1.);
}

float tri(float x) {
    return abs(mod((x + .5) * 2., 2.) - 1.);
}

// https://thebookofshaders.com/edit.php?log=160909064320
float easeInOutExpo(float t) {
    if (t == 0. || t == 1.) {
        return t;
    }
    if ((t *= 2.) < 1.) {
        return .5 * exp2(10. * (t - 1.));
    } else {
        return .5 * (-exp2(-10. * (t - 1.)) + 2.);
    }
}

float easeInOutQuad(float t) {
    if ((t *= 2.) < 1.) {
        return .5 * t * t;
    } else {
        return -.5 * ((t - 1.) * (t - 3.) - 1.);
    }
}

float easeInOutCubic(float t) {
    if ((t *= 2.) < 1.) {
        return .5 * t * t * t;
    } else {
        return .5 * ((t -= 2.) * t * t + 2.);
    }
}

float easeOutQuad(float t) {
    return -1. * t * (t - 2.);
}

float easeInQuad(float t) {
    return t * t;
}

float easeInExpo(float t) {
    return (t == 0.) ? 0. : pow(2., 10. * (t - 1.));
}

float bold(vec2 uv) {
    uv *= 1.3;
    float t1 = linearstep(.5, 1., fract(time*.5));
    float t2 = linearstep(0., .8, fract(time*.5));
    t2 = easeInExpo(t2);
    t1 = easeInOutExpo(t1);

    // alternative
    /*
    float s1 = mix(.1, .3, t1);
    float offs = mix(.2, .4, t1);//.2
    float alpha = mix(.5, 1., t1);
    float mask = fill(rect(uv+vec2(0, .2), vec2(s1))) * alpha;
    mask += fill(rect(uv+vec2(offs, .2), vec2(.1)));
    mask += fill(rect(uv-vec2(0, -.2 + offs), vec2(.1)));
    mask += fill(rect(uv-vec2(offs, -.2), vec2(.1)));
	*/

    float s1 = mix(.05, .3, t1);
    float offs = mix(.7, .42, t2);//.2
    float alpha = mix(.5, 1., t1);
    float mask = fill(rect(uv+vec2(0, .2), vec2(s1))) * alpha;
    mask += fill(rect(uv+vec2(offs, .2), vec2(.12)));
    mask += fill(rect(uv-vec2(0, -.2 + offs), vec2(.12)));
    mask += fill(rect(uv-vec2(offs, -.2), vec2(.12)));

    return mask;
}

float playful(vec2 uv) {
    float t1 = fract(time);
    //t1 = easeInOutQuad(t1);

    float alpha = mix(.5, .8, tri(t1));;

    vec2 south = vec2(0, .3);
    vec2 east = vec2(.3, 0);
    vec2 north = vec2(0, -.3);
    vec2 west = vec2(-.3, 0);


    vec2 pos1 = mix(south, east, t1);
    vec2 pos2 = mix(east, north, t1);
    vec2 pos3 = mix(north, west, t1);
    vec2 pos4 = mix(west, south, t1);

    float mask = fill(rect((uv-pos1)*r2d(t1*pi), vec2(.1))) * alpha;
    mask += fill(rect((uv-pos2)*r2d(-t1*pi), vec2(.1))) * alpha;
    mask += fill(rect((uv-pos3)*r2d(t1*pi), vec2(.1))) * alpha;
    mask += fill(rect((uv-pos4)*r2d(-t1*pi), vec2(.1))) * alpha;

    return mask;
}

float nervous(vec2 uv) {
    float t1 = fract(time*.5);
    t1 = easeInOutQuad((t1));

    float offs = mix(.5, .25, t1);
    float alpha = .5;
    float shake = sin(time*30.*pi)*.01;
    float mask = fill(rect(uv+vec2(shake, .2), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(offs, .2), vec2(.12)));
    mask += fill(rect(uv-vec2(0, -.2 + offs), vec2(.12)));
    mask += fill(rect(uv-vec2(offs, -.2), vec2(.12)));

    return mask;
}

float wistful(vec2 uv) {
    float t1 = fract(time*.5);
    t1 = easeInOutQuad(t1);

    float dx = mix(0., .3, t1);

    float alpha = mix(1., 0., t1);
    float mask = 0.;
    mask += fill(rect(uv+vec2(dx, -.37), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(-dx, -.12), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(dx, .12), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(-dx, .37), vec2(.1))) * alpha;
    return mask;
}

float ordered(vec2 uv) {
    float t1 = fract(time);
    t1 = easeInOutExpo(t1);

    if(fract(time*.5) < .5)
    	uv.x += mix(.4, 0., t1);
    else
    	uv.x += mix(0., -.4, t1);

    float alpha = 1.;//mix(1., 0., t1);
    float mask = 0.;
    mask += fill(rect(uv+vec2(0, -.37), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(0, -.12), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(0, .12), vec2(.1))) * alpha;
    mask += fill(rect(uv+vec2(0, .37), vec2(.1))) * alpha;
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    float t1 = fract(time*.1);
    float mask = 0.;
    if(t1 < 2./10.)
        mask = bold(uv);
    else if(t1 < 4./10.)
        mask = playful(uv);
	else if(t1 < 6./10.)
        mask = nervous(uv);
    else if(t1 < 8./10.)
        mask = wistful(uv);
	else
        mask = ordered(uv);


    //mask = bold(uv);

    mask = clamp(mask, 0., 1.);
    vec3 col = mix(col1, col2, 1. - mask);

    col += .05 * texture(iChannel0, uv).r * texture(iChannel1, uv).r;
    fragColor = vec4(col,1.0);
}

#define POINT_COUNT 10

vec2 points[POINT_COUNT];
const float speed = -0.6;
const float len = 0.40;
const float scale = 0.015;
float intensity = 1.2;
float radius = 0.03;

//https://www.shadertoy.com/view/MlKcDD
//Signed distance to a quadratic bezier
float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C){    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    if(h >= 0.0){ 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = uv.x + uv.y - kx;
        t = clamp( t, 0.0, 1.0 );

        // 1 root
        vec2 qos = d + (c + b*t)*t;
        res = length(qos);
    }else{
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        t = clamp( t, 0.0, 1.0 );

        // 3 roots
        vec2 qos = d + (c + b*t.x)*t.x;
        float dis = dot(qos,qos);

        res = dis;

        qos = d + (c + b*t.y)*t.y;
        dis = dot(qos,qos);
        res = min(res,dis);

        qos = d + (c + b*t.z)*t.z;
        dis = dot(qos,qos);
        res = min(res,dis);

        res = sqrt( res );
    }

    return res;
}


//http://mathworld.wolfram.com/HeartCurve.html
vec2 getHeartPosition(float t){
    return vec2(22.0 * sin(t) * sin(t) * sin(t),
                -(13.0 * cos(t) - 5.0 * cos(2.0*t)
                - 2.0 * cos(3.0*t) - cos(4.0*t)));
}

//https://www.shadertoy.com/view/3s3GDn
float getGlow(float dist, float radius, float intensity){
    return pow(radius/dist, intensity);
}

float getSegment(float t, vec2 pos, float offset){
	for(int i = 0; i < POINT_COUNT; i++){
        points[i] = getHeartPosition(offset + float(i)*len + fract(speed * t) * 6.28);
    }

    vec2 c = (points[0] + points[1]) / 2.0;
    vec2 c_prev;
	float dist = 10000.0;

    for(int i = 0; i < POINT_COUNT-1; i++){
        //https://tinyurl.com/y2htbwkm
        c_prev = c;
        c = (points[i] + points[i+1]) / 2.0;
        dist = min(dist, sdBezier(pos, scale * c_prev, scale * points[i], scale * c));
    }
    return max(0.0, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    float widthHeightRatio = iResolution.x/iResolution.y;
    vec2 centre = vec2(0.5, 0.5);
    vec2 pos = centre - uv;
    pos.y /= widthHeightRatio;
    //Shift upwards to centre heart
    pos.y += 0.03;

    float t = iTime;

    //Get first segment
    float dist = getSegment(t, pos,3.1);
    float glow = getGlow(dist, radius, intensity);

    vec3 col = vec3(0.0);

    //White core
    col += 0.1*vec3(smoothstep(0.040, 0.021, dist));
    //Pink glow
    col += glow * vec3(0.94,0.14,0.4);

    //Get second segment
    dist = getSegment(t, pos, 0.0);
    glow = getGlow(dist, radius, intensity);

    //White core
    col += 0.1*vec3(smoothstep(1.015, 1.07, dist));
    //Blue glow
    col += glow * vec3(0.0,0.23,0.56);

    //Tone mapping
    col = 0.9 - exp(-col);

    //Output to screen
    fragColor = vec4(col,0.9);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy) /iResolution.y;
    float t = iTime*3.;


    float circle = length(uv) - 0.23;
    float rect =  max(abs(uv.x),abs(uv.y)) - 0.1;

    //interpolate weight (0,1) 0: circle, 1: rect
    float w = -sin(t+sin(t+sin(t)))*0.5+0.5; 

    float f = circle*(1.-w)+ w*rect; //linear lerp


    f = smoothstep(0.0,-0.01,f) - smoothstep(-0.01,-0.02,f);    
    vec3 col = vec3(f);
    fragColor = vec4(col,1.0);
}

#define NUM_OCTAVES 10

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}



float fbm ( in vec2 _st) {
    float t = 1.;
    float v = 0.;
    float a = 0.5;
    vec2 shift = vec2(120.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.5));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

vec2 addfbm(vec2 uv) {
    return uv + vec2(fbm(uv), fbm(uv+vec2(314.433623, 234.62324)));
}

vec3 makeFbm(vec2 uv) {
    vec2 noiseUv = addfbm(uv);
    noiseUv.x += iTime*.1;
    noiseUv = addfbm(noiseUv);
    noiseUv = addfbm(noiseUv);
    noiseUv = addfbm(noiseUv);
    noiseUv.x += iTime*.1;
    noiseUv = addfbm(noiseUv);

    float m = fbm(noiseUv*5.);
    vec3 col = vec3(pow(m, 4.));
    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    vec3 ro = vec3(0,0,0);
    float z = 1.;
    vec3 rd = normalize(vec3(uv, z)-ro);

    float t=0.;
    float step;

    for(int i=0; i<128; i++){
        vec3 p = ro + rd*t;

        float r = makeFbm(p.xy).x*2.;
        float dS = length(p-vec3(0,0,3))-1.+r;
        dS = max(abs(dS), 0.02);

        step = float(i);
        if (t > 100. || dS < 0.01) break;
        t += dS;
    }

    col = vec3(pow(step/128., 1. + sin(iTime)*.5+.5));


    // Output to screen
    fragColor = vec4(col,1.0);
}

