    degree = mod( degree - 3.14159  / (segments * 2.) , 3.14159  / segments );
    degree -= 3.14159  / (segments * 2.);


    p2.x = l * sin( degree );
    p2.z = l * cos( degree );

    p2.x += sin( p2.y * 3. + which * 2. + iTime * which * .1) * .02;
    res = opSmoothUnion( .04 * noise( pos * 5. ) * p2.y + sdVerticalCapsule( p2 - vec3(0.,-(2.1 + sin( 6.*which * 3.14159 / segments)),.6 - .1*p2.y),3.2 + sin(  6.*which* 3.14159 / segments), .1 * max(-p2.y * .3,1.)),res ,.03);


    float pluemVal =  .4 * noise( pos * 5. ) + sdVerticalCapsule( pos - vec3(0.,-7.,0.), 8., .4  * max(.5,.4*-pos.y) );
   	pluemVal = opSmoothUnion( .2 * noise( pos * 8. ) + sdVerticalCapsule( pos - vec3(0.,-7.,0.), 8., .4  * max(.5,.5*-pos.y) ),pluemVal ,.03);
   	pluemVal = opSmoothUnion( .1 * noise( pos * 9. ) + sdVerticalCapsule( pos - vec3(0.,-7.,0.), 8., .4  * max(.5,.6*-pos.y) ),pluemVal ,.03);

    // long tenkz

    p2 = pos;
    degree = atan( p2.x , p2.z );

    ogD = degree;


    l = length( p2.xz );
     segments = 6.;
    which = floor((degree - 3.14159  / (segments*2.)) / ( 3.14159 / segments)- 3.14159  / (segments * 2.));
    degree = mod( degree - 3.14159  / (segments * 2.) , 3.14159  / segments );
    degree -= 3.14159  / (segments * 2.);


    p2.x = l * sin( degree );
    p2.z = l * cos( degree );

    p2.x += sin( p2.y * 3. + which * 2. + iTime * which * .1) * .02;

    float extras = sdVerticalCapsule( p2 - vec3(0.,-8.,-.2*p2.y),8.2,.05 * max(-p2.y * .5,1.));
    pluemVal = opSmoothSubtraction(  extras, pluemVal ,.3);
    pluemVal = opSmoothUnion(  extras, pluemVal ,.01);
   	pluemVal = opSmoothUnion( sdVerticalCapsule(pos- vec3(0.,-1.,0.), 2.,.2),pluemVal,.2);
    pluemVal = opSmoothSubtraction( sdVerticalCapsule(pos- vec3(0.,-10.,0.), 2.,1.2),pluemVal,.6);

    res = opSmoothUnion( res , pluemVal , .1 );

    return vec2(res,1.);

}

vec2 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec2( res , id );

}



// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}


vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.yx + 2.0*fragCoord.yx)/iResolution.y;
