void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.st / iResolution.st;
	     uv = uv * 2.0 - 1.0;

	float aspectRatio = iResolution.s / iResolution.t;
	uv.x *= aspectRatio;

	vec2 screenCentre = vec2(0.0);
	float camToScreenDistance = 1.0;

	vec2 centreToUV = uv - screenCentre;
	vec3 camToUV = vec3(centreToUV, camToScreenDistance);
	vec3 camToCentre = vec3(0.0, 0.0, camToScreenDistance);

	vec3 normCamToUV = normalize(camToUV);
	vec3 normCamToCentre = normalize(camToCentre);


	//vec3 sphereCentre = vec3(sin(iTime) * 2.0 - 1.0, cos(iTime) * 2.0 - 1.0, 3.0 + (sin(iTime * 10.0) * 2.0 - 1.0) * 0.5);
    vec3 sphereCentre = vec3(sin(iTime * 5.0), cos(iTime * 5.0), 1.0 + (cos(iTime * 10.0) + 1.0) * 2.0);
	float sphereRadius = 1.0;




	vec3 posInScreen = vec3(uv, 0.0);
	vec3 pos = vec3(uv, -camToScreenDistance);

	float avaliable = 0.0;
	float depthMap = 1.0;

	const int samp = 64;
	float stepLength = 0.1;
	for(int i = 0; i <= samp; i++)
	{
		pos += normCamToUV * stepLength;
		float map = Sphere(sphereCentre - pos, sphereRadius);

		if(map < 0.02)
		{
			avaliable = 1.0;
			depthMap += length(pos - posInScreen) / (float(samp) * stepLength) - 1.0;
			break;
		}
	}

	depthMap = clamp(depthMap, 0.0, 1.0);

	fragColor = vec4(vec3(depthMap), 1.0);

}

