#define time (speed*(bpm/60.)*iTime)

mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float fill(float d) {
    return 1. - smoothstep(0., AA / iResolution.x, d);
}

// inspired by Pixel Spirit Deck: https://patriciogonzalezvivo.github.io/PixelSpiritDeck/
// + https://www.shadertoy.com/view/tsSXRz
float stroke(float d, float width) {
	return 1. - smoothstep(0., AA / iResolution.x, abs(d) - width * .5);
}

float flip(float value, float percent) {
	return mix(value, 1. - value, percent);
}

float circle(vec2 p, float radius) {
  return length(p) - radius;
}

// https://thebookofshaders.com/edit.php?log=160909064320
float easeInOutExpo(float t) {
    if (t == 0. || t == 1.) {
        return t;
    }
    if ((t *= 2.) < 1.) {
        return .5 * exp2(10. * (t - 1.));
    } else {
        return .5 * (-exp2(-10. * (t - 1.)) + 2.);
    }
}

// not used, but can be
float easeInOutQuad(float t) {
    if ((t *= 2.) < 1.) {
        return .5 * t * t;
    } else {
        return -.5 * ((t - 1.) * (t - 3.) - 1.);
    }
}

// not used, but can be
float easeInOutCubic(float t) {
    if ((t *= 2.) < 1.) {
        return .5 * t * t * t;
    } else {
        return .5 * ((t -= 2.) * t * t + 2.);
    }
}

