//--------------------------------------------------------------------------
#define EPSILON 0.001
#define GAMMA 2.2

#define CAM_POS vec3(1.0, 0.0, -1.0)
#define CAM_LOOKAT vec3(0.0, 0.0, 0.0)

#define MAX_STEPS 200
#define MAX_DIST 100.0

#define BASE_COLOR_MULTIPLIER 0.2;
#define MAX_SHININESS 32.0

#define SUN_DIR vec3(0.6, 0.9, -0.4)
#define SUN_COLOR vec3(1.0, 0.8, 0.6)
#define SUN_INTENSITY 10.0

#define SAND_COLOR vec3(0.7, 0.7, 0.5)
#define STRUCTURE_COLOR vec3(0.53, 0.27, 0.18)

#define SKY_LIGHT_COLOR vec3(0.0, 0.1, 0.3)
#define SKY_LIGHT_INTENSITY 1.0

#define BOUNCE_LIGHT_COLOR vec3(0.8, 0.6, 0.4)
#define BOUNCE_LIGHT_INTENSITY 1.0

#define SKY_COLOR vec3(0.1, 0.9, 1.0)
#define SPECULAR_COLOR vec3(0.8, 0.8, 0.8)
#define SHADOW_COLOR vec3(0.25, 0.16, 0.1)

vec3 xSymOp(vec3 pos)
{
    return vec3(abs(pos.x), pos.yz);
}

vec3 zSymOp(vec3 pos)
{
    return vec3(pos.xy, abs(pos.z));
}

vec3 xzSymOp(vec3 pos)
{
    return vec3(abs(pos.x), pos.y, abs(pos.z));
}


mat2 rotOp(float angle)
{
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    return rot;
}

vec2 minOp(vec2 obj1, vec2 obj2)
{
    vec2 min;

    if(obj1.x <= obj2.x)
    {
        min = obj1;
    }
    else
    {
        min = obj2;
    }

    return min;    
}

vec2 maxOp(vec2 obj1, vec2 obj2)
{
    vec2 max;

    if(obj1.x >= obj2.x)
    {
        max = obj1;
    }
    else
    {
        max = obj2;
    }

    return max;    
}

float sMin(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - (k * h * (1.0 - h));
}

float planeSDF(vec3 pos, vec3 up)
{
    float dist = dot(normalize(up), pos);
    return dist;
}

float sphereSDF(vec3 pos, float radius)
{
    float dist = length(pos) - radius;
    return dist;
}

float boxSDF(vec3 pos, vec3 size)
{
    float dist = length(max(abs(pos) - size, 0.0));
    return dist;
}

float vertCapsuleSDF(vec3 pos, float height, float radius)
{
    pos.y -= clamp(pos.y, 0.0, height);
    float dist = length(pos) - radius;
    return dist;
}

float capCylinderSDF(vec3 pos, float height, float radius)
{
    vec2 d = abs(vec2(length(pos.xz), pos.y)) - vec2(radius, height);
    float dist = min(max(d.x,d.y),0.0) + length(max(d,0.0));
    return dist;
}

float pillarSDF(vec3 pos, vec3 size, float rounding)
{
    float pillarDist = boxSDF(pos, size) - rounding;
    float baseDist = capCylinderSDF(pos - vec3(0.0, -0.28, 0.0), 0.03, 0.08);

    float topDist = capCylinderSDF(pos - vec3(0.0, 0.31, 0.0), 0.03, 0.08); 
    float dist = min(pillarDist, min(baseDist, topDist));

    vec3 r = pos;
    vec3 sym = xzSymOp(r);
    vec3 c = sym - vec3(0.06, -0.2, 0.06);
    float carvingDist = vertCapsuleSDF(c, 0.4, 0.03);

    r.xz *= rotOp(40.0);
    sym = xzSymOp(r);
    c = sym - vec3(0.06, -0.2, 0.06);
    float carvingDist2 = vertCapsuleSDF(c, 0.4, 0.03);
    carvingDist = min(carvingDist, carvingDist2);

    return max(dist, -carvingDist);
}

vec2 structureSDF(vec3 pos)
{
    vec3 sym = xzSymOp(pos - vec3(0.0, 0.0, 0.0));
    vec3 cen = sym - vec3(0.7, 0.0, 0.25);
    float pillarSet1Dist = pillarSDF(cen, vec3(0.01, 0.3, 0.01), 0.05);

    sym = xzSymOp(pos - vec3(0.0, 0.0, 1.0));
    cen = sym - vec3(0.7, 0.0, 0.25);
    float pillarSet2Dist = pillarSDF(cen, vec3(0.01, 0.3, 0.01), 0.05);

    float damageDist = vertCapsuleSDF(pos - vec3(0.66, 0.1, 0.2), 0.4, 0.15);
    pillarSet1Dist = max(pillarSet1Dist, -damageDist);

    float pillarDist = min(pillarSet1Dist, pillarSet2Dist);

    sym = xzSymOp(pos - vec3(0.0, 0.0, 2.5));
    float pyrDist = dot(normalize(vec3(0.1, 0.1, 0.1)), sym - 0.3);

    float plate1Dist = boxSDF(pos - vec3(0.0, -0.2, 2.5), vec3(1.1, 0.25, 1.1));
    float plate2Dist = boxSDF(pos - vec3(0.0, 0.3, 2.5), vec3(1.1, 0.2, 1.1));
    float plate3Dist = boxSDF(pos - vec3(0.0, 0.8, 2.5), vec3(1.1, 0.25, 1.1));

    pyrDist = min(min(max(pyrDist, plate1Dist), max(pyrDist, plate2Dist)), max(pyrDist, plate3Dist));

    float strucDist = min(pillarDist, pyrDist);
    vec2 struc = vec2(strucDist, 2.0);

    sym = xzSymOp(pos - vec3(0.0, 0.0, 0.5));
    float pathDist = boxSDF(sym - vec3(0.0, -0.3, 0.0), vec3(0.2, 0.01, 1.0));
    vec2 path = vec2(pathDist, 3.0);

    vec2 obj = minOp(struc, path);
    return obj;
}

float dunes(vec3 pos)
{
    float waves = (sin(pos.z + 1.2) * 0.2) - 0.2 + (cos(pos.x + 0.7) * 0.2) - 0.11;

    float dunes1 = max(min(sin(pos.z  - 2.2), cos(pos.x * 0.5 - 3.0)), 0.0);
    float dunes2 = max(min(sin(pos.x - 1.5), cos(pos.z * 0.2 - 1.0)), 0.0);

    return max(max(dunes1, dunes2), waves);
}

vec2 sceneSDF(vec3 pos)
{
    float face1Dist = sphereSDF(pos - vec3(-0.2, 1.3, 2.5), 0.3); 
    float face2Dist = sphereSDF(pos - vec3(0.2, 1.3, 2.5), 0.3);
    float faceDist = max(face1Dist, face2Dist);

    float ballDist = sphereSDF(pos - vec3(0.0, 1.3, 2.5), 0.16);
	faceDist = max(faceDist, -ballDist);
    vec2 face = vec2(faceDist, 4.0);

    float planeDist = planeSDF(pos - vec3(0.0, -0.3, 0.0), vec3(0.0, 1.0, 0.0)) - dunes(pos);
    vec2 plane = vec2(planeDist, 1.0);

    vec2 struc = structureSDF(pos);
    vec2 scene = minOp(minOp(plane, struc), face);

    return mix(scene, plane, round(sin(iTime * 0.5 + 1.0) * 0.5 + 0.5));
    //return minOp(plane, scene);
}

vec2 rayMarch(vec3 rayOrig, vec3 rayDir)
{
    float t = 0.0;
    float id = 0.0;

    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 pos = rayOrig + t * rayDir;

        vec2 dist = sceneSDF(pos);

        if(dist.x < EPSILON)
        {
            id = dist.y;
            break;
        }

        t += dist.x;


        if(t > MAX_DIST)
            break;
    }

    return vec2(t, id);
}

vec3 getNormal(vec3 pos)
{
    vec2 e = vec2(EPSILON, 0.0);
    vec3 normal = normalize(vec3(sceneSDF(pos + e.xyy).x - sceneSDF(pos - e.xyy).x,
                       sceneSDF(pos + e.yxy).x - sceneSDF(pos - e.yxy).x,
                       sceneSDF(pos + e.yyx).x - sceneSDF(pos - e.yyx).x));

    return normal;
}

vec3 getLighting(vec3 pos, vec3 viewDir, float specIntensity, float shininess)
{   
    vec3 normal = getNormal(pos);

    float nDotL = clamp(dot(normal, SUN_DIR), 0.0, 1.0);
    vec3 sunCol = SUN_COLOR * nDotL;
    sunCol *= SUN_INTENSITY * BASE_COLOR_MULTIPLIER;

    nDotL = clamp(dot(normal, vec3(0.0, 1.0, 0.0)) * 0.5 + 0.5, 0.0, 1.0);
    vec3 skyCol = SKY_LIGHT_COLOR * nDotL;
    skyCol *= SKY_LIGHT_INTENSITY * BASE_COLOR_MULTIPLIER;

    nDotL = clamp(dot(normal, vec3(0.0, -1.0, 0.0)) * 0.5 + 0.5, 0.0, 1.0);
    vec3 bounceCol = BOUNCE_LIGHT_COLOR * nDotL;
    bounceCol *= BOUNCE_LIGHT_INTENSITY * BASE_COLOR_MULTIPLIER;

    //vec3 reflectDir = reflect(SUN_DIR, normal);
    vec3 halfwayDir = normalize(SUN_DIR + viewDir);
    float power = shininess * MAX_SHININESS;
    float spec = pow(max(dot(halfwayDir, viewDir), 0.0), power);
    vec3 specCol = spec * specIntensity * SPECULAR_COLOR;

    float dist = rayMarch(pos + normal * 0.001, SUN_DIR).x;
    if(dist < length(SUN_DIR))
    {
        return sunCol * SHADOW_COLOR + skyCol + bounceCol;
    }

    return sunCol + skyCol + bounceCol + specCol; 
}

vec3 getRayDir(vec3 pos, vec3 lookAt, vec2 uv, float fov)
{  
    float z = 1.0 / tan((radians(fov) / 2.0));

    vec3 zDir = normalize(lookAt - pos);
    vec3 xDir = normalize(cross(vec3(0.0, 1.0, 0.0), zDir));
    vec3 yDir = normalize(cross(zDir, xDir));

    vec3 rayDir = normalize(vec3(uv.x * xDir + uv.y * yDir + z * zDir));
    return rayDir;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float angle = 0.0;//10.0 * iMouse.x / iResolution.x;
    vec3 rayOrig = CAM_POS * vec3(-sin(angle), 1.0, cos(angle));
    vec3 rayDir = getRayDir(rayOrig, CAM_LOOKAT, uv, 60.0);
    vec3 viewDir = normalize(CAM_LOOKAT - rayOrig);

    vec3 col = mix(SKY_COLOR, vec3(0.9, 0.9, 0.9), 1. - sqrt(rayDir.y));

    vec2 dist = rayMarch(rayOrig, rayDir);

    if(dist.x < MAX_DIST)
    {
        vec3 pos = rayOrig + dist.x * rayDir;   

        if(dist.y < 1.5)
        {
            col = SAND_COLOR * getLighting(pos, viewDir, 0.2, 1.0);// * (texture(iChannel0, vec2(pos.x * 4.0, pos.z * 4.0)).x - 0.07) * 2.0;
        }
        else if(dist.y < 2.5)
        {
            col = STRUCTURE_COLOR * getLighting(pos, viewDir, 1.0, 0.1) * texture(iChannel1, vec2(pos.y, pos.z)).xyz;
        }
        else
        {
            col = STRUCTURE_COLOR * getLighting(pos, viewDir, 1.0, 0.1) * texture(iChannel1, vec2(pos.x * 8.0, pos.z * 16.0)).xyz;
        }

        col = pow(col, vec3(1.0 / GAMMA));
        //col = getNormal(pos);
    }

    col *= smoothstep(0.0, 1.0, 1.0 - length(uv.x * uv.y) + sin(iTime));
    //col = texture(iChannel2, rayDir).xyz;

    fragColor = vec4(col,1.0);
}

#define TWO_PI 6.28318530718

vec3 hsb2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6. + vec3(0. , 4., 2.), 6.) - 3.) - 1., 0., 1. );
    rgb = rgb * rgb * (3. - 2. * rgb);
    return c.z * mix(vec3(1.), rgb, c.y);
}


mat2 zrotate(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // 0.0を原点に -0.5〜0.5にする
    vec2 toCenter = vec2(0.5) - uv;
    // 回転
    toCenter *= zrotate(iTime);

    // 原点からの距離 
    float radius = length(toCenter);
    // 0〜1.0にする
    radius *= 2.;

     // 原点のX軸とUVのラジアンを算出
    float angle = atan(toCenter.y,toCenter.x);
    // ラジアン(-π〜π)をπ * 2で割ることで-0.5〜0.5にする
    angle /= TWO_PI;
    // 0.0〜1.0に
    angle += 0.5;

    // 極座標系作成
    vec3 hsb = vec3(angle, radius, 1.);   
    // hsbからrgbに変換
    vec3 color = hsb2rgb(hsb);

    // Output to screen
    fragColor = vec4(color,1.);
}

float fn(float x, float thres)
{
    float n = abs(sin(x * 2. * 3.14159265));
    return step(thres, (n - 0.5) * 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float P = 4.;
    float time = mod(iTime, P);
    const float PI = 3.14159265;
    const float PI_3 = PI / 3.;
    float hue = time / P * PI;

    // Normalized pixel coordinates (from 0 to 1)
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= vec2(iResolution.x / scale, iResolution.y / scale) / 2.;
    uv *= 2.0;

    //float distance = length(uv);
    //float distance = pow(sqrt(uv.x*uv.x+uv.y*uv.y), 1./3.);
    float distance = log(uv.x*uv.x+uv.y*uv.y) / 2.;
    float angle = atan(uv.y, uv.x) / PI / 2.;

    // Time varying pixel color
    float thres1 = 0.4 * sin(time * 2. * PI / P) + 0.5;
    float thres2 = 0.4 * cos(time * 2. * PI / P) + 0.5;
    float c1 = fn(distance * 0.2 + 2. * angle + time, thres1);
    float c2 = fn(distance * 0.9 + angle - time, thres2);

    // Flashing
    const float flashIntvl = P / 6.;
    const float flashPow = 1.1;
    float f1 = max(0., 1. - mod(time, flashIntvl) * 6.);
    float f2 = max(0., 1. - mod(time + flashIntvl * 0.5, flashIntvl) * 6.);
	c1 += f1 * flashPow; c2 += f2 * flashPow;

    // Output to screen
    fragColor = vec4(
        abs(c1 * sin(hue))        + abs(c2 * -sin(hue + PI_3)),
        abs(c1 * sin(hue + PI_3)) + abs(c2 * -sin(hue - PI_3)),
        abs(c1 * sin(hue - PI_3)) + abs(c2 * -sin(hue)),
        1
    );
}

mat2 Rotate(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Star(vec2 uv, float flare){
	float d = length(uv);
    float m = .05/d;

    float rays = max(0., 1.-abs(uv.x * uv.y * 1000.));
   	m += rays * flare;
    uv *= Rotate(3.1415/4.);
    rays = max(0., 1.-abs(uv.x * uv.y * 1000.));
   	m += rays*.3 * flare;

    return m;
}

float Hash21(vec2 p) {
	p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	uv *= 3.;

    vec3 col = vec3(0);

    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

    for(int y=-1;y<=1;y++){
    	for(int x=-1;x<=1;x++){
            vec2 offs = vec2(x,y);
        	float n = Hash21(id+offs);
    		col += Star(gv-offs-vec2(n,fract(n*34.))+.5, abs(sin(iTime)));    
        }
    }


    //if (gv.x>.48 || gv.y>.48) col.r=1.0;


    fragColor = vec4(col,1.0);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float t =  -iTime * 3. + 5000. +  sin(iTime / 3.) * 5.;

    float dist = distance(uv, vec2(0., 0.)) * .6;
    float maxDist = .5;
    vec4 color;

    float expDist = dist * dist * dist;
    float strength = (sin(expDist * 100.)+1.)/2.;
    float height = (sin(t * strength)+1.)/2.;
    float alpha = 1. - expDist / (maxDist * maxDist * maxDist) + (1. - height) * -0.014  ;
    color = vec4(.9,.9,.9, 9.) * height - (1. - alpha) * 0.652;
    color.a = alpha;
    if(dist > maxDist) color = vec4(.1,.1,.1, 0.);
    fragColor = color;
}

#define SHADERTOY
#define SHADERTOY_ZOOM 2.0
precision mediump float;
uniform vec3 uOrigin;
uniform mat3 uView;
uniform vec2 uInvSize;


#define PI 3.141592653589793
#define PI_2 1.5707963267948966
#define SQRT1_2 0.7071067811865476
#define SQRT2 1.4142135623730951

// raymarching code from https://www.shadertoy.com/view/Xds3zN

// blend color for chamfer and round operators (sd is simple operator distance)
vec3 colorBlending(const in float sd, const in vec4 a, const in vec4 b) {
  float ra = clamp(sd / a.x, 0.0, 1.0);
  float rb = clamp(sd / b.x, 0.0, 1.0);
  return (a.yzw * ra + b.yzw * rb) / (ra + rb);
}

vec3 colorUnion(const in vec4 a, const in vec4 b){
#ifdef BLEND_COLOR
  return colorBlending(min(a.x, b.x), a, b);
#else
  return (a.x < b.x) ? a.yzw : b.yzw;
#endif
}

vec3 colorInter(const in vec4 a, const in vec4 b){
#ifdef BLEND_COLOR
  return colorBlending(max(a.x, b.x), a, b);
#else
  return (a.x < b.x) ? a.yzw : b.yzw;
#endif
}

vec2 pR45(const in vec2 p) {
  return (p + vec2(p.y, -p.x)) * SQRT1_2;
}

/////////////
// PRIMITIVES
/////////////

float cullPlane(const in vec3 p) {
  return p.y >= -0.502 ? p.y + 0.5 : 20.0;
}

float sdSphere(const in vec3 p, const in float s) {
  return length(p) - s;
}

float sdBox(const in vec3 p, const in vec4 b) {
  vec3 d = abs(p) - b.xyz;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0)) - b.w;
}

float sdTorus(const in vec3 p, const in vec2 t) {
  return length(vec2(length(p.xz) - t.x, p.y)) - t.y;
}

float sdCapsule(const in vec3 p, const in vec2 rh) {
  return mix(length(p.xz) - rh.x, length(vec3(p.x, abs(p.y) - rh.y, p.z)) - rh.x, step(rh.y, abs(p.y)));
}

float sdEllipsoid(const in  vec3 p, const in vec3 r) {
  return (length(p / r) - 1.0) * min(min(r.x, r.y), r.z);
}

///////////////
// COMBINATIONS
///////////////

// UNION
float opUnion(const in float a, const in float b) {
  return min(a, b);
}
vec4 opUnion(const in vec4 a, const in vec4 b) {
  return (a.x < b.x) ? a : b;
}

// INTER
float opInter(const in float a, const in float b) {
  return max(a, b);
}
vec4 opInter(const in vec4 a, const in vec4 b) {
  return (a.x > b.x) ? a : b;
}

// SUB
float opSub(const in float a, const in float b) {
  return max(-b, a);
}
vec4 opSub(const in vec4 a, const in vec4 b) {
  // to keep b material on diff intersection instead of a mat
  // return (-b.x > a.x) ? vec4(-b.x, b.yzw) : a);
  return vec4(max(-b.x, a.x), a.yzw);
}

// see hg_sdf.glsl
/////////////////////
// COMBINATIONS ROUND
/////////////////////

// UNION ROUND (soft media mol version)
float opUnionRound(const in float a, const in float b, const in float r) {
  float e = max(r - abs(a - b), 0.0);
  return min(a, b) - e * e * 0.25 / r;
}
vec4 opUnionRound(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opUnionRound(a.x, b.x, r), colorUnion(a, b));
}

// INTER ROUND
float opInterRound(const in float a, const in float b, const in float r) {
  vec2 u = max(vec2(r + a, r + b), vec2(0.0));
  return min(-r, max(a, b)) + length(u);
}
vec4 opInterRound(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opInterRound(a.x, b.x, r), colorInter(a, b));
}

// SUB ROUND
float opSubRound(const in float a, const in float b, const in float r) {
  return opInterRound(a, -b, r);
}
vec4 opSubRound(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opInterRound(a.x, -b.x, r), a.yzw);
}

///////////////////////
// COMBINATIONS CHAMFER
///////////////////////

// UNION CHAMFER
float opUnionChamfer(const in float a, const in float b, const in float r) {
  return min(min(a, b), (a - r + b) * SQRT1_2);
}
vec4 opUnionChamfer(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opUnionChamfer(a.x, b.x, r), colorUnion(a, b));
}

// INTER CHAMFER
float opInterChamfer(const in float a, const in float b, const in float r) {
  return max(max(a, b), (a + r + b) * SQRT1_2);
}
vec4 opInterChamfer(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opInterChamfer(a.x, b.x, r), colorInter(a, b));
}

// SUB CHAMFER
float opSubChamfer(const in float a, const in float b, const in float r) {
  return opInterChamfer(a, -b, r);
}
vec4 opSubChamfer(const in vec4 a, const in vec4 b, const in float r) {
  return vec4(opInterChamfer(a.x, -b.x, r), a.yzw);
}

//////////////////////
// COMBINATION COLUMNS
//////////////////////

// UNION COLUMNS
float opUnionColumns(const in float a, const in  float b, const in vec2 rn) {
  float r = rn.x;
  float n = rn.y;
  if((a < r) && (b < r)) {
    vec2 p = vec2(a, b);
    float columnradius = r * SQRT2 / ((n - 1.0) * 2.0 + SQRT2);
    p = pR45(p);
    p.x -= SQRT1_2 * r;
    p.x += columnradius * SQRT2;
    if(mod(n, 2.0) == 1.0) {
      p.y += columnradius;
    }
    // At this point, we have turned 45 degrees and moved at a point on the
    // diagonal that we want to place the columns on.
    // Now, repeat the domain along this direction and place a circle.
    p.y = mod(p.y + columnradius, columnradius * 2.0) - columnradius;
    float result = length(p) - columnradius;
    result = min(result, p.x);
    result = min(result, a);
    return min(result, b);
  } 

  return min(a, b);
}
vec4 opUnionColumns(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(opUnionColumns(a.x, b.x, rn), colorUnion(a, b));
}

// SUB COLUMNS
float opSubColumns(const in float ain, const float b, const vec2 rn) {
  float a = -ain;
  float r = rn.x;
  float n = rn.y;
  float m = min(a, b);
  //avoid the expensive computation where not needed (produces discontinuity though)
  if((a < r) && (b < r)) {
    vec2 p = vec2(a, b);
    float columnradius = r * SQRT2 / n / 2.0;
    columnradius = r * SQRT2 / ((n - 1.0) * 2.0 + SQRT2);

    p = pR45(p);
    p.y += columnradius;
    p.x -= SQRT1_2 * (r + columnradius);

    if(mod(n, 2.0) == 1.0) {
      p.y += columnradius;
    }
    p.y = mod(p.y + columnradius, columnradius * 2.0) - columnradius;

    float result = -length(p) + columnradius;
    result = max(result, p.x);
    result = min(result, a);
    return -min(result, b);
  }

  return -m;
}
vec4 opSubColumns(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(opSubColumns(a.x, b.x, rn), a.yzw);
}

// INTER COLUMNS
float opInterColumns(const in float a, const in float b, const in vec2 rn) {
  return opSubColumns(a, -b, rn);
}
vec4 opInterColumns(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(opSubColumns(a.x, -b.x, rn), colorInter(a, b));
}

/////////////////////
// COMBINATION STAIRS
/////////////////////
// UNION STAIRS
float opUnionStairs(const in float a, const in float b, const in vec2 rn) {
  float s = rn.x / rn.y;
  float u = b - rn.x;
  return min(min(a, b), 0.5 * (u + a + abs((mod(u - a + s, 2.0 * s)) - s)));
}
vec4 opUnionStairs(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(opUnionStairs(a.x, b.x, rn), colorUnion(a, b));
}

// INTER STAIRS
float opInterStairs(const in float a, const in float b, const in vec2 rn) {
  return -opUnionStairs(-a, -b, rn);
}
vec4 opInterStairs(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(-opUnionStairs(-a.x, -b.x, rn), colorInter(a, b));
}

// SUB STAIRS
float opSubStairs(const in float a, const in float b, const in vec2 rn) {
  return -opUnionStairs(-a, b, rn);
}
vec4 opSubStairs(const in vec4 a, const in vec4 b, const in vec2 rn) {
  return vec4(-opUnionStairs(-a.x, b.x, rn), a.yzw);
}

/////////////
// REPETITION
/////////////

vec3 pMod(const in vec3 p, const in vec3 size) {
  vec3 pmod = p;
  if(size.x > 0.0) pmod.x = mod(p.x + size.x * 0.5, size.x) - size.x * 0.5;
  if(size.y > 0.0) pmod.y = mod(p.y + size.y * 0.5, size.y) - size.y * 0.5;
  if(size.z > 0.0) pmod.z = mod(p.z + size.z * 0.5, size.z) - size.z * 0.5;
  return pmod;
}

