	float supersin = sin(2.3) + cos(0.7);

  // camera 
  vec3 ro = SHADERTOY_ZOOM * vec3( -0.2 + 4.5 * cos(0.1 * time + supersin * mo.x), 1.0 + 2.0 * mo.y, 0.3 + 3.5 * sin(0.1 * time + 6.0 * mo.x));
  vec3 ta = vec3(-0.3, -0.8, 0.8);

