mat2 rot(float t){
	return mat2(cos(t),sin(t),-sin(t),cos(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv-=.5;
	uv.x*=iResolution.x/iResolution.y;

	border=2./iResolution.y;

	const vec3 bg_col=vec3(0);

	vec3 color=bg_col;

	const float pi=3.14159265358979;

	const vec3 logo_color_dark=vec3(.185);
	const vec3 logo_color_light=vec3(1);

	float dis_sq=1e38;

	const vec2 grid_size=vec2(.2,.2);

	const float loop_time=60.;

	float t0 = iTime;
	float t1 = mod(t0/loop_time*2.*pi,2.*pi);
	float t2 = .4*cos(t1);

	float zoom1=4.*exp(-1.3+.3*sin(t1));
	float z_val=(1.-(1.+.2*cos(t1))*uv.y+.2*sin(t1)*uv.x);
	zoom1/=z_val;

	const float zoom2=4.;

	uv*=rot(t2);

	uv*=zoom1;
	border*=zoom1;

	float veil=.875+.125*sin(mod(32.*uv.y+8.*uv.x,2.*pi));
	veil*=.875+.125*cos(mod(2.*uv.x+1.*uv.y,2.*pi));

	vec2 cell_index=floor(uv/grid_size+.5);
	vec2 cur_midpoint=cell_index*grid_size;
	uv-=cur_midpoint;

	if( mod(cell_index.x+cell_index.y,2.) == 0.){
		uv*=rot(pi/2.);
	}

	const float light_speed=.1;
	const int num_lights=7;

	vec3 logo_color=logo_color_dark;
	for(int i=0;i<num_lights;i++){
		if(floor(.5+(hash21(1.4+floor(mod(t0*light_speed+float(i)/float(num_lights),loop_time*light_speed))+hash11(float(i)))*vec2(20.,11.)-vec2(11.,2.))) == cell_index){
			//fade in and out
			//float fade_param=smoothstep(0.,1.,1.-2.*abs(fract(t0*light_speed)-.5));
			//only fade in
			float fade_param=smoothstep(0.,.25,fract(t0*light_speed+float(i)/float(num_lights)));
			logo_color=mix(logo_color_dark,logo_color_light,fade_param);
		}
	}

	uv*=zoom2;
	border*=zoom2;

	float dis=logo_dis(uv);

	color=mix(logo_color,color,smoothstep(-border/2.,border/2., dis));
	color*=veil;

	fragColor=vec4(color,1);
}

// strange foam of coloured bubbles. wonderfully wasteful calculation.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float timeScale = iTime * 0.1;

    fragColor = vec4(0.2,0.4,0.8,0.0);
    // draw N bubbles. use fragColor.a channel to plot only the highest bubble.
    for (float i = 0.0 ; i < 50.0; i ++) {
        // bubble's coordinate
        vec2 ax = vec2(0.9+0.7*cos(timeScale+i*23.0), 0.7+0.6*sin(timeScale+i*17.0));       

        // current pixel "height" vs. the given bubble
	    vec2 uv = fragCoord/iResolution.y - ax;
        float sizeScale = 5.0 + 5.0*(1.0+cos(5.0*(timeScale + i)));
        float height = 1.0 - sizeScale*(uv.x*uv.x + uv.y*uv.y);

        if (height > fragColor.a) {
            // highest bubble so far, colorize & apply
			fragColor = height*vec4(                
                0.6+0.4*cos(i+timeScale), 
                0.6+0.4*sin(i+timeScale), 
                0.8+0.4*(sin(3.0*i)+cos(2.0*i)),
                1.0
            );
        }
    }
    fragColor.a = 1.0;
}

float rand () {
    return fract(sin(iTime)*1e7);
}

vec2 hash( vec2 p )
{
	// Dave Hoskin's hash as in https://www.shadertoy.com/view/4djSRW
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    vec2 o = fract(vec2((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y));
	return o;
}

float hash12(vec2 p){
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 voro(in vec2 x)
{
	// Tomasz Dobrowolski's fast voronoi with 3x3 scan as in https://www.shadertoy.com/view/llG3zy
	vec2 n = floor(x);
	vec2 f = fract(x);
	vec2 mr;
	float md = 8.0;
	for( int j=-1; j <= 1; j++)
	for( int i=-1; i <= 1; i++)
	{
		vec2 g = vec2(float(i),float(j));
		vec2 o = hash( n + g );
        o = 0.5 + 0.5*sin( iTime + 2.2831*o );
        vec2 r = g + o - f;
        float d = dot(r,r);
        if( d<md )
        {
            md = d;
            mr = r;
        }
	}
	md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash( n + g );
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }
    return vec3( md, mr );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy / iResolution.xy;
	vec2 p = (fragCoord.xy-iResolution.xy)/iResolution.y;
	vec2 st = vec2(atan(p.x,p.y),length(p));
	p = vec2(st.x/0.2831+.5+(2.1*iTime),st.y);
	vec3 c = voro(6.*p*atan(iTime*0.01));
	vec3 col = c.x*3.*(sin(c.x)+.9)*vec3(p.y,c.x,sin(p.x));
    vec4 fc = vec4(col,1.0);
    float scanline = sin( uv.y * 1200.0 * rand())/30.0;
    float v = iTime*0.152;
    vec2 n = (fragCoord.xy * v + iTime * 1500. + 50.0);
	fc *= 1. - scanline - 0.1*hash12(n);
    float vegDist = length(( 0.5 , 0.5 ) - uv);
    fc *= 0.9 - vegDist * 0.6;
    if (iTime > 60.) // Time limit for Fast Effect compo
    {
        fragColor = vec4(0.,0.,0.,0.);
    }
    else fragColor = fc;
}

struct Obj {
	int type;
    float d;
};

struct Ray {
	Obj hitObj;
    vec3 hitPos;
    // travelled distance along the ray
    float t;
};

float sdSphere(vec3 p, float r) {
	return length(p)-r;
}

float sdPlane(vec3 p) {
	return p.y + 1.95;
}

Obj SDF(vec3 pos) {

    float d0 = sdPlane(pos);
    float d1 = sdSphere(pos-vec3(1, .2, 1), 2.2);

    Obj obj = Obj(1, d1);

    if (d0 < d1) {
      obj = Obj(0, d0);
    }

    return obj;
  }

// Raymarching loop
Ray castRay(vec3 rayOrigin, vec3 rayDir) {

    float tmax = 180.0;
    float t = 0.0;

    Obj hitObj = Obj(-1, t);
    vec3 hitPos = vec3(0);

    for (int i = 0; i < 500; i++) {

      vec3 p = rayOrigin + t*rayDir;
      Obj res = SDF(p);

      if (res.d < 0.01) {
        hitObj = res;
        hitPos = p;
        break;
      } else if (res.d > tmax) {
        break;
      }
      t += res.d;
    }

	return Ray(hitObj, hitPos, t);
}

vec3 calcNormal(vec3 pos) {

	// Center sample
    float c = SDF(pos).d;

    // Use offset samples to compute gradient / normal
    vec2 eps_zero = vec2(0.01, 0.0);
    return normalize(vec3(SDF(pos + eps_zero.xyy).d, SDF(pos + eps_zero.yxy).d,
                          SDF(pos + eps_zero.yyx).d) - c);
}

// Calculates Lambertian reflection
vec3 calculateLightning(vec3 pos, vec3 lightDir, vec3 surfaceColor) {

	// L is vector from surface point to light, N is surface normal. N and L must be normalized!
    vec3 L = normalize(lightDir);

    vec3 N = calcNormal(pos);

    float NoL = max(dot(N, L), 0.0);
    vec3 directional = vec3(1.80,1.27,0.99) * NoL;

    vec3 diffuse = surfaceColor * directional;

    return diffuse;
}

// Analytically box-filtered checkerboard by iq
float checkersTextureGradBox(in vec2 p, in vec2 ddx, in vec2 ddy) {

    // filter kernel
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;

    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)/2.0)-0.5)-abs(fract((p+0.5*w)/2.0)-0.5))/w;

    // xor pattern
	return 0.5 - 0.5*i.x*i.y;
}

vec2 texCoords(in vec3 pos) {

    const vec4 sc0 = vec4(1.0, 1.3, 1.0, 0.5);
    vec3 q = normalize(pos - sc0.xyz);
    vec2 matuv = vec2( atan(q.x,q.z), acos(q.y ) )*sc0.w;

    return 12.0*matuv;
}

vec3 floorColor(in vec3 p) {

    float f = mod(floor(p.z / 3.0) + floor(p.x / 3.0), 2.0);
    return f * vec3(.8)+0.03;
}

vec3 fog(vec3 color, float d, vec3 camDir, vec3 sunDir, float extintion, float inscattering) {

    float sunAmount = max(dot(camDir, sunDir), 0.0);
    vec3 fogColor = mix(vec3(0.001, 0.001, 0.001), vec3(0.6, 0.6, 0.6), pow(sunAmount, 8.0));
    return (color * exp(-d * extintion)) + (fogColor * (1.0 - exp(-d * inscattering)));
}

// Value noise from the Art of Code
float generateNoise(vec2 p) {
	return fract(sin(p.x*100.+p.y*6574.)*5647. );
}

float smoothNoise(vec2 uv) {

    vec2 lv = fract(uv*1.0);
    vec2 id = floor(uv*1.0);

    lv = lv*lv*(3.-2.*lv);

    float bl = generateNoise(id);
    float br = generateNoise(id + vec2(1,0));
    float b = mix(bl, br, lv.x);

    float tl = generateNoise(id + vec2(0,1));
    float tr = generateNoise(id + vec2(1,1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float turbulence(vec2 uv) {

    float c = smoothNoise(uv*4.0);
    c += smoothNoise(uv*8.0) * 0.5;
    c += smoothNoise(uv*16.0) * 0.25;
    c += smoothNoise(uv*32.0) * 0.125;
    c += smoothNoise(uv*65.0) * 0.0625;

    return c / 5.0;
}

vec3 render(in vec3 camPos, in vec3 rayDir, out bool rayHitObj, in vec2 uv, in mat3 camMat) {

    vec3 col;

    vec3 lightDir = vec3(0.8, 0.4, -0.5);
    vec3 sunDir = camMat * lightDir;

    rayHitObj = false;

    Ray ray = castRay(camPos, rayDir);

    // no objects were hit by the ray -> skydome
    if (ray.hitObj.type == -1) {

        //Sun
        vec3 sun1 = vec3(1, 0.0, 0.0);
        vec3 sun2 = vec3(0.268, 0.243, 0.007);

        float sun_weight = smoothstep(0.0, .9, sin(uv.y)*1.1);
        vec3 sunCol = mix(sun1, sun2, sun_weight);

        // the sun is rendered only if the light is coming from the visible sky
        if (sunDir.z > 0.0) {

          	if ((uv.x - sunDir.x*2.2) * (uv.x - sunDir.x*2.2) +
                (uv.y - sunDir.y+0.2) * (uv.y - sunDir.y+0.2) <= 0.1) {

            	sunCol *= turbulence(uv)*1.9;
            	return sunCol;
        	}
       	}

		vec3 sky_col = vec3(rayDir.y*0.5+0.01);
		col = vec3(rayDir.y*0.5+0.01);
       	// add clouds
       	col *= turbulence(uv);
       	return col;
    }

    // ray hit the floor plane
    else if (ray.hitObj.type == 0) {
    	col = floorColor(ray.hitPos);

    	//vec3 lightDir = vec3(0.8, 0.4, -.5);
    	col = calculateLightning(ray.hitPos, lightDir, col)*0.3;

    	// Add shadows
    	float shadow = 0.0;
    	vec3 L = normalize(lightDir);
    	vec3 N = calcNormal(ray.hitPos);
    	vec3 shadowRayOrigin = ray.hitPos + N * 0.01;
    	vec3 shadowRayDir = L;

    	Ray shadowRayIntersection = castRay(shadowRayOrigin, shadowRayDir);
    	if (shadowRayIntersection.hitObj.type == 1) {
            col *= 0.01;
        } else {

      	// Add fog for floor attenuation
      	float fogExtintion = 0.035;
     	float fogInscattering = .1;
        lightDir *= 0.8;
      	col = fog(col, ray.t, rayDir, lightDir, fogExtintion, fogInscattering);
        }
    }

    // sphere
    else if (ray.hitObj.type == 1) {

      vec2 uvw = texCoords(ray.hitPos) - uv;

      vec2 ddx_uv = dFdx( uvw );
      vec2 ddy_uv = dFdy( uvw );
      col = vec3(checkersTextureGradBox(uvw, ddx_uv, ddy_uv));

      // apply anti alias only to the sphere 
      rayHitObj = true;

      // add lightning
      vec3 surfaceCol = vec3(0.6, 0.0, 0.0);
      vec3 lightDir = vec3(0.8, 0.4, -1.0);
      col += calculateLightning(ray.hitPos, lightDir, surfaceCol);

      // add small amount of light to the backside of the sphere
      col += surfaceCol*0.4;
    }

     return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll) {

    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;
    vec3 camPos = vec3(30.0*sin(iTime*0.5), 2.0, 30.0*cos(iTime*0.5));
    vec3 camTar = vec3(-0.2, 2.0, -0.2);

    mat3 camMat = lookAt(camPos, camTar, 0.0);

    vec3 col = vec3(0.0);

    // MSAA anti aliasing
    bool msaa = true;

    bool rayHitObj = false;
    if (msaa) {
    	const float AA_size = 2.0;
    	float count = 0.0;
      	for (float aaY = 0.0; aaY < AA_size; aaY++) {
        	for (float aaX = 0.0; aaX < AA_size; aaX++) {

				vec2 offset = ((fragCoord.xy + vec2(aaX, aaY) / AA_size) - iResolution.xy*0.5)/iResolution.y;
          		vec3 rayDir = normalize(camMat * vec3(offset.xy, 2.0));

          		col += render(camPos, rayDir, rayHitObj, offset, camMat);


          		count += 1.0;
          		if (!rayHitObj) {
            		break;
          		}
        	}
      	}
      col /= count;

    } else {
		bool rayHitObj = false;
    	vec3 rayDir = normalize(camMat * vec3(uv.xy, 2.0));
    	col = render(camPos, rayDir, rayHitObj, uv, camMat);
    }

    // gamma
	col = pow(col,vec3(.454545));
	fragColor = vec4(col, 1.0); 
}

#define R iResolution
#define pi 3.14159
#define tao 6.28318
#define phi 1.6180339
#define rot20(x) cos((x)*tao/20. - vec2(0,pi/2.))

float sdLine(vec2 uv, vec2 a, vec2 abn){
    vec2 x = uv-a;
    vec2 r = abn.yx*vec2(-1,1);
    return dot(r, x - abn * dot(x, abn));
}

float penrose(vec2 uv){
    int type = 0;

    float angle = atan(uv.y,uv.x);
    angle = mod(angle + tao/10.,tao/5.) - tao/10.;
    angle = abs(angle);
    uv = length(uv) * cos(angle - vec2(0,pi/2.));
    float r = 0.;
    if(sdLine(uv, rot20(2.), rot20(6.)) > 0. ){

        for(int i = 0; i < 16; i++){
            float d0 = sdLine(uv, vec2(1./phi,0), rot20(8.) );
            float d1 = sdLine(uv, vec2(1./phi,0), rot20(3.) );
            float d2 = sdLine(uv, rot20(2.), rot20(9.));
            float d3 = sdLine(uv, rot20(2.), rot20(4.));

            if(d0 > 0.){
                type = 1;
                uv.y = d0 * phi;
                uv.x = d1 * phi;
            } else {
                type = 0;
                uv.x = d2 * phi;
                uv.y = abs(d3) * phi;
            }

    		r += float(type) / pow(1.2,float(i)) / 6.;
        }

    }
    return r;
}

void mainImage( out vec4 o, in vec2 i )
{
    float s = 2./R.y;
    vec2 uv0 = (i*2.-R.xy)/R.y / pow(1.6180339, mod(iTime,4.))/2.2;
    vec2 uv1 = (i*2.-R.xy)/R.y / pow(1.6180339, 4.+ mod(iTime,4.))/2.2;
    o = vec4(.5+.5*sin(vec4(8,5,12,0)*(iTime/1e2 + mix(penrose(uv1),penrose(uv0),  mod(iTime,4.)/4. ))));
}

#define PI 3.14159
#define TWO_PI 6.28318

// Perlin Noise from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(vec2 p, float freq ){
	float unit = 1./freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

float pNoise(vec2 p, int res){
	float persistance = .5;
	float n = 0.;
	float normK = 0.;
	float f = 4.;
	float amp = 1.;
	for (int i = 0; i<=res; i++){
		n+=amp*noise(p, f);
		f*=2.;
		normK+=amp;
		amp*=persistance;
	}
	float nf = n/normK;
	return nf*nf*nf*nf;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	float n = pNoise(uv, 2);
    float v = (sin((iTime + uv.x * 20. + uv.y * 20. + n * smoothstep(.5, -.5, length(uv)) * 200.) * TWO_PI) + 1.) / 2.;
    vec3 col = vec3(smoothstep(-1., 1., (v-.65)/fwidth(v) ));
    col.b = .65;
    fragColor = vec4(col,1.0);
}

vec2 path[18];
vec2 a[10], b[10];  // 10 = int(18 / 2) + 1
void init() {
	// manual point set
	path[0] = vec2(1.0137, 0.3967);
	path[1] = vec2(0.5626, 0.5417);
	path[2] = vec2(0.3414, -0.0639);
	path[3] = vec2(0.1158, 0.6121);
	path[4] = vec2(-0.7459, 0.7070);
	path[5] = vec2(-0.8443, 0.1465);
	path[6] = vec2(-0.3618, 0.1444);
	path[7] = vec2(-0.1585, 0.4285);
	path[8] = vec2(-0.3173, 0.3743);
	path[9] = vec2(-0.4706, -0.2456);
	path[10] = vec2(-0.7936, -0.3968);
	path[11] = vec2(-0.5655, -0.1589);
	path[12] = vec2(0.2119, -0.6991);
	path[13] = vec2(0.2968, -0.9548);
	path[14] = vec2(0.3969, -0.4136);
	path[15] = vec2(0.7119, 0.0779);
	path[16] = vec2(0.6283, 0.2814);
	path[17] = vec2(0.7057, -0.0209);

	// calculate Fourier coefficients, b[0] is always zero
	float t, dt;
	for (int k = 0; k < 10; k++) {
		a[k] = vec2(0.), b[k] = vec2(0.);
		t = 0.0, dt = 6.283185 * float(k) / 18.;
		for (int i = 0; i < 18; i++, t += dt)
			a[k] += path[i] * cos(t), b[k] += path[i] * sin(t);
		a[k] = a[k] * (2.0 / 18.), b[k] = b[k] * (2.0 / 18.);
	}
	a[0] = a[0] * 0.5;
}

vec2 eval(float t) {
	vec2 r = a[0];
    float c0 = cos(t), s0 = sin(t), c = c0, s = s0, c1;
    for (int k = 1; k < 10; k++) {
        r += a[k] * c + b[k] * s;
        c1 = c, c = c * c0 - s * s0, s = c1 * s0 + s * c0;
    }
	return r;
}


// an improvement of iq's https://www.shadertoy.com/view/Xlf3zl
float sdSqSegment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 pa = p - a, ba = b - a;
	vec2 q = pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return dot(q, q);
}
float sd(vec2 p) {
	float o = sin(0.5*iTime); o = .04 + .005*o*o;  // path offset
	float o2 = (o + .02)*(o + .02);
	float t_max = 6.3*min(1.5*fract(0.15*iTime), 1.0);
	vec2 a = eval(0.0), b, c;
	float dt = 0.05, t = dt;
	float d = 1e8, dd;
	while (t < t_max) {
		b = eval(t);
		dd = sdSqSegment(p, a, b);
		if (dd < o2) {  // more accurate and doesn't reduce much speed
			c = eval(t - 0.5*dt);
			dd = min(sdSqSegment(p, a, c), sdSqSegment(p, c, b));
		}
		d = min(d, dd);
		dt = clamp(0.026*length(a - p) / length(a - b), 0.02, 0.1);
		t += dt;
		a = b;
	}
	d = min(d, sdSqSegment(p, a, eval(t_max))); 	// add this line to eliminate gaps
	d = min(sqrt(d), abs(length(p) - 0.15));
	return d - o;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	init();
	vec2 p = 5.0 * (fragCoord - 0.5*iResolution.xy) / length(iResolution.xy);
	float d = sd(p - vec2(-.1, .08));

    // modified from iq's sdf visualizing function
	vec3 col = d > 0. ? vec3(1.0, 0.3, 0.5) : vec3(0.3, 1.5, 2.7);
	col *= 1.0 - 0.9*exp(-6.*abs(d));
	col *= 0.8 + 0.2*cos(120.*d - 3.0*iTime);
	col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0, 0.02, abs(d)));
	fragColor = vec4(col, 1.0);
}

//Basic Fractal Zero by @paulofalcao

const int maxIterations=6;

//generic rotation formula
mat2 rot(float a){
    float c=cos(a);float s=sin(a);
    return mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	//normalize stuff
    vec2 R=iResolution.xy,uv=(fragCoord-0.5*R)/R.x;

	//global zoom
	uv*=sin(iTime)*0.5+1.5;

	//shift, mirror, rotate and scale 6 times...
	for(int i=0;i<maxIterations;i++){
        uv*=2.1;          //<-Scale
        uv*=rot(iTime);   //<-Rotate
		uv=abs(uv);       //<-Mirror
        uv-=0.5;          //<-Shift
	}

	//draw a circle
	fragColor=vec4(length(uv)<0.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float EPS = 0.0001;
    const int MAX_STEPS = 200;
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3(0,cos(iTime)*2.,iTime/3.14*4.);
    vec3 rd = normalize(vec3(uv, 1.5));
    vec3 col;
    vec3 p;
    float d, t = 0.;
    int i;
    for(i=0; i<MAX_STEPS; i++) {
        p = ro+rd*t;
        p.xz = mod(p.xz + 2., 4.) - 2.;
        float dSphere = length(p) - 1.25;
        if(dSphere < EPS) {
            t = EPS*2.; // so this branch isn't triggered on the next iteration
            ro = p;
            rd = reflect(rd, normalize(p));
        }
        t += dSphere;
    }
    fragColor = vec4((rd.y > 0. ? vec3(.5,.7,.9) : vec3(.9,.7,.5)) * (1.5 - abs(rd.y)), 1.);
}

