const float	layerThickness	= 0.02;
const int	nbLayers 		= 7;


float sdTorus( vec3 p, vec2 t )
{
    return length( vec2(length(p.xz)-t.x,p.y) )-t.y;
}


float saturate(float c)
{
    return clamp(c,0.,1.);
}


vec2 minVecSelect(vec2 a, vec2 b)
{
    return a.x<b.x?a:b;
}

float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

vec2 map(in vec3 p)
{
    float slide = iTime / 1.;
    float fr = fract(slide);
    int   fl = int(floor(slide));    
    vec2  vd = vec2(100., -1.);
    float cnoise = noise(p * 2. + iTime / 8.) / 3.;
    float o = 100.0f;
    for( int i = 0; i < nbLayers; i++)
    {
        float m = mod(float(i + fl), float(nbLayers));
        float r = 0.6 - m * layerThickness + ( 1. - fr) * layerThickness;
        float d = sdTorus( p, vec2(1, r)) ;
        d = d - layerThickness / 2.;
        float o =  - 4. * fract( (iTime + float(i)) / float(nbLayers));
        float n = 1.5 + p.x  + o + cnoise;
		d = max(d, n);
 		vd = minVecSelect(vec2(d, float(i)), vd);        
    }   
    return vd;
}


vec3 calcNormal(vec3 p)
{
    const float h = 0.001;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy * map(p + k.xyy*h).x + 
                      k.yyx * map(p + k.yyx*h).x + 
                      k.yxy * map(p + k.yxy*h).x + 
                      k.xxx * map(p + k.xxx*h).x );
}

// From IQ
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/12.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return saturate(1.0 - 4. * occ);    
}


vec3 Render(vec3 ro,vec3 rd,vec3 cd,float dist)
{
    float t = 0.5;
    float d;
    float m = 0.;
    for( int i=0; i<1024; i++ )
    {
        vec3	p = ro + t*rd;
        vec2	h = map(p);
        t += h.x*0.7;
        d = dot(t*rd,cd);
        m = h.y;
        if( abs(h.x)<0.0001 || d>dist ) break;
    }

    vec3 col = vec3(0.3);

    if( d<dist )
    {
        vec3 light = vec3(0.,4.,2.);
        vec3 p = ro + t*rd;
        vec3 n = calcNormal(p);
        vec3 v = normalize(ro-p);
        vec3 l = normalize(light-p);
        vec3 h = normalize(l+v);

        vec3 diffcol = normalize(vec3(1. + sin(m * 0.7 + 1.3) / 2., 1. + sin(m * 1.3 + 4.45) / 2., 1. + sin(m * 1.9 + 2.3) / 2.)); 
        vec3 speccol = vec3(1.,1.,1.);
        vec3 ambcol = diffcol;
		float ao = calcAO(p, n);

        col = saturate(dot(n,l)) * diffcol;
        col+= pow(saturate(dot(n,h)),40.) * speccol * 0.5;
        col+= 0.2 * ambcol;
        col*= ao;
    }
    return col;
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
    fragColor = vec4(Render(ro/3. + vec3(0.0,.0,4.0),rd ,rd,14.) ,1);
}

mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 vignette(vec3 color, vec2 q, float v)
{
    color *= 0.3 + 0.8 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), v);
    return color;
}

vec3 desaturate(in vec3 c, in float a)
{
    float l = dot(c, vec3(1. / 3.));
    return mix(c, vec3(l), a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 tot = vec3(0.0);

#ifdef AA
	vec2 rook[4];
    rook[0] = vec2( 1./8., 3./8.);
    rook[1] = vec2( 3./8.,-1./8.);
    rook[2] = vec2(-1./8.,-3./8.);
    rook[3] = vec2(-3./8., 1./8.);
    for( int n=0; n<4; ++n )
    {
        // pixel coordinates
        vec2 o = rook[n];
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else //AA
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif //AA

        // camera

        float theta	= radians(360.)*(iMouse.x/iResolution.x-0.5) + iTime*.1;
        float phi	= radians(45.) + radians(90.)*(iMouse.y/iResolution.y-0.5)-1.;
        vec3 ro = 2.7*vec3( sin(phi)*cos(theta),cos(phi),sin(phi)*sin(theta));
        //vec3 ro = vec3(0.0,.2,4.0);
        vec3 ta = vec3(0, -0.2, 0);
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta );
        //vec3 cd = ca[2];    

        vec3 rd =  ca*normalize(vec3(p,1.5));        

        vec3 col = Render(ro ,rd ,ca[2],20.);

	    tot += col;
#ifdef AA
    }
    tot /= 4.;
#endif

    tot = desaturate(tot, -0.4);
    tot = vignette(tot, fragCoord / iResolution.xy, 0.8);
    tot = pow(tot, vec3(1./2.2));

	fragColor = vec4( tot, 1.0 );
}

float hash1( vec2 p )
{
    p  = 50.0*fract( p*0.3183099 );
    return fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 w = fract(x);
    vec2 u = w*w*w*(w*(w*6.0-15.0)+10.0);

    float a = hash1(p+vec2(0,0));
    float b = hash1(p+vec2(1,0));
    float c = hash1(p+vec2(0,1));
    float d = hash1(p+vec2(1,1));

    return -1.0+2.0*( a + (b-a)*u.x + (c-a)*u.y + (a - b - c + d)*u.x*u.y );
}

const mat2 m2 = mat2(  0.80,  0.60,
                      -0.60,  0.80 );

float fbm_9( in vec2 x )
{
    float f = 1.9;
    float s = 0.55;
    float a = 0.0;
    float b = 0.5;
    for( int i=0; i<9; i++ )
    {
        float n = noise(x);
        a += b*n;
        b *= s;
        x = f*m2*x;
    }
	return a;
}

float sqr (float x) { return x*x; }

vec4 middle_seeds (vec2 uv) {
    float x = length(sin(length(uv*400.0)))
        + sqr(sqr(length(sin(uv*175.0))/1.5));
    return vec4(0.25-0.1*x, 0.2-0.05*x, 0.15-0.03*x, 1.0);
}

vec4 seeds (vec2 uv) {
    float x = length(sin(length(uv*230.0)))
        + sqr(sqr(length(sin(uv*137.0))/1.5));
    return vec4(0.2-0.1*x, 0.2-0.05*x, 0.15-0.05*x, 1.0);
}

vec4 petals (vec2 uv, float offset) {
    float x = sin(offset + atan(uv.x, uv.y)*20.0);
    return vec4(0.9+0.1*x, 0.825+0.175*x, 0, 1.0);
}

vec4 stem (float uvx) {
    float x = cos(uvx*45.0);
    return vec4(sqr(x)*0.3, 0.4+x*0.6, sqr(x)*0.3, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float vscale = 1.0/iResolution.y;
    vec2 uvs = fragCoord*vscale;
    vec2 ctr = vec2(0.5*iResolution.x/iResolution.y, 0.56);
    vec2 uv = uvs - ctr;

    if (length(uv) < 0.08) {
	    fragColor = middle_seeds(uv);
    } else if (length(uv) < 0.2+length(sin(atan(uv.x, uv.y)*40.0))*0.01*fbm_9(uv*3.0)) {
	    fragColor = seeds(uv);
    } else if (length(uv) < length(sin(atan(uv.x, uv.y)*5.0))*0.38) {
	    fragColor = petals(uv, 0.0);
    } else if (length(uv) < length(sin(1.0+atan(uv.x, uv.y)*5.0))*0.4) {
	    fragColor = petals(uv, 1.0);
    } else if (length(uv) < length(sin(2.0+atan(uv.x, uv.y)*5.0))*0.42) {
	    fragColor = petals(uv, 2.0);
    } else if (length(uv.x) < 0.03 && uv.y < 0.0) {
	    fragColor = stem(uv.x);
    } else {
        float x = fbm_9(-iTime/10.0 + uv);
	    fragColor = vec4(0.6+0.4*x, 0.7+0.3*x, 1.0 ,1.0);
    }
}

//
// Analytic Linear Motion Blur Series:
//
// Self-Intersecting Polygon (XOR Rule) - https://www.shadertoy.com/view/tltXRS
// Concave Polygon - https://www.shadertoy.com/view/tldSzS
// Square - https://www.shadertoy.com/view/wtcSzB
// Checkerboard - https://www.shadertoy.com/view/tlcXRX
//

const int numPolygonCorners = 5;

vec2 screenToPolygonTransform(vec2 p, float t)
{
    float a = t * 4.;
    mat2 m = mat2(cos(a), sin(a), -sin(a), cos(a));
    return m * (p - vec2(cos(t * 14.)*.5, 0.));
}

vec2 polygonCorner(int i)
{
    const float thd = acos(-1.) * 4. / float(numPolygonCorners);
    float r = .8;
    return vec2(cos(float(i) * thd), sin(float(i) * thd)) * r;
}

float integratePolygon(vec2 sa, vec2 sb)
{
    vec2 sd = sb - sa;
    float sum = 0.;
    int e = 0;

    float ts[numPolygonCorners];

    int endPointInOut = 0;
    int startPointInOut = 0;

    vec2 pa = polygonCorner(0);
    for(int i = 1; i <= numPolygonCorners; ++i)
    {
        vec2 pb = polygonCorner(i);
        vec2 d = pb - pa;
        vec2 n = vec2(d.y, -d.x);

        float dotsdn = dot(sd, n);
        float t = dot(pa - sa, n) / dotsdn;
        float u = dot(sa + sd * t - pa, d);

        ts[i - 1] = -1.;

        if(u > 0. && u <= dot(d, d))
        {
            if(t > 0. && t <= 1.)
                ts[i - 1] = t;
            if(t > 1.)
                endPointInOut ^= 1;
            if(t < 0.)
                startPointInOut ^= 1;
        }

        pa = pb;
    }

    // The signs of the intersection distances to be added together
    // unfortunately are order-dependent. So all intersections are needed first
    // and then their relative order defines the sign.
    for(int i = 0; i < numPolygonCorners; ++i)
    {
        int e = 0;
        float ti = ts[i];

        for(int j = 0; j < numPolygonCorners; ++j)
        {
            float tj = ts[j];

            if(tj > 0. && tj < ti)
            	e ^= 1;
        }

        if(ts[i] > 0.)
            sum += e != 0 ? ts[i] : -ts[i];
    }

    if(startPointInOut != 0)
        sum = -sum;

    if(endPointInOut != 0)
        sum += 1.;

    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y * 2.;

    float t = iTime;

    vec3 col = vec3(1. / 9.);

	vec2 uv0 = screenToPolygonTransform(uv, t);
	vec2 uv1 = screenToPolygonTransform(uv, t - 1. / 30.);

	float sum = integratePolygon(uv0, uv1);

    col = mix(col, vec3(.9), sum);

    fragColor = vec4(pow(col, vec3(1. / 2.2)),1.0);
}

// Noise functions and most of the implementation based on
// https://www.shadertoy.com/view/4dS3Wd by Morgan McGuire @morgan3d!

// see also
// http://www.iquilezles.org/www/articles/warp/warp.htm
// https://thebookofshaders.com/13/
// for informations on fbm, noise, ...

// please check out stuff like: https://www.shadertoy.com/view/lsGGDd
// for more advanced planet lighting/clouds/...

// Looking for a blue planet? Colors:
// vec3 col_top = vec3(0.0, 0.5, 0.0);
// vec3 col_bot = vec3(0.0, 1.0, 1.0);
// vec3 col_mid1 = vec3(0.0, 1.0, 0.0);
// vec3 col_mid2 = vec3(0.0, 0.0, 1.0);
// vec3 col_mid3 = vec3(0.0, 0.0, 1.0);


// number of octaves of fbm
#define NUM_NOISE_OCTAVES 10
// size of the planet
#define PLANET_SIZE		0.75
// uncomment to use a simple sharpen filter
// #define SHARPEN
// simple and fast smoothing of outside border
#define SMOOTH


//////////////////////////////////////////////////////////////////////////////////////
// Noise functions:
//////////////////////////////////////////////////////////////////////////////////////

// Precision-adjusted variations of https://www.shadertoy.com/view/4djSRW
float hash(float p) { p = fract(p * 0.011); p *= p + 7.5; p *= p + p; return fract(p); }

float noise(vec3 x) {
    const vec3 step = vec3(110, 241, 171);
    vec3 i = floor(x);
    vec3 f = fract(x);
    float n = dot(i, step);
    vec3 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(mix( hash(n + dot(step, vec3(0, 0, 0))), hash(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 0))), hash(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash(n + dot(step, vec3(0, 0, 1))), hash(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 1))), hash(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

float fbm(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < NUM_NOISE_OCTAVES; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

//////////////////////////////////////////////////////////////////////////////////////
// Visualization:
//////////////////////////////////////////////////////////////////////////////////////

const float pi          = 3.1415926535;
const float inf         = 9999999.9;
float square(float x) { return x * x; }
float infIfNegative(float x) { return (x >= 0.0) ? x : inf; }

// C = sphere center, r = sphere radius, P = ray origin, w = ray direction
float intersectSphere(vec3 C, float r, vec3 P, vec3 w) {	
	vec3 v = P - C;
	float b = -dot(w, v);
	float c = dot(v, v) - square(r);
	float d = (square(b) - c);
	if (d < 0.0) { return inf; }	
	float dsqrt = sqrt(d);

	// Choose the first positive intersection
	return min(infIfNegative((b - dsqrt)), infIfNegative((b + dsqrt)));
}

// returns max of a single vec3
float max3 (vec3 v) {
  return max (max (v.x, v.y), v.z);
}

vec3 getColorForCoord(vec2 fragCoord) {
    // (intermediate) results of fbm
    vec3 q = vec3(0.0);
    vec3 r = vec3(0.0);
	float v = 0.0;
    vec3 color = vec3(0.0);

    // planet rotation
    float theta = iTime * 0.15;  
    mat3 rot = mat3(
        cos(theta), 0, sin(theta),	// column 1
        0, 1, 0,	                // column 2
        -sin(theta), 0, cos(theta)	// column 3
    );

    // Ray-sphere
    const float verticalFieldOfView = 25.0 * pi / 180.0;

    // position of viewpoint (P) and ray of vision (w)
    vec3 P = vec3(0.0, 0.0, 5.0);
    vec3 w = normalize(vec3(fragCoord.xy - iResolution.xy * 0.5, (iResolution.y) / (-2.0 * tan(verticalFieldOfView / 2.0))));

    // calculate intersect with sphere (along the "line" of w from P)
    float t = intersectSphere(vec3(0, 0, 0), PLANET_SIZE, P, w);

    // calculate color for sphere/background
    if (t < inf) {
        // calculate point of intersection on the sphere
        vec3 X = P + w*t;

        // apply rotation matrix
        X = rot*X;

        // calculate fbm noise (3 steps)
        q = vec3(fbm(X + 0.025*iTime), fbm(X), fbm(X));
        r = vec3(fbm(X + 1.0*q + 0.01*iTime), fbm(X + q), fbm(X + q));
        v = fbm(X + 5.0*r + iTime*0.005);
    } else {
        // ray missed the sphere
		return vec3(0.0);
    }

    // convert noise value into color
    // three colors: top - mid - bottom (mid being constructed by three colors)
    vec3 col_top = vec3(1.0, 1.0, 1.0);
    vec3 col_bot = vec3(0.0, 0.0, 0.0);
    vec3 col_mid1 = vec3(0.1, 0.2, 0.0);
    vec3 col_mid2 = vec3(0.7, 0.4, 0.3);
    vec3 col_mid3 = vec3(1.0, 0.4, 0.2);

    // mix mid color based on intermediate results
    vec3 col_mid = mix(col_mid1, col_mid2, clamp(r, 0.0, 1.0));
    col_mid = mix(col_mid, col_mid3, clamp(q, 0.0, 1.0));
    col_mid = col_mid;

    // calculate pos (scaling betwen top and bot color) from v
    float pos = v * 2.0 - 1.0;
    color = mix(col_mid, col_top, clamp(pos, 0.0, 1.0));
    color = mix(color, col_bot, clamp(-pos, 0.0, 1.0));

    // clamp color to scale the highest r/g/b to 1.0
    color = color / max3(color);

    // create output color, increase light > 0.5 (and add a bit to dark areas)
    color = (clamp((0.4 * pow(v,3.) + pow(v,2.) + 0.5*v), 0.0, 1.0) * 0.9 + 0.1) * color;

    // apply diffuse lighting  
    float diffuse = max(0.0, dot(P + w*t, vec3(1.0, sqrt(0.5), 1.0)));
    float ambient = 0.1;
    color *= clamp((diffuse + ambient), 0.0, 1.0);

#ifdef SMOOTH
    // apply a smoothing to the outside
    color *= (P + w*t).z * 2.0;
#endif    

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
#ifdef SHARPEN 
    // use a simple sharpen filter (you could improve that immensely!
    fragColor.rgb =
        getColorForCoord(fragCoord) * 3. -
        getColorForCoord(fragCoord + vec2(1.0, 0.0)) * 0.5 -
        getColorForCoord(fragCoord + vec2(0.0, 1.0)) * 0.5 -
        getColorForCoord(fragCoord - vec2(1.0, 0.0)) * 0.5 -
        getColorForCoord(fragCoord - vec2(0.0, 1.0)) * 0.5;
#else
    // just use a single pass
    fragColor.rgb = getColorForCoord(fragCoord);
#endif
}

 const float EPSILON=1e-3;
struct Camera {
    vec3 Obs;
    vec3 View;
    vec3 Up;
    vec3 Horiz;
    float H;
    float W;
    float z;
};

struct Ray {
    vec3 Origin;
    vec3 Dir;
};

struct Material{
  vec4 color;
  bool emet;
  bool reflet;

};

struct Sphere {
   vec3 Center;
   float R;
};

struct Object{
    Sphere sphere;
    Material material;
};


Camera camera(in vec3 Obs, in vec3 LookAt, in float aperture) {
   Camera C;
   C.Obs = Obs;
   C.View = normalize(LookAt - Obs);
   C.Horiz = normalize(cross(vec3(0.0, 0.0, 1.0), C.View));
   C.Up = cross(C.View, C.Horiz);
   C.W = float(iResolution.x);
   C.H = float(iResolution.y);
   C.z = (C.H/2.0) / tan((aperture * 3.1415 / 180.0) / 2.0);
   return C;
}

Ray launch(in Camera C, in vec2 XY) {
   return Ray(
      C.Obs,
      C.z*C.View+(XY.x-C.W/2.0)*C.Horiz+(XY.y-C.H/2.0)*C.Up 
   );
}

bool intersect_sphere(in Ray R, in Sphere S,out float t) {
   vec3 CO = R.Origin - S.Center;
   float a = dot(R.Dir, R.Dir);
   float b = 2.0*dot(R.Dir, CO);
   float c = dot(CO, CO) - S.R*S.R;
   float delta = b*b - 4.0*a*c;
   if(delta < 0.0) {
      return false;
   }
   t = (-b-sqrt(delta)) / (2.0*a);
   return true;
}

Object objs[6];

void init_scene(){
    float cx = cos(iTime*2.0)*2.0;
    float cy = sin(iTime*2.0)*2.0;
    float dx = cx+cos(iTime*3.0)*0.5;
    float dy = cy+sin(iTime*3.0)*0.5;
    float kx = cx+sin(iTime*2.0)*0.4;
    float ky = cy+cos(iTime*2.0)*0.4;
    float kz = cos(iTime*2.0)*0.5;
    float px = cos(iTime*7.0)*0.5;
    float py = sin(iTime*7.0)*0.5;

    objs[0] = Object(Sphere(vec3(0.0, 0.0, 0.0), 0.4),
                     Material(vec4(0.8,0.8,0.8,1.0),false,true));
    objs[1]=Object(Sphere(vec3(cx, cy, 0.0), 0.2),
                   Material(vec4(1.0,0.0,0.0,1.0),false,false));
    objs[2]=Object(Sphere(vec3(dx, dy, 0.0), 0.1),
                  Material(vec4(0.0,1.0,0.0,1.0),false,false));
    objs[3]=Object(Sphere(vec3(kx,ky,kz),0.1),
                   Material(vec4(1.0,1.0,0.0,1.0),false,false));
    objs[4]=Object(Sphere(vec3(-5.0,5.0,5.0),0.5),
                   Material(vec4(1.0,1.0,1.0,1.0),true,false));
    objs[5]=Object(Sphere(vec3(px,py,.5),0.02),
                   Material(vec4(1.0,0.0,0.5,1.0),true,false));
}

bool shadow(in Ray R) {
   for(int i=0; i<objs.length(); ++i) {
        float t;
        if(
          !objs[i].material.emet &&
          intersect_sphere(R, objs[i].sphere, t) &&
          t > EPSILON && t < 1.0
        ) {
          return true;
        }
    }
    return false;
}

vec4 lighting(in vec3 P, in vec3 N, in Material material){
    if (material.emet == true){
        return material.color;
    }
    vec4 result = vec4(0.0,0.0,0.0,0.0);

    for (int i=0; i<objs.length(); i++){
        if(objs[i].material.emet == true){
            Ray R2 = Ray(P, objs[i].sphere.Center);
            if(!shadow(R2)){
                vec3 E = objs[i].sphere.Center-P;
                float lamb = max(0.0, dot(E,N) /length(E));
                result += lamb * material.color * objs[i].material.color;
            }
         }
    }
    return vec4(result.x,result.y,result.z,1.0);

}
vec4 raytrace(in Material m, in Ray R, out vec3 P, out vec3 N)
{
  float t = 1e30;
  vec4 color = lighting(P,N,m);
    for (int i=0; i<objs.length(); i++){
        float ti;
        if( intersect_sphere(R, objs[i].sphere, ti) && ti < t && ti > 0.0 && ti > 1e-6){
            t = ti;
            P = R.Origin + 1e-3 * R.Dir;
         	N = normalize(P - objs[i].sphere.Center);
            vec3 E = objs[i].sphere.Center-P;
            float lamb = max(0.0, dot(E,N) /length(E));
         	color =lighting(P,N,objs[i].material)*0.9;   
        }
    }
  return color;

}

void mainImage(
   out vec4 col,in vec2 coord
) {     

	float centrex = iResolution.x/2.0;
	float centrey = iResolution.y/2.0;
	   Camera C = camera(
       vec3(2.0, 2.0, 1.5),
       vec3(0.5, 0.5, 0.5),
       50.0       
   );
   Ray R = launch(C, coord);


   col = vec4(0.0, 0.9, 0.8, 1.0);
   init_scene();

    float t =1e30;
    vec3 P;
    vec3 N;
    Material material;
    for (int i=0; i<objs.length();++i){
        float ti;
        if( intersect_sphere(R,objs[i].sphere,ti) && ti<t){
         t =ti;
         P = R.Origin + t*R.Dir;
         N = normalize(P - objs[i].sphere.Center);
         material = objs[i].material;

        }


    }
    if (t != 1e30){
        if(material.reflet==true){
             Ray R2 = Ray(P,-2.0*dot(N,R.Dir)*N + R.Dir);
            col = raytrace(material,R2,P,N);
        }else{
        col = lighting(P,N,material);
        }

    }

}

#define PI 3.1415926

vec3 xs[6];

mat2 rotm(float angle) {
	return mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
}

vec3 color(float loc) {
    vec3 result = vec3(0.0);
    for (int i = 0; i < 6; ++i) {
        float fi = float(i);
        float inSection = step(fi, loc) - step(fi + 1.0, loc);
        vec3 inverted = 1.0 - xs[i];

        vec3 stepColor = mix(xs[i], sqrt(inverted), mod(iTime * 1.0 + fi / 6.0, 1.0) * inSection);
        result += stepColor * (step(fi - 0.1, loc) - step(fi + 0.1, loc));
    }

    return result;
}

vec2 rot(vec2 uv, float angle) {
    return rotm(angle) * (uv - 0.5) + 0.5;
}

vec3 pattern(vec2 uv) {
    vec2 sv = vec2(uv.x, 1.0 - uv.y) * 6.0;

    float mi = min(sv.x, sv.y);
    float moat = 0.0;
    for (int i = 0; i < 6; ++i) {
        float fi = float(i);
        moat += smoothstep(fi - 0.04, fi + 0.04, mi) - smoothstep(fi + 0.06, fi + 0.14, mi);
    }
    moat = clamp(moat, 0.0, 1.0);

    vec2 iv = floor(sv);
    vec2 fv = fract(sv);
	return color(min(iv.x, iv.y)) * (1.0 - moat);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    xs[0] = vec3(0.92, 0.0, 0.0);
	xs[1] = vec3(1.0, 0.5, 0.0);
	xs[2] = vec3(1.0, 0.9, 0.0);
	xs[3] = vec3(0.0, 0.5, 0.1);
	xs[4] = vec3(0.25, 0.4, 0.95);
	xs[5] = vec3(0.5, 0.0, 0.5);

    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mv = iMouse.xy / iResolution.xy;
    vec2 cv = mv - 0.5;

    float compensation = -atan(-0.5, -0.5 * iResolution.x / iResolution.y) - PI / 4.0;

    float angle = -atan(cv.y, cv.x * iResolution.x / iResolution.y) - compensation;
    float d = length(cv);

    vec2 sv = rot(uv, angle);
    float scale = 2.0 + 8.0 * d;
    sv *= scale;

    vec2 iv = floor(sv);
    vec2 fv = fract(sv);

	fragColor = vec4(pattern(fv), 1.0);
}

