float sdBox( vec3 p, vec3 b )
{
 vec3 d = abs(p) - b;
 return length(max(d,0.0)); 
}


float dBar(vec2 p, float width) {
 vec2 d = abs(p) - width;
 return min(max(d.x, d.y), 0.0) + length(max(d, 0.)) + 0.01 * width;
}

float dCrossBar(vec3 p, float x) {
 float bar_x = dBar(p.yz, x);
 float bar_y = dBar(p.zx, x);
 float bar_z = dBar(p.xy, x);
 return min(bar_z, min(bar_x, bar_y));
}



float dMengerSponge(vec3 p) 
{
 float d = sdBox(p, vec3(0.79));
 float itt = 1.3;
 float one_third = 1. / itt;
 for (float i = 0.0; i < itt; i++) {
  float k = pow(one_third, i);
  float kh = k * 1.;
  d = max(d, -dCrossBar(mod(p + kh, k * 2.) - kh, k * one_third));
 }
 return d;
}



float GetDist(vec3 p){
 float gap = 1.;
 p.xyz = mod(p.xyz + gap,2.0 * gap) - gap;
 float dm1;
 float d;  
 d=1.0;           
 dm1=dMengerSponge(p-vec3(-0.,-0.,0.0));
 d=min(d, dm1);
 return d;
}

vec3 GetNormal(vec3 p) {
 float d = GetDist(p);
 vec2 e = vec2(.001, 0);  
 vec3 n = d - vec3(
 GetDist(p-e.xyy),
 GetDist(p-e.yxy),
 GetDist(p-e.yyx));   
 return normalize(n);
}

vec3 cameraPath(float t) {
 t *= PI *.5 ;
 float t2 =  cos(t)+0.;
 float c = cos(t*2.);
 float x = 0.;
 float y = 0.;
 float z= 0.;   
 if (t2<0.){
 x = 1. /1. + 2. +c;
 };       
 if (t2>0.){
 y = 1. /1. + 2. +c;
 };
 vec3 xyz =vec3(x,y,z);
 return xyz;
}

vec3 RayDirection(float fieldOfView, vec2 fragCoord, vec2 size){
 vec2 xy = fragCoord - size / 2.0;
 float z = size.y / tan(radians(fieldOfView) / 2.);
 return normalize(vec3(xy, -z));
}

float RayMarch(vec3 ro, vec3 rd) {
 float dO=0.1;
 for(int i=0; i<64; i++) {
 vec3 p = ro + rd*dO;
 float dS = GetDist(p);
 dO += dS*1.;
 if(dO>100. || dS<0.001) break;
 } 
 return dO;
}

