#define time iTime
#define ZPOS -50.

float PI = acos(-1.);

float wave(float tempo) {
    return .5 * cos(time * tempo) + .5;
}

vec3 palette(float x) {

    vec3 a = vec3(1., .7, .7);
    vec3 b = vec3(.8, 1., 1.);
    vec3 c = vec3(1., .8, 1.);
    vec3 d = vec3(1., 1., 1.);

    return a + b * sin(2. * PI * (c + x * d));
}

mat2 rot2d(float a) {
	float c = cos(a), s = sin(a);

    return mat2(c, s, -s, c);
}

vec3 kifs(vec3 p, float it, float s, float tf, float ss) {
    float t = tf * time;
    t = floor(ss * t) + smoothstep(0., .8, fract(ss * t));
    for (float i = 0.; i < it; i++) {
        p.xy *= rot2d(tf * t);
        p.yz *= rot2d(tf * t + i);
        p = abs(p);
        p -= s;
        s *= .8;
    }

    return p;
}

float sph(vec3 p, float r) {
    return length(p) - r;
}

float map(vec3 p) {
    float d = 1000.;
    vec3 pk1 = kifs(p, 2., 2., .5, 1.); 
    vec3 pk2 = kifs(pk1, 4., 1.5, .3, 2.);
    vec3 pk3 = kifs(pk2, 8., 1.3, .2, .5);

    float sph1 = sph(pk1, 1.);
    float sph2 = sph(pk2, 1.);
    float sph3 = sph(pk3, 1.);
  	d = min(d,
            mix(
                mix(
                	sph1, 
	                sph2, 
    	            wave(1.)
       		    ),
                sph3,
                wave(.3)
        )
    );   
//    d = min(d, sph1);

    return d;
}

float rm(vec3 ro, vec3 rd) {
    float d = 0.;

    for (int i = 0; i < 100; i++) {
        vec3 p = ro + d * rd;
        float ds = map(p);

        if (ds < .001 || ds > 100.) {
            break;
        }
        d += ds;
    }

    return d;
}

vec3 normal(vec3 p){
    vec2 e = vec2(0.01, 0);

    vec3 n = map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
    );

    return normalize(n);
}

float light(vec3 p) {
	vec3 lp = vec3(2, 2, ZPOS);
    vec3 n = normal(p);
    vec3 tl = normalize(lp - p);
    float dif = dot(tl, n);
    float dl = rm(p + .01 * n, tl);

    if (dl < length(lp - p)) {
        dif *= .1;
    }

    return dif;
}

