// A moving ball in 3D space. Motion blurred by averaging the multi-sampling of past 24 frames.

#define BACKGROUND vec3(0, 0.17, 0.19)
#define SAMPLE_COUNT 24.
#define DIRECTIONAL_LIGHT_TYPE 51

#define INF 10000000000.0f
#define PI 3.141592653589793

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Camera {
    vec3 position;
};

struct LightSource {
    vec3 position;
    float intensity;
    vec3 color;
    int type;
};

struct Intersection {
    vec4 P;
    vec3 N;
    vec3 V;
};

float SphereIntersect(Ray r, vec3 center, float radius) {
    // geometric method
    vec3 L = center - r.origin;
    float d = length(L);
    float d2 = d * d;
    float cos_theta = dot(normalize(L), r.direction);
    float cos_theta2 = cos_theta * cos_theta;
    if (cos_theta <= 0.) {
        return INF;
    }
    float sin_theta2 = 1. - cos_theta2;
    float r2 = radius * radius;
    float intersect_half2 = r2 - d2 * sin_theta2;
    if (intersect_half2 < 0.) {
        return INF;
    }
    float center_project_dist = d * cos_theta;
    float intersect_half = sqrt(intersect_half2);
    float t0 = center_project_dist - intersect_half;
    if (t0 < 0.) {
        return center_project_dist + intersect_half;
    } else {
        return t0;
    }
}

// ----------------  shading technics  ----------------
vec3 Lambertian(vec3 N, LightSource light) {
    return clamp(light.color * light.intensity * dot(N, -light.position), 0., 1.) + vec3(0.1);
}

// ----------------  shading technics ends  ----------------
vec3 Render(Ray r, float time, LightSource light) {
    vec3 col = vec3(0);

    float angle = PI * cos(time) * 4.;
    vec3 center = vec3(cos(angle), 0, 3. + sin(angle));
    const float radius = 0.5;

    float t_min = SphereIntersect(r, center, radius);

    // only directional light
    if (t_min < INF) {
        vec4 P = vec4(r.origin + r.direction * t_min, 1.);
        vec3 N = normalize(P.xyz - center);
        col += Lambertian(N, light);
    } else {
        col = BACKGROUND;
    }
    return col;
}

Camera camera;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // Setup scene
    LightSource light = LightSource(normalize(vec3(-1, -1, 1)), 1., vec3(1, 1, 1), DIRECTIONAL_LIGHT_TYPE);
    camera = Camera(vec3(0, 0, 0));

    Ray r = Ray(camera.position, normalize(vec3(uv, 1)));
	vec3 col = vec3(0);
    float dt = iTimeDelta / 10.;
    for (float time = iTime - dt * SAMPLE_COUNT; time <= iTime; time += dt) {
        col += Render(r, time, light);
    }
    col /= SAMPLE_COUNT;

    fragColor = vec4(col, 1.);
}

#define NUM_LAYERS 10.
mat2 Rotate(float angle) {
	float s = sin(angle*3.14159265/180.), c = cos(angle*3.14159265/180.);
    return mat2(c,-s,s,c);
}

// Returns a psuedo random number.
float Hash21(vec2 p) {
    vec2 result = fract(p*vec2(123.456, 427.123));
    result += dot(p, p+.12)*0.023;
    return fract(result.x*result.y);
}

float Star(vec2 uv, float flare, float r) {
    float d = length(uv);
    float result = .01/d; //smoothstep(0.2, .05, d);

    if (flare>0.) {
    for (float x=0.;x<2.;x++) {
        uv *= Rotate(x*(237.*r));
        float rays2 = max(0.0, 1.-abs(uv.x*uv.y*1500.));    
        result += rays2*.1*flare;
    }
    }

    result *= smoothstep(1.0, 0.2, d);

    return result;
}

vec3 StarLayer(vec2 uv) {
    // Time varying pixel color
    vec3 result = vec3(0);

    vec2 grid_uv = fract(uv)-0.5;
    vec2 id = floor(uv); // Identify each square.

    for (int y=-1;y<=1;y++) {
        for (int x=-1;x<=1;x++) {
            vec2 offset = vec2(x,y);
            vec2 local_id = id+offset;
            float n = Hash21(local_id)-0.5;
            float size = 5.*(fract(n*1247.238));
            vec3 color = (sin( vec3(.7,.3,.9) * fract(n*222.112)*347.)*.5)+.5;
            color *= vec3(1.,0.5,1.+size/5.);
		    result += color*Star(grid_uv-offset-vec2(n,fract(n*34.)-.5), smoothstep(0.8, 1., size), 17.*(fract(n*873.43)-0.2))*size;            
        }
    }
	return result;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime*.5;
    // Zoom out
    uv *= 3.;

//    if (grid_uv.x>0.49 || grid_uv.y>0.49) {
//        col.r = 1.;
//    }
//    col.rg += id*0.4;
//    col.rg = grid_uv;
//    col += Hash21(id);
    vec3 col = vec3(0);
    uv *= Rotate(6.*t);

    for (float i=0.; i<=1. ; i+=1./NUM_LAYERS) {
//    float i = 1.;
        float depth = fract(i*t);
        float scale = mix(20., 0.15, depth);
        float fade = depth;
    	col += StarLayer(uv*(scale-(i*7.1232)))*fade;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

