// Space parametrization of a rounded box. Or could also be called
// "rounded box" coordinates.

// List of all shape parametrizations: https://www.shadertoy.com/playlist/XclfRs


float msign( in float x ) { return (x<0.0)?-1.0:1.0; }

// x = local dist
// y = local perimeter dist
// z = total local perimeter
// w = global distance (sdf)
vec4 paBox( in vec2 p, 
            in vec2 b, in float r, 
            in float s )
{
    vec2 q = abs(p)-b;

    float l = b.x+b.y + 1.570796*r;

    float k1 = min(max(q.x,q.y),0.0) + length(max(q,0.0))-r;
    float k2 = ((q.x>0.0)?atan(q.y,q.x):1.570796);
    float k3 = 3.0 + 2.0*msign(min(p.x,-p.y)) - msign(p.x);
    float k4 = msign(p.x*p.y);
    float k5 = r*k2+max(-q.x,0.0);

    float ra = s*round(k1/s);

    float l2 = l + 1.570796*ra;

    return vec4(k1-ra,
                k3*l2+k4*(b.y+((q.y>0.0)?k5+k2*ra:q.y)),
                4.0*l2,
                k1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates    
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.x;

    // animate segment
    float id = floor((iTime+0.1)/3.0);
    vec2  si = vec2(0.35,0.15) + 0.1*cos( vec2(1,2) + id*vec2(3,1) );
    float ra = 0.15 + 0.05*sin(id*0.2);

    // distance and parametrization    
    const float band = 0.1;
    vec4 b = paBox( p, si, ra, band );

    // base color
    float d = b.w;
    vec3 col = vec3(1.0,0.68,0.35) + vec3(-0.35,0.15,0.6)*step(d,0.0);
    col *= 1.0 - 0.6*exp(-64.0*abs(d));
    col *= 1.0-smoothstep(0.47,0.50,abs(fract(d/band)-0.5));
	col *= 0.9 + 0.2*smoothstep(0.26,0.24,abs(fract(0.5*d/band+0.25)-0.5));
    col += smoothstep(0.004, 0.002, abs(d));

    // circles
    if( d>-band*0.5 )
    {
	vec2 q = b.xy;
    q.y *= floor(b.z/band)*(band/b.z);  // optional - ensure periodicity, but break physicallity
    q.y -= iTime*0.1;                   // animate circles

    vec2 uv = fract(q/band+0.5)-0.5;    // draw circles
    float l = length(uv);
    col *= 0.1 + 0.9*smoothstep(0.01,0.04,abs(l-0.35));
    col *= 0.1 + 0.9*smoothstep(0.10,0.11,l);
    }

	fragColor = vec4(col, 1.0);
}

#define RES 10.
#define DELAY 50.
#define SPH_RAD 80.
#define ROT_RAD 100.
#define SPEED 10.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float grad = DELAY*floor(RES*(1.0 - uv.y))*1.0/RES;
    vec2 coord = (fragCoord - iResolution.xy*0.5) + ROT_RAD*vec2(cos(SPEED*iTime+grad), sin(SPEED*iTime+grad));
    float sphere = step(coord.x * coord.x + coord.y * coord.y, SPH_RAD * SPH_RAD);


    // Output to screen
    fragColor = vec4(sphere*uv,0,1.0);
}

// Author: Dr Ord (@ordiology)
// Title: shader(h')art
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0
//
// This Valentine's Day heart art project uses the circle from 
// the Book of Shaders' Pixel Spirit Deck as well as knowledge
// gained from the first chapters of the Book of Shaders and 
// the concept of building a heart manipulating a circle from
// @The_ArtOfCode's "Making a Heart in ShaderToy" YouTube video.
//
// Please feel free to use the concepts and code but reference
// the relevant sources appropriately.

#ifdef GL_ES
precision mediump float;
#endif

#define TAU 6.28318530718

uniform vec2 u_resolution; // Canvas size (width,height)
uniform float u_time; // Time in seconds since load

vec3 colorA = vec3(0.8000000, 0.0000000, 0.5058824);
vec3 colorB = vec3(0.3686275, 0.9019608, 0.7686275);

// 08 circleSDF (from @bookofshaders Pixel Spirit Deck)
float circleSDF(vec2 st){
    return length(st - 0.5)*2.0;
}

// Shader(h')art heart (uses concept from @The_ArtOfCode)
// Deps 08
float heart(vec2 st){
    st.y *= 1.;
    st.y -= sqrt(abs(st.x-0.5))*0.5-0.1;
    float r = circleSDF(st);
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 color = vec3(0.0);
    vec2 st = fragCoord.xy/
              iResolution.xy;

    float t = iTime;

    // Shader(h')art
    float size = .64;
    float blur = 0.005;
    for (int i = 1; i < 28; i++){
        color += smoothstep(size -= blur, size -= blur, length(heart(st)));
        color -= smoothstep(size -= blur, size -= blur, length(heart(st)));
    }

    // Mix the two colors in y-space
    vec3 colorC = mix(colorA, colorB, sin(st.y));
    vec3 colorD = mix(colorB, colorA, sin(st.y));

    // Oscillate the colours over the heart fragment
    color = mix(colorC, colorD, sin(t)*(color-0.5)+ 0.5);
    fragColor = vec4(color,1.0);
}

#define time iTime
#define ZPOS -50.

float PI = acos(-1.);

float wave(float tempo) {
    return .5 * cos(time * tempo) + .5;
}

vec3 palette(float x) {

    vec3 a = vec3(1., .7, .7);
    vec3 b = vec3(.8, 1., 1.);
    vec3 c = vec3(1., .8, 1.);
    vec3 d = vec3(1., 1., 1.);

    return a + b * sin(2. * PI * (c + x * d));
}

mat2 rot2d(float a) {
	float c = cos(a), s = sin(a);

    return mat2(c, s, -s, c);
}

vec3 kifs(vec3 p, float it, float s, float tf) {
    for (float i = 0.; i < it; i++) {
        p.xy *= rot2d(tf * time);
        p.yz *= rot2d(tf * time + i);
        p = abs(p);
        p -= s;
        s *= .8;
    }

    return p;
}

float sph(vec3 p, float r) {
    return length(p) - r;
}

float map(vec3 p) {
    float d = 1000.;
    vec3 pk1 = kifs(p, 2., 2., .5); 
    vec3 pk2 = kifs(pk1, 4., 1.5, .3);
    vec3 pk3 = kifs(pk2, 8., 1.3, .2);

    float sph1 = sph(pk1, 1.);
    float sph2 = sph(pk2, 1.);
    float sph3 = sph(pk3, 1.);
  	d = min(d,
            mix(
                mix(
                	sph1, 
	                sph2, 
    	            wave(1.)
       		    ),
                sph3,
                wave(.3)
        )
    );   

    return d;
}

float rm(vec3 ro, vec3 rd) {
    float d = 0.;

    for (int i = 0; i < 100; i++) {
        vec3 p = ro + d * rd;
        float ds = map(p);

        if (ds < .001 || ds > 100.) {
            break;
        }
        d += ds;
    }

    return d;
}

vec3 normal(vec3 p){
    vec2 e = vec2(0.01, 0);

    vec3 n = map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
    );

    return normalize(n);
}

float light(vec3 p) {
	vec3 lp = vec3(2, 2, ZPOS);
    vec3 n = normal(p);
    vec3 tl = normalize(lp - p);
    float dif = dot(tl, n);
    float dl = rm(p + .01 * n, tl);

    if (dl < length(lp - p)) {
        dif *= .1;
    }

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);
	vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, ZPOS);
    vec3 rd = normalize(vec3(uv, 1));
    float d = rm(ro, rd);
    vec3 p = ro + d * rd;
    float dif = light(p);

  	col = dif * palette(.5 * cos(length(p) / 20. + .5) + p.z / 5.);

    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926536;
const float tau = 6.2831853071;
const float sqrt3 = 1.7320508076;


float distToTriangle(in vec2 p) {
    p.x = abs(p.x) - 1.;
    p.y = p.y + 1. / sqrt3;

    if(p.x + sqrt3* p.y > 0.)
        p = vec2(p.x - sqrt3 * p.y, -sqrt3 * p.x - p.y) * .5;

    p.x -= clamp(p.x, -2., 0.);

    return -length(p) * sign(p.y);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    float angle = tau / 3.;
    angle = tau / (3. + sin(iTime * 0.1));

    uv *= 3.;
    for(int i = 0; i < 4; i++) {
        uv *= 2.;
        uv.x = abs(uv.x);

        vec2 n = vec2(cos(angle), sin(angle));
        uv -= 2. * n * min(0., dot(uv, n));

        uv.y -= 2. * tan(pi / 6.);
    }

    float d = distToTriangle(uv);
    d = smoothstep(1. / iResolution.y, 0., d);

    fragColor = vec4(d, d, d, 1.);
}

float genWave(float x) {
	return (
        (
            sin(
                (x/70.0)+
                (iTime)
            ) * 30.0
        ) * (sin(iTime)/2.0)
    ) + (
        sin(iTime)*30.0
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float halfPoint = iResolution.y/2.0;
    float waveStep = iResolution.y/8.0;
	vec2 uv = fragCoord.xy/iResolution.xy;

	float waveHeightX = genWave(fragCoord.x);
	float waveHeightY = genWave(fragCoord.x+(iResolution.x/8.0));
	float waveHeightZ = genWave(fragCoord.x);

	float waveHeightA = halfPoint+waveHeightX;
	float waveHeightB = (halfPoint-waveStep)+waveHeightY;
	float waveHeightC = (halfPoint-(waveStep*2.0))+waveHeightZ;

    if (fragCoord.y < waveHeightC) {
		fragColor = vec4(uv.y+0.2, uv.y+0.2, uv.y+0.5, 1.0);
    } else if (fragCoord.y < waveHeightB) {
		fragColor = vec4(uv.y, uv.y, uv.y+0.4, 1.0);
    } else if (fragCoord.y < waveHeightA) {
		fragColor = vec4(uv.y-0.2, uv.y-0.2, uv.y+0.2, 1.0);
    } else {
		fragColor = vec4(0.4, 0.4, 0.8, 1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(3,3,3);
	col.r = sin(iTime*uv.x);
    //col.g = cos(iTime*uv.y);
    col.g = tan(uv.y+uv.x*iTime*50.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define LOOP_MAX 216
#define EPS 1e-4
#define HORIZONTAL_AA 1
#define saturate(x) clamp(x, 0.0, 1.0)
#define sim(x, y) (abs(x - y) < EPS)

const float PI = acos(-1.0);
const float PI2 = PI * 2.0;

vec2 uv = vec2(0.0, 0.0);
const vec3 dLight = normalize(vec3(0.2, 0.1, -0.5));
const vec3 pLight = vec3(0.0, 0.0, 0.6);

// https://thebookofshaders.com/10/?lan=jp
float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// https://qiita.com/kaneta1992/items/21149c78159bd27e0860
mat2 rot(float r) {
    float c = cos(r), s = sin(r);
    return mat2(c, s, -s, c);
}

vec2 pmod(vec2 p, float r) {
    float a =  atan(p.x, p.y) + PI/r;
    float n = PI2 / r;
    a = floor(a/n)*n;
    return p*rot(-a);
}

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sphere( vec3 p, float s )
{
  return length(p)-s;
}

float cappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec3 repLim2(vec3 p, vec3 c, vec3 l)
{
    return p - c * clamp(round(p / c), -l, l);
}

float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float octahedron( vec3 p, float s)
{
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

// http://mercury.sexy/hg_sdf/
float fOpUnionRound(float a, float b, float r) {
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);
}

// The "Stairs" flavour produces n-1 steps of a staircase:
// much less stupid version by paniq
float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2.0 * s)) - s)));
}

float centerMap(vec3 p) {
    vec3 centerP = p + vec3(0.0, -0.04, -0.6);

    centerP.xz = centerP.xz * rot(iTime * PI * -0.4);
    centerP.xz = centerP.xz * rot(PI * 4.0 * centerP.y);
    centerP.xz = pmod(centerP.xz, 8.0) * 0.1;
    centerP.z -= 0.05 * (1.8 - abs(centerP.y) / 0.1) * 0.1;

    return box(centerP, vec3(0.001, 0.2, 0.001));
}

float coreMap(vec3 p) {
    vec3 coreP = p + vec3(0.0, -0.04, -0.6);

    coreP.xz = coreP.xz * rot(iTime * PI * 0.2);

    return octahedron(coreP, 0.05);
}

float map(vec3 p) {
    vec2 seedP = p.xz;
    seedP += vec2(EPS, 0.5);
    vec2 seed = vec2(floor(seedP.x), floor(seedP.y + EPS));

    vec3 sphereP = p;
    float sphereC = 0.6;
    sphereP.xz += vec2(sphereC * 0.5, -0.3);
    sphereP = repLim2(vec3(sphereP), vec3(sphereC, 0.0, 1.0), vec3(1.0, 1.0, 3.0));
    sphereP.y -= mod(iTime * 0.6 - random(seed) * 5.0, 3.0);
    sphereP.y += 0.5;

    vec3 pillarP = p;
    float pillarC = 0.8;
    pillarP.z -= 0.3;
    pillarP.x = abs(pillarP.x) - 0.7;
    pillarP.z = mod(pillarP.z + 0.5 * pillarC, pillarC) - 0.5 * pillarC - 0.2;

    vec3 dentP = pillarP;
    dentP.y -= 0.94;
    dentP.xz = pmod(dentP.xz, 20.0);
    dentP.z -= 0.1;

    float sphereDE = sphere(sphereP, 0.05);
    float pillarDE = cappedCylinder(pillarP, 0.1, 1.1);
    float dentDE = box(dentP, vec3(0.004, 1.1, 0.01));
    float planeDE = dot(p, vec3(0.0, 1.0, 0.0)) + 0.2;

    float milk = fOpUnionRound(sphereDE, planeDE, 0.07);
    float pillars = fOpUnionStairs(planeDE, max(-dentDE, pillarDE), 0.07, 5.0);
    float center = min(centerMap(p), coreMap(p));
    return min(min(milk, pillars), center);
}

vec3 norm(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS, 0.0, 0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3(0.0, -EPS, 0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3(0.0, 0.0, -EPS))
    	));
}

float fog(float depth) {
    float density = 0.6;
    return 1.0 - saturate(1.0 / exp(pow(density * depth, 2.0)));
}

float ao(vec3 p, vec3 n, float amp, float len) {
	float amt = 0.0;
    for(int i = 0; i < 4; i++) {
    	p = p + len * n;
        amt += amp * saturate(map(p) / len);
        amp *= 0.5;
        len += 0.02;
    }
    return amt;
}

// https://www.shadertoy.com/view/lsKcDD
float softShadow(vec3 p, vec3 light) {
    float minDist = 1e-6;
    float maxDist = 10.0;

    float sharpness = 35.0;

    float s = 1.0;
    float ph = 1e20;
    float dist = minDist;
    for(int i = 0; i < 32; i++) {
    	float st = centerMap(p + light * dist);

        float y = (i == 0) ? 0.0 : st * st / (2.0 * ph);
        float d = sqrt(st * st - y * y);
        s = min(s, sharpness * d / max(0.0, dist - y));

        ph = st;
        dist += st;

        if(s < 1e-6 || maxDist < dist) {
        	break;
        }
    }

    return 1.0 - max(s, 0.0);
}

vec3 objMat(vec3 rp) {
    vec3 no = norm(rp);
    vec3 dpl = normalize(pLight - rp);
    float ipl = distance(pLight, rp);

    vec3 albedo = vec3(0.4);
    float pDiff = saturate(dot(dpl, no)) * (1.0 / (ipl * 1.5));
    float shadow = softShadow(rp, dpl);
    float a = ao(rp, no, 0.5, 0.01) * 0.7 + 0.5;
    return (albedo + pDiff * 0.2) * a * (mix(0.6, 0.5, shadow) * (1.0 / (ipl * 3.0)));
}

vec3 centerMat(vec3 rp) {
	vec3 no = norm(rp);
    vec3 invDLight = dLight * -1.0;
    vec3 dpl = normalize(pLight - rp);
    float ipl = distance(pLight, rp);

    vec3 albedo = vec3(0.15);
    float dif = saturate(dot(invDLight, no));
    float pDiff = saturate(dot(dpl, no)) * (1.0 / ipl);
    float a = ao(rp, no, 1.8, 0.07) * 0.8 + 0.5;
    return (albedo + dif * pDiff * 2.0) * (1.0 - a);
}

vec3 coreMat(vec3 rp) {
	vec3 no = norm(rp);
    vec3 invDLight = dLight * -1.0;

    vec3 albedo = vec3(1.3);
    float dif = saturate(dot(invDLight, no));
    return albedo + dif;
}

vec3 skyMat() {
    return mix(vec3(0.13), vec3(0.0), uv.y * 0.5);
}

vec3 march(vec3 ro, vec3 rd, out vec3 rp, out float depth) {
    vec3 col = vec3(0.0);
    depth = 1.0;

    for(int i = 0; i < LOOP_MAX; i++) {
        rp = ro + rd * depth;
        float dist = map(rp);

        if(abs(dist) < EPS) {
            if(sim(dist, centerMap(rp))) {
                col = centerMat(rp);
            }
            else if(sim(dist, coreMap(rp))) {
                col = coreMat(rp);
            }
            else {
                col = objMat(rp);
            }
            break;
        }
        else {
        	col = skyMat();
        }

        depth += dist;
    }
    return col;
}

vec3 render(vec2 p) {
    float fov = 80.0 * 0.5 * PI / 180.0;
    vec3 cp = vec3(0.0, 0.01, -0.7);
    vec3 cd = normalize(vec3(0.0) - cp);
    vec3 cs = normalize(cross(cd, vec3(0.0, 1.0, 0.0)));
    vec3 cu = normalize(cross(cs, cd));
    float td = 1.0 / tan(fov / 2.0);

    vec3 ro = cp;
    vec3 rd = normalize(cs * p.x + cu * p.y + cd * td);
    vec3 rp = vec3(0.0);

    vec3 col = vec3(0.0);
    float depth = 1.0;
    col += march(ro, rd, rp, depth);

    return mix(col, skyMat(), fog(depth));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col = vec3(0.0);
    for(int x = 0; x < HORIZONTAL_AA; x++)
    {
        vec2 delta = vec2(float(x - HORIZONTAL_AA / 2), 0.0) * 1e-3;
        col += render(uv + delta);
    }

    fragColor = vec4(col / float(HORIZONTAL_AA), 1.0);
}

// Utilizing simplex noise function from https://www.shadertoy.com/view/4tSGDy
float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,128.852))) * 43758.5453)*2.0-1.0;
}

float simplex3D(vec3 p)
{

	float f3 = 1.0/3.0;
	float s = (p.x+p.y+p.z)*f3;
	int i = int(floor(p.x+s));
	int j = int(floor(p.y+s));
	int k = int(floor(p.z+s));

	float g3 = 1.0/6.0;
	float t = float((i+j+k))*g3;
	float x0 = float(i)-t;
	float y0 = float(j)-t;
	float z0 = float(k)-t;
	x0 = p.x-x0;
	y0 = p.y-y0;
	z0 = p.z-z0;

	int i1,j1,k1;
	int i2,j2,k2;

	if(x0>=y0)
	{
		if(y0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=1; k2=0; } // X Y Z order
		else if(x0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=0; k2=1; } // X Z Y order
		else { i1=0; j1=0; k1=1; i2=1; j2=0; k2=1; }  // Z X Z order
	}
	else 
	{ 
		if(y0<z0) { i1=0; j1=0; k1=1; i2=0; j2=1; k2=1; } // Z Y X order
		else if(x0<z0) { i1=0; j1=1; k1=0; i2=0; j2=1; k2=1; } // Y Z X order
		else { i1=0; j1=1; k1=0; i2=1; j2=1; k2=0; } // Y X Z order
	}

	float x1 = x0 - float(i1) + g3; 
	float y1 = y0 - float(j1) + g3;
	float z1 = z0 - float(k1) + g3;
	float x2 = x0 - float(i2) + 2.0*g3; 
	float y2 = y0 - float(j2) + 2.0*g3;
	float z2 = z0 - float(k2) + 2.0*g3;
	float x3 = x0 - 1.0 + 3.0*g3; 
	float y3 = y0 - 1.0 + 3.0*g3;
	float z3 = z0 - 1.0 + 3.0*g3;	

	vec3 ijk0 = vec3(i,j,k);
	vec3 ijk1 = vec3(i+i1,j+j1,k+k1);	
	vec3 ijk2 = vec3(i+i2,j+j2,k+k2);
	vec3 ijk3 = vec3(i+1,j+1,k+1);	

	vec3 gr0 = normalize(vec3(noise3D(ijk0),noise3D(ijk0*2.01),noise3D(ijk0*2.02)));
	vec3 gr1 = normalize(vec3(noise3D(ijk1),noise3D(ijk1*2.01),noise3D(ijk1*2.02)));
	vec3 gr2 = normalize(vec3(noise3D(ijk2),noise3D(ijk2*2.01),noise3D(ijk2*2.02)));
	vec3 gr3 = normalize(vec3(noise3D(ijk3),noise3D(ijk3*2.01),noise3D(ijk3*2.02)));

	float n0 = 0.0;
	float n1 = 0.0;
	float n2 = 0.0;
	float n3 = 0.0;

	float t0 = 0.5 - x0*x0 - y0*y0 - z0*z0;
	if(t0>=0.0)
	{
		t0*=t0;
		n0 = t0 * t0 * dot(gr0, vec3(x0, y0, z0));
	}
	float t1 = 0.5 - x1*x1 - y1*y1 - z1*z1;
	if(t1>=0.0)
	{
		t1*=t1;
		n1 = t1 * t1 * dot(gr1, vec3(x1, y1, z1));
	}
	float t2 = 0.5 - x2*x2 - y2*y2 - z2*z2;
	if(t2>=0.0)
	{
		t2 *= t2;
		n2 = t2 * t2 * dot(gr2, vec3(x2, y2, z2));
	}
	float t3 = 0.5 - x3*x3 - y3*y3 - z3*z3;
	if(t3>=0.0)
	{
		t3 *= t3;
		n3 = t3 * t3 * dot(gr3, vec3(x3, y3, z3));
	}
	return 96.0*(n0+n1+n2+n3);

}

// Some simple 2d scrolling "waves"
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float pos = fragCoord.x/120.0;
	float posOct = fragCoord.x/100.0;
	float timeStep = iTime/10.0;

	// Create lines by using the simplex noise
    float noiseA = simplex3D(vec3(pos+timeStep, pos, timeStep));
    float noiseB = simplex3D(vec3(-(posOct+timeStep), posOct, -timeStep));

	// Scale the noise to be more approriate
    float scaledNoiseA = (noiseA+1.0)/2.0;
    float scaledNoiseB = (noiseB+1.0)/2.0;

	// Get the noise with the octaves applied
	float octNoise = scaledNoiseA*scaledNoiseB;

    fragColor = vec4(octNoise, octNoise, octNoise+0.2, 1.0);
}

vec4 dmin(vec4 x, float y, vec3 n) {
	if(x.w < 0.0) return vec4(n,y);
    else if(y < 0.0) return x;
    return x.w < y ? x : vec4(n,y);
}
float square(vec3 p, vec3 v) {
    if(p.z < 0.0 || v.z > 0.0) return -1.0;
    float t = - p.z / v.z;
    vec3 c = p + v * t;
    if(abs(c.x) < 1.0 && abs(c.y) < 1.0) return t;
    else return -1.0;
}
vec4 cube(vec3 p, vec3 v, float flip) {
	vec4 dist = vec4(-1);
    vec3 u = vec3(0,0,1) * flip;
    dist = dmin(dist, square(p.xyz-u,v.xyz), vec3(0,0,1));
    dist = dmin(dist, square(p.zxy-u,v.zxy), vec3(0,1,0));
    dist = dmin(dist, square(p.yzx-u,v.yzx), vec3(1,0,0));
    dist = dmin(dist, square(-p.xyz-u,-v.xyz), vec3(0,0,-1));
    dist = dmin(dist, square(-p.zxy-u,-v.zxy), vec3(0,-1,0));
    dist = dmin(dist, square(-p.yzx-u,-v.yzx), vec3(-1,0,0));
    return dist;
}

vec3 scene(vec3 p, vec3 v) {
    vec3 empty = vec3(0.1);
    vec4 t;
    int count = 0;
    ivec3 l = ivec3(0);
    bool hole = false;
    if(abs(p.x) < 1. && abs(p.y) < 1. && abs(p.z) < 1.) {
        for(int i=0;i<18;i++) {
            vec3 c = p;
            ivec3 e = clamp(ivec3(c * 3. / 2. + 1.5), 0, 2);
            l = l * 3 + e;
            vec3 o = vec3(e - 1) * 2.0 / 3.0;
            p = (p - o) * 3.0;
            count++;
            ivec3 u = e;
            hole = false;
            if(u.x == 1 && u.y == 1) hole = true;
            if(u.y == 1 && u.z == 1) hole = true;
            if(u.z == 1 && u.x == 1) hole = true;
            if(hole) break;
        }
    }
    for(int i=0;i<18;i++) {
        if(!hole) {
            t = cube(p,v,1.);
            if(t.w < 0.) return empty;
            vec3 c = p + v * t.w;
            ivec3 e = clamp(ivec3(c * 3. / 2. + 1.5), ivec3(0), ivec3(2));
            l = l * 3 + e;
            vec3 o = vec3(e - 1) * 2.0 / 3.0;
            p = (p - o) * 3.0;
            count++;
        } else {
            t = cube(p,v,-1.);
            if(t.w < 0.) return empty;
            for(int j=0;j<10;j++) {
                ivec3 e = (l%3+3)%3;
                bool up = false;
                if(t.x < -0.5 && e.x == 2) up = true;
                if(t.y < -0.5 && e.y == 2) up = true;
                if(t.z < -0.5 && e.z == 2) up = true;
                if(t.x > 0.5 && e.x == 0) up = true;
                if(t.y > 0.5 && e.y == 0) up = true;
                if(t.z > 0.5 && e.z == 0) up = true;
                if(up) {
                    vec3 o = vec3(e - 1) * 2.0 / 3.0;
                    p = p / 3.0 + o;
                    l /= 3;
                    count--;
                    if(count == 0) return empty;
                } else break;
            }
            l -= ivec3(t.xyz);
            p += t.xyz * 2.;
        }
        ivec3 u = l%3;
        hole = false;
        if(u.x == 1 && u.y == 1) hole = true;
        if(u.y == 1 && u.z == 1) hole = true;
        if(u.z == 1 && u.x == 1) hole = true;
        if(count >= 7) break;
    }
    vec3 wp = (p+v*t.w) / pow(3.,7.); // what
    vec3 lv = normalize(vec3(-1,2,-0.5));
    return (wp*0.3+0.7) * (normalize(t.xyz)*0.3+0.7) * (dot(t.xyz,lv)*0.5+0.5);
}

vec3 image(vec2 uv) {
    vec3 dir = normalize(vec3(uv*0.7,-1));
    float vignetting = pow(mix(-dir.z,1.,0.5),10.0)*3.0;
    float gt = iTime/2.;

    // Uniform Catmull-Rom spline
    vec3 p0 = vec3(0,5,2);
    vec3 p1 = vec3(0,0,1);
    vec3 p2 = vec3(0,1./3.,-2./3.);
    vec3 p3 = vec3(0,8./9.,-5./9.);
    float t = fract(gt);
    float a = 0.0;
    float t0 = 0.;
    float t1 = pow(distance(p0,p1),a) + t0;
    float t2 = pow(distance(p1,p2),a) + t1;
    float t3 = pow(distance(p2,p3),a) + t2;
    float tt = mix(t1,t2,t);
    vec3 a1 = ((t1-tt)*p0 + (tt-t0)*p1) / (t1-t0);
    vec3 a2 = ((t2-tt)*p1 + (tt-t1)*p2) / (t2-t1);
    vec3 a3 = ((t3-tt)*p2 + (tt-t2)*p3) / (t3-t2);
    vec3 b1 = ((t2-tt)*a1 + (tt-t0)*a2) / (t2-t0);
    vec3 b2 = ((t3-tt)*a2 + (tt-t1)*a3) / (t3-t1);
    vec3 c  = ((t2-tt)*b1 + (tt-t1)*b2) / (t2-t1);
    vec3 eye = c;
    float r = t*3.1415926535/2. - 0.1;
    dir.yz *= mat2(cos(r),-sin(r),sin(r),cos(r));

    float m = floor(gt)*3.1415926535/2.;
    eye.yz *= mat2(cos(m),-sin(m),sin(m),cos(m));
    dir.yz *= mat2(cos(m),-sin(m),sin(m),cos(m));
    return scene(eye,dir) * vignetting;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y*2.;
    vec3 col = vec3(0);
    for(int i=0;i<2;i++) {
        for(int j=0;j<2;j++) {
            col += image(uv + vec2(i,j)/iResolution.y/2.) / 4.;
        }
    }
    fragColor = vec4(col,1.0);
}

