#define TAU radians(360.)

//#define AUDIOREACT

const float nbCircle = 13.; 

vec2 rotate(float angle, float radius)
{
    return vec2(cos(angle),-sin(angle)) * radius;
}

vec2 perp(vec2 v)
{
    return vec2(-v.y, v.x);
}

float udTriangle( vec2 p, vec2 a, vec2 b, vec2 c )
{
  vec2 ab = b - a; vec2 pa = a - p;
  vec2 bc = c - b; vec2 pb = b - p;
  vec2 ca = a - c; vec2 pc = c - p;

  float d0 = dot(perp(ab), pa);
  float d1 = dot(perp(bc), pb);
  float d2 = dot(perp(ca), pc);

  return min(min(d0, d1), d2);
}

float sdfTriangle(vec2 p)
{
    float radius = 1.2;
    float angle = iTime * 0.8;
    vec2 a = rotate( angle, radius);
    vec2 b = rotate( TAU / 3. + angle, radius);
    vec2 c = rotate( 2. * TAU / 3. + angle, radius);

    return udTriangle(p, a, b, c);
}

float radFilter(float v)
{
    return smoothstep(100. / iResolution.y, 0., abs(v-1.));
}

float distFilter(float v)
{
    return smoothstep(0., 0.5, v);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	p = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float	angle = atan(p.y, p.x);
    float 	l = length(p) * nbCircle;
    float	circleId = floor(l);
    #ifdef AUDIOREACT
    float	s = texture(iChannel0, vec2(0.5, 1. - circleId / nbCircle)).x;
    #else
    float	s = 0.6;    
    #endif
    float	modCircleId = mod(circleId, 2.);
    float d = sdfTriangle(p);
    d = distFilter(d);
    float speed = mix(0.1, -0.1, modCircleId);
    l+= sin((angle - iTime * speed) * circleId * 5.) * 0.5 * d * s;
    l = fract(l) * 2.;
    float	t = min(l, 2. - l);
    t = radFilter(t); 
    fragColor = vec4(vec3(pow(t,1./2.2)),1.);
}

