//Edited by Onan
// added timeScaling Variable for speeding up/slowing down

/*
	Technical details:

	The main volume noise is generated from a deformed periodic grid, which can produce
	a large range of noise-like patterns at very cheap evalutation cost. Allowing for multiple
	fetches of volume gradient computation for improved lighting.

	To further accelerate marching, since the volume is smooth, more than half the the density
	information isn't used to rendering or shading but only as an underlying volume	distance to 
	determine dynamic step size, by carefully selecting an equation	(polynomial for speed) to 
	step as a function of overall density (not necessarialy rendered) the visual results can be 
	the	same as a naive implementation with ~40% increase in rendering performance.

	Since the dynamic marching step size is even less uniform due to steps not being rendered at all
	the fog is evaluated as the difference of the fog integral at each rendered step.

*/

mat2 rot(in float a){float c = cos(a), s = sin(a);return mat2(c,s,-s,c);}
const mat3 m3 = mat3(0.33338, 0.56034, -0.71817, -0.87887, 0.32651, -0.15323, 0.15162, 0.69596, 0.61339)*1.93;
float mag2(vec2 p){return dot(p,p);}
float linstep(in float mn, in float mx, in float x){ return clamp((x - mn)/(mx - mn), 0., 1.); }
float prm1 = 0.;
vec2 bsMo = vec2(0);
float timeScaling = 0.2;

vec2 disp(float t){ return vec2(sin(t*0.22)*1., cos(t*0.175)*1.)*2.; }

vec2 map(vec3 p)
{
    vec3 p2 = p;
    p2.xy -= disp(p.z).xy;
    p.xy *= rot(sin(p.z+iTime*timeScaling)*(0.1 + prm1*0.05) + iTime*timeScaling*0.09);
    float cl = mag2(p2.xy);
    float d = 0.;
    p *= .61;
    float z = 1.;
    float trk = 1.;
    float dspAmp = 0.1 + prm1*0.2;
    for(int i = 0; i < 5; i++)
    {
		p += sin(p.zxy*0.75*trk + iTime*timeScaling*trk*.8)*dspAmp;
        d -= abs(dot(cos(p), sin(p.yzx))*z);
        z *= 0.57;
        trk *= 1.4;
        p = p*m3;
    }
    d = abs(d + prm1*3.)+ prm1*.3 - 2.5 + bsMo.y;
    return vec2(d + cl*.2 + 0.25, cl);
}

vec4 render( in vec3 ro, in vec3 rd, float time )
{
	vec4 rez = vec4(0);
    const float ldst = 8.;
	vec3 lpos = vec3(disp(time + ldst)*0.5, time + ldst);
	float t = 1.5;
	float fogT = 0.;
	for(int i=0; i<130; i++)
	{
		if(rez.a > 0.99)break;

		vec3 pos = ro + t*rd;
        vec2 mpv = map(pos);
		float den = clamp(mpv.x-0.3,0.,1.)*1.12;
		float dn = clamp((mpv.x + 2.),0.,3.);

		vec4 col = vec4(0);
        if (mpv.x > 0.6)
        {

            col = vec4(sin(vec3(5.,0.4,0.2) + mpv.y*0.1 +sin(pos.z*0.4)*0.5 + 1.8)*0.5 + 0.5,0.08);
            col *= den*den*den;
			col.rgb *= linstep(4.,-2.5, mpv.x)*2.3;
            float dif =  clamp((den - map(pos+.8).x)/9., 0.001, 1. );
            dif += clamp((den - map(pos+.35).x)/2.5, 0.001, 1. );
            col.xyz *= den*(vec3(0.005,.045,.075) + 1.5*vec3(0.033,0.07,0.03)*dif);
        }

		float fogC = exp(t*0.2 - 2.2);
		col.rgba += vec4(0.06,0.11,0.11, 0.1)*clamp(fogC-fogT, 0., 1.);
		fogT = fogC;
		rez = rez + col*(1. - rez.a);
		t += clamp(0.5 - dn*dn*.05, 0.09, 0.3);
	}
	return clamp(rez, 0.0, 1.0);
}

float getsat(vec3 c)
{
    float mi = min(min(c.x, c.y), c.z);
    float ma = max(max(c.x, c.y), c.z);
    return (ma - mi)/(ma+ 1e-7);
}

//from my "Will it blend" shader (https://www.shadertoy.com/view/lsdGzN)
vec3 iLerp(in vec3 a, in vec3 b, in float x)
{
    vec3 ic = mix(a, b, x) + vec3(1e-6,0.,0.);
    float sd = abs(getsat(ic) - mix(getsat(a), getsat(b), x));
    vec3 dir = normalize(vec3(2.*ic.x - ic.y - ic.z, 2.*ic.y - ic.x - ic.z, 2.*ic.z - ic.y - ic.x));
    float lgt = dot(vec3(1.0), ic);
    float ff = dot(dir, normalize(ic));
    ic += 1.5*dir*sd*ff*lgt;
    return clamp(ic,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = (gl_FragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    bsMo = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    float time = iTime*timeScaling*3.;
    vec3 ro = vec3(0,0,time);

    ro += vec3(sin(iTime*timeScaling)*0.5,sin(iTime*timeScaling*1.)*0.,0);

    float dspAmp = .85;
    ro.xy += disp(ro.z)*dspAmp;
    float tgtDst = 3.5;

    vec3 target = normalize(ro - vec3(disp(time + tgtDst)*dspAmp, time + tgtDst));
    ro.x -= bsMo.x*2.;
    vec3 rightdir = normalize(cross(target, vec3(0,1,0)));
    vec3 updir = normalize(cross(rightdir, target));
    rightdir = normalize(cross(updir, target));
	vec3 rd=normalize((p.x*rightdir + p.y*updir)*1. - target);
    rd.xy *= rot(-disp(time + 3.5).x*0.2 + bsMo.x);
    prm1 = smoothstep(-0.4, 0.4,sin(iTime*timeScaling*0.3));
	vec4 scn = render(ro, rd, time);

    vec3 col = scn.rgb;
    col = iLerp(col.bgr, col.rgb, clamp(1.-prm1,0.05,1.));

    col = pow(col, vec3(.55,0.65,0.6))*vec3(1.,.97,.9);

    col *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.12)*0.7+0.3; //Vign

	fragColor = vec4( col, 1.0 );
}

#define N 20
#define PI 3.14

float dLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;

    float t = dot(pa, ba)/dot(ba, ba);
    t = clamp(t, 0., 1.);
    return length(pa-ba*t);
}

float Line(vec2 p, vec2 a, vec2 b) {
    float d = dLine(p, a, b);
    return smoothstep(.006, 0., d);
}


vec3 getCol(vec2 uv)
{
    vec3 col = vec3(0);
    float time = iTime;
    vec2 p[N];
    float r = .5;
    p[0] = vec2(0, r);

    vec3 c1 = vec3(1, 0, 0);
    vec3 c2 = vec3(1);

    for(int i=0;i<N;i++) {
        float theta = float(i)*2.*PI/float(N) + sign(sin(time+sin(time+sin(time)))*sin(time*3.));
        float c = cos(theta);
        float s = sin(theta);
        p[i] = r*vec2(c, s);
    }

    vec3 b = vec3(1, 0, 0);
    vec3 red = vec3(0,0,1);
    for(int i=0;i<N;i++) {
        col += red*Line(uv, p[i], p[i]);
    }


    int pa = (N/2)%N;
    float d1 = 100000.;
    for(int i=0;i<N/2;i++){
        d1 = min(d1, dLine(uv, p[pa], p[i]));
        pa = (pa+1)%N;
        d1 = min(d1, dLine(uv, p[pa], p[i]));
        pa = (pa+1)%N;
    }

    pa = (0);
    float d2 = 100000.;
    float spr = mix(200., 1000., fract(time));

    float circle = length(uv) - r;
    col += c1*(1./(spr*d1));

    float mask = step(0., -circle)*(1.-smoothstep(0.01, 0., d1));

    for(int i=N/2;i<N;i++){
        d2 = min(d2, dLine(uv, p[pa], p[i]));
        pa = (pa+1)%N;
        d2 = min(d2, dLine(uv, p[pa], p[i]));
        pa = (pa+1)%N;
    }

    col += (mask)*c2*(1./(spr*d2));
   	return col;  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = 1. -2. * uv;
    uv.x *= iResolution.x/iResolution.y;
    uv /= 1.5;
    // Time varying pixel color
    vec3 col = getCol(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define time .4 * iTime
#define ZPOS -30. + 20. * time

float PI = acos(-1.);

mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

float sph(vec3 p, float r) {
  return length(p) - r;
}

float tube(vec3 p, vec3 a, vec3 b, float r) {
  vec3 ab = b - a;
  vec3 ap = p - a;
  float t = dot(ab, ap) / dot(ab, ab);
  t = clamp(t, 0., 1.);
  vec3 c = a + ab * t;

  return length(p - c) - r;
}

vec3 rep(vec3 p, vec3 r) {
  vec3 q = mod(p, r) - .5 * r;

  return q;
}

float at = 0.;
float map(vec3 p) {
  p.xy *= rot2d(sin(3. * time) * sin(p.z / 15.));
  p.xz *= rot2d(sin(.01 * time) * PI);
  p = rep(p, vec3(5, 5, 5));
  float d = 5000.;
  vec3 a1 = vec3(-2, 0, 0);
  vec3 b1 = vec3(2, 0, 0);
  vec3 a2 = vec3(0, -2, 0);
  vec3 b2 = vec3(0, 2, 0);
  vec3 a3 = vec3(0, 0, -3.);
  vec3 b3 = vec3(0, 0, 3.);
  float t = floor(time * .5) + smoothstep(.3, .6, fract(time * .5));
  a1.xy *= rot2d(t);
  b1.xy *= rot2d(t);
  a2.xy *= rot2d(t);
  b2.xy *= rot2d(t);
  d = min(d, tube(p, a1, b1, .5));
  d = min(d, tube(p, a2, b2, .5));
  d = min(d, tube(p, a3, b3, .5));

  float t_wave = floor(time) + fract(time);
  float wave = .5 * sin(t_wave) + .5;
  vec3 shift = vec3(.2 * sin(time), 0, 0);
  d = min(d, sph(p + shift, 1.));

  at += .1 / (.1 + d);

  return d;
}


vec3 glow = vec3(0, 0, 0);
float rm(vec3 ro, vec3 rd) {
  float d = 0.;

  for (int i = 0; i < 100; i++) {
    vec3 p = ro + d * rd;
    float ds = map(p);

    if (ds < 0.01 || ds > 100.) {
      break;
    }

    d += ds * 1.;
    glow += .02 * at * vec3(0, 1., 0.);
  }

  return d;
}

vec3 normal(vec3 p) {
  vec2 e = vec2(0.01, 0);

  vec3 n = map(p) - vec3(
    map(p - e.xyy),
    map(p - e.yxy),
    map(p - e.yyx)
  );

  return normalize(n);
}

float light(vec3 p) {
  vec3 lp = vec3(0, 0, ZPOS);
  vec3 tl = lp - p;
  vec3 tln = normalize(tl);
  vec3 n = normal(p);
  float dif = dot(n, tln);
  float d = rm(p + 2. * n, tln);

  if (d < length(tl)) {
    dif *= .1;
  }

  return dif;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

	vec3 ro = vec3(0, 0, ZPOS);
  	vec3 rd = normalize(vec3(uv, 1.));
  	float d = rm(ro, rd);
  	vec3 p = ro + d * rd;
//  	float dif = light(p);

//    vec3 col = .2 * dif * glow;
    vec3 col = .2 * glow;

    fragColor = vec4(col,1.0);
