#define FAR 32.

const float pi = 3.14159;

mat2 rot(float a){return mat2(cos(a), -sin(a), sin(a), cos(a));}

float sdBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float opsub( float d1, float d2 ) { return max(-d1,d2); }

float minDist = 999.;
float mat = -1.0;

void cd(float tDist, float m, out float tmat){
	if(tDist <minDist){
		minDist = tDist;
		mat = m;
		tmat = m;
	}
}

const vec3 gd = vec3(1.4, 0.5, 0.5);

float frame(vec3 p){
    float d = sdBox(p, gd+.05);
    d = opsub(sdBox(p, vec3(gd.x-.05, gd.y-.05, gd.z + 1.)),d );
    d = opsub(sdBox(p, vec3(gd.x+1., gd.y-.05, gd.z -.05)),d );
    d = opsub(sdBox(p, vec3(gd.x-.05, gd.y+1., gd.z -.05)),d );

    return d;
}

vec2 map(vec3 rp){
    float d = 999.;
    float hmat = 0.;

 	vec3 p0 = rp - vec3(0.0, 0.0, 3.0);
    vec3 p1 = rp - vec3(0.0, 0.0, 3.0);

    p0.xy *= rot(iTime*.4);
    p0.xz *= rot(iTime*.3);
    p0.yz *= rot(-sin(iTime)*.2);

    d = min(d, abs(sdBox(p0, gd)) - 0.005);
    cd(d, 0., hmat);

    d = min(d, frame(p0));
    cd(d, 2., hmat);

    d = min(d, length(p1)-.1);
    cd(d, 1., hmat);     

    rp.xy *= rot(iTime);
    vec3 p2 = rp - vec3(.3*sin(iTime*2.), 0.0,3. + .3*cos(iTime*2.) );
    d = min(d, length(p2)-.05);


    cd(d, 3., hmat); 

    return vec2(d, hmat);
}
vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy).x + 
        e.yyx * map(pos + e.yyx).x + 
        e.yxy * map(pos + e.yxy).x + 
        e.xxx * map(pos + e.xxx).x);
}

vec2 march(vec3 rd, vec3 ro){
 	float t = 0.;   
    vec2 d = vec2(0);

    for(int i = 0; i < 64; i++){
    	d = map(ro + rd*t); 	   

        if(abs(d.x) < 0.002)break;
        if(t > FAR){t = FAR;break;}
        t += d.x * 0.9;
    }   

    return vec2(t, d.y);
}
vec3 color(vec3 p, vec3 rd, vec3 n, float t, float m){
    vec3 lp = vec3(0., 4.0, -5.0);
    vec3 ld = normalize(lp-p);
   	vec3 ref = reflect( rd, n );

    float lgd = length(lp - p);
    float faloff = 1.-exp(-(4. / lgd));

    float spec = pow(max(dot(normalize(reflect(ld, n)), rd), 0.), 32.);
    float dom = smoothstep(-0.2, 0.2, ref.y);
    float dif = max(dot(n, ld), .0);

    vec3 col = vec3(0);    
    vec3 lig = vec3(0);


    if(t < FAR){
    	if(m == 0.)col = vec3(0.2, 0.3, 0.3);
    	if(m == 1.)col = vec3(1.0, 0., 0.);
    	if(m == 2.)col = vec3(.01);
    	if(m == 3.)col = vec3(0., 0.4, 1.);

    	lig += .15*dom*vec3(0.4,0.6,1.3)*dom;
    	lig += 2.*dif*vec3(1., 1., 1.) * faloff;
    	col *= lig;
    	col += vec3(0.85, 0.83, 0.85) * spec * .2;
    }
    else col = vec3(.2);

	return col;   
}

