float line(vec2 p, vec2 a,vec2 b, float width, float blur) { // --- distance to segment with caps
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    float d = length(p - b * h);                      // dist to segment

    return smoothstep(width-blur, width+blur, d);
}


