vec4 rays(vec2 uv)
{
    vec4 c = vec4(0.0);
    uv.y += sin(uv.x*uv.x * 5.0) / 4.0;

    float armLength = 0.05 + (sin(iTime / 4.0) + 1.0) / 8.0;


    vec2 pc = vec2(atan(uv.x,uv.y),sqrt(length(uv)));

    pc.y += 0.1+(sin(iTime*0.1+pc.x*50.0 ) + 1.0) / 4.0;

    vec4 rays = vec4(1.0-line(pc, vec2(-PI/3.0,armLength), vec2(PI / 3.0,armLength), 0.5, 0.02), 0.0,0.0,0.0);


   	rays.g += smoothstep(0.4,0.0,length(uv)) * rays.r;

    rays *= smoothstep(0.025,0.05,length(uv));

    c += rays;  

    return c;
}

