// From Lighthouse
uint WangHash(inout uint s)
{
    s = (s ^ uint(61)) ^ (s >> 16);
    s *= uint(9);
    s = s ^ (s >> 4);
    s *= uint(0x27d4eb2d);
    s = s ^ (s >> 15);
    return s;
}

uint RandomInt(inout uint s)
{
    s ^= s << 13;
    s ^= s >> 17;
    s ^= s << 5;
    return s;
}

