    sphereCenters[1] = vec3(xPos, iResolution.y * 0.75 - yPos, 0.0);

    vec3 lightPos = vec3(iResolution.x * (1.5), iResolution.y * 0.5, -1.5 * iResolution.x);

    vec3 rayOrigin = vec3(iResolution.x * 0.5, iResolution.y * 0.5, -iResolution.x);
    vec3 rayDst = vec3(pixelCoord.xy, 0.0);
    vec3 rayDir = normalize(rayDst - rayOrigin);
    vec3 normals[2];

    float grayscale = RaytraceScene(rayOrigin, rayDir, sphereCenters, lightPos);
    float ditherColor = 0.0;
    if(pixelCoord.y > iResolution.y * 0.5)
    {
        ditherColor = GetBayerDither(grayscale, ivec2(pixelCoord));
    }else
    {
        ditherColor = GetBlueNoiseDither(grayscale, ivec2(pixelCoord));
    }
