// Signed distance and gradient to a box. Probably
// faster than central differences or automatic
// differentiation/dual numbers.

// List of other 2D distances+gradients:
// https://iquilezles.org/www/articles/distgradfunctions2d/distgradfunctions2d.htm
//
// Circle:             https://www.shadertoy.com/view/WltSDj
// Pie:                https://www.shadertoy.com/view/3tGXRc
// Arc:                https://www.shadertoy.com/view/WtGXRc
// Isosceles Triangle: https://www.shadertoy.com/view/3dyfDd
// Triangle:           https://www.shadertoy.com/view/tlVyWh
// Box:                https://www.shadertoy.com/view/wlcXD2
// Quad:               https://www.shadertoy.com/view/WtVcD1
// Cross:              https://www.shadertoy.com/view/WtdXWj
// Segment:            https://www.shadertoy.com/view/WtdSDj
// Hexagon:            https://www.shadertoy.com/view/WtySRc
// Vesica:             https://www.shadertoy.com/view/3lGXRc
// Smooth-Minimum:     https://www.shadertoy.com/view/tdGBDt
// Parallelogram:      https://www.shadertoy.com/view/sssGzX


// Other Box functions
// http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
//
// Intersection:     https://www.shadertoy.com/view/ld23DV
// Occlusion:        https://www.shadertoy.com/view/4sSXDV
// Occlusion:        https://www.shadertoy.com/view/4djXDy
// Density:          https://www.shadertoy.com/view/Ml3GR8
// Fake soft shadow: https://www.shadertoy.com/view/WslGz4
// Gradient:         https://www.shadertoy.com/view/wlcXD2


// .x = f(p)
// .y = ∂f(p)/∂x
// .z = ∂f(p)/∂y
// .yz = ∇f(p) with ‖∇f(p)‖ = 1
vec3 sdgBox( in vec2 p, in vec2 b )
{
    vec2 w = abs(p)-b;
    vec2 s = vec2(p.x<0.0?-1:1,p.y<0.0?-1:1);

    float g = max(w.x,w.y);
	vec2  q = max(w,0.0);
    float l = length(q);

    return vec3(   (g>0.0)?l   : g,
                s*((g>0.0)?q/l : ((w.x>w.y)?vec2(1,0):vec2(0,1))));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // corner radious
    float ra = 0.1*(0.5+0.5*sin(iTime*1.2));

    // sdf(p) and gradient(sdf(p))
    vec3  dg = sdgBox(p,vec2(0.8,0.3));
    float d = dg.x-ra;
    vec2 g = dg.yz;

    // central differenes based gradient, for comparison
    // g = vec2(dFdx(d),dFdy(d))/(2.0/iResolution.y);

	// coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.4,0.7,0.85);
    col *= 1.0 + vec3(0.5*g,0.0);
  //col = vec3(0.5+0.5*g,1.0);
    col *= 1.0 - 0.5*exp(-16.0*abs(d));
	col *= 0.9 + 0.1*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

	fragColor = vec4(col,1.0);
}

#define WORLEY_EUCLIDIAN 0
#define WORLEY_MANHATTAN 1
#define WORLEY_CHEBYSHEV 2
#define WORLEY_TRIANGULAR 3
#define WORLEY_8THDEG 4
#define WORLEY_SINE 5
#define WORLEY_MINK4 6
#define WORLEY_MINK05 7
#define WORLEY_QUAD 8


uint BaseHash(uvec2 p)
{
	const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
	uint h32 = p.y + PRIME32_5 + p.x*PRIME32_3;
	h32 = PRIME32_4 * ((h32 << 17) | (h32 >> (32 - 17))); 
	h32 = PRIME32_2 * (h32 ^ (h32 >> 15));
	h32 = PRIME32_3 * (h32 ^ (h32 >> 13));
	return h32 ^ (h32 >> 16);
}

vec2 Hash22(uvec2 x)
{
	uint n = BaseHash(x);
	uvec2 rz = uvec2(n, n * 48271U);
	return vec2((rz.xy >> 1) & uvec2(0x7fffffffU, 0x7fffffffU)) / float(0x7fffffff);
}


vec2 Random2DVector2(vec2 p)
{
	return Hash22(uvec2(floatBitsToUint(p.x),floatBitsToUint(p.y)));
}

float RootN(float x, float y)
{
    return pow(x, (1.0 / y));
}

float Length2P8(vec2 p)
{
    float d = pow(p.x,8.0) + pow(p.y,8.0);
    return RootN(d, 8.0);
}

float Map(float value, float old_lo, float old_hi, float new_lo, float new_hi)
{
    float old_range = old_hi - old_lo;
    float new_range = new_hi - new_lo;
    return (((value - old_lo) * new_range) / old_range) + new_lo;
}

float DistanceByMetric(vec2 p, vec2 o, vec2 r, int distanceMetric)
{
	float d = 0.0;
	switch (distanceMetric)
	{
	case WORLEY_EUCLIDIAN:
		d = length(r); // euclidian
		break;

	case WORLEY_MANHATTAN:
		d = abs(r.x) + abs(r.y);  // manhattan
		break;

	case WORLEY_CHEBYSHEV:
		d = max(abs(r.x), abs(r.y)); // chebyshev
		break;

	case WORLEY_TRIANGULAR:
		d = max(abs(r.x)*0.866025 + r.y*0.5, -r.y); // triangular xy plane
		break;

	case WORLEY_8THDEG:
		d = Length2P8(r); // 8th degree len.
		break;

    case WORLEY_SINE:
        d = length(r) + (1.0 + sin(16.0*length(r))) * 0.5;
        break;

    case WORLEY_MINK4:
        d = sqrt(sqrt(dot(r * r, r * r)));
        break;

    case WORLEY_MINK05:
        d = sqrt(sqrt(abs(r.x))+sqrt(abs(r.y)) );
        break;

    case WORLEY_QUAD:
        d = r.x*r.x+r.y*r.y+r.x*r.y;
        break;


	}

	return d;
}

vec4 ComputeWaveGradientRGB(float t, vec4 bias, vec4 scale, vec4 freq, vec4 phase)
{
	vec4 rgb = bias + scale * cos(3.14159 * 2.0 * (freq * t + phase));
	return vec4(clamp(rgb.xyz,0.0,1.0), 1.0);
}

void WorleyNoise2D(
    vec2 p, 
    int distanceMetric, 
    bool invert, 
    float falloff,
    out vec4 cca, 
    out vec4 ccb, out vec4 ccc)
{

	vec2 n = floor(p);
	vec2 nf = fract(p);
	vec2 h = step(0.5, nf) - 2.0;
	n += h; 
	nf -= h;

	vec4 f = vec4(8.0, 8.0, 8.0, 8.0);
	vec2 mg, mr;
	float res = 0.0;

	for(int j=0; j<=3; j++)
	for (int i=0; i<=3; i++)
	{
		vec2 g = vec2(float(i), float(j));
		vec2 o = Random2DVector2(n + g);
		vec2 r = g + o - nf;

		float d = DistanceByMetric(p, o, r, distanceMetric);

        res += 1.0 / pow(d, falloff);

		if (d < f.x)
		{
			f.w = f.z;
			f.z = f.y;
			f.y = f.x;
			f.x = d;
			mr = r;
			mg = g;
		}
		else if (d < f.y)
		{
			f.w = f.z;
			f.z = f.y;
			f.y = d;

		}
		else if (d < f.z)
		{
			f.w = f.z;
			f.z = d;

		}
		else if (d < f.w)
		{
			f.w = d;
		}



	}

	// distance to borders

	float minDist = 8.0;
	for (int j = 0; j <= 3; j++)
		for (int i = 0; i <= 3; i++)
		{
			vec2 g = vec2(float(i), float(j));
			vec2 o = Random2DVector2(n + g);
			vec2 r = g + o - nf;

			if (dot(mr - r, mr - r) > 0.00001)
			{
				float dist = dot(0.5*(mr + r), normalize(r - mr));
				minDist = min(minDist, dist);
			}
		}

    float v;

	// f1
	cca.x = invert ? 1.0 - f.x : f.x;

	// f2
	cca.y = invert ? 1.0 - f.y : f.y;

	// f3
	cca.z = invert ? 1.0 - f.z : f.z;

	// f4
    v= clamp(max(0.5,16.0 * (f.y-f.x)), 0.0, 1.0);
    v = minDist < (falloff/50.0) ? (1.0 - minDist) : 0.0; // 1.0: 0.0;
	cca.w = invert ? 1.0 - v: v;


	// f2 - f1
	v = f.y - f.x;
	ccb.x = invert ? 1.0 - v : v;

	// f1 + f2
	v = f.x + f.y;
	ccb.y = invert ? 1.0 - v : v;

	// (f1 + f2 + f3 + f4) / 4
	v = (f.x + f.y + f.z + f.w) * 0.25;
	ccb.z = invert ? 1.0 - v : v;

	// border distance
	ccb.w = minDist;

    // f4
    ccc.x = invert ? 1.0 - f.w : f.w;

    // rounded
	v = 1.0 / (1.0 / (f.y - f.x) + 1.0 / (f.z - f.x));
	ccc.y = invert ? 1.0 - v : v;

    // f2^2 - f1^2
	v = (f.y*f.y) - (f.x*f.x);
	ccc.z = invert ? 1.0 - v : v;

    // smoothed
	v = pow(1.0 / res, 0.5 / falloff);
	ccc.w = invert ? 1.0 - v : v;


}

vec4 MapColor(float intensity, int pallette)
{
    vec4 retval;

    switch(pallette)
    {
        case 0: retval = vec4(intensity); break;
        case 1: 
        {
            vec4 bias = vec4(0.000, 0.500, 0.500, 1.0);
            vec4 scale = vec4(0.000, 0.500, 0.500, 1.0);
            vec4 freq = vec4(0.000, 0.500, 0.333, 1.0);
            vec4 phase = vec4(0.000, 0.500, 0.667, 1.0);

    		vec4 col = ComputeWaveGradientRGB(intensity,bias,scale,freq,phase);
    		retval =  vec4(col.xyz, 1.0);
        }
        break;

        case 2: retval = vec4(clamp(vec3(3.0, 3.0, 4.0) * intensity - vec3(0.0, 1.0, 3.0), 0.0, 1.0),1.0); break;

    }

    return retval;
}

bool InsideCount(vec2 px, int count, vec2 tl)
{
    bool rv = false;

    float md = 9999.0;
    for(int i =0; i < count; i++)
    {
        vec2 c = vec2(tl.x + (float(i) * 20.0), tl.y);
        float d = length(px-c);
        md = min(d, md);
    }

    rv = md < 8.0;

    return rv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = vec2(iResolution.x, iResolution.y);
	float maxRes = max(res.x, res.y);
	vec2 pos = vec2(fragCoord.x,fragCoord.y);
    vec2 p = (2.0 * pos - res) / min(res.x, res.y);

    float scale = Map( (sin(iTime * 0.4)+1.0) * 30.0, 0.0,60.0,1.0,4.0);
    vec2 translate = vec2(iTime * 0.33);

    p *= scale;
    p += translate;

    int metric = 0; // 0 - 6 
    int data = 0;   // 0 - 7 change every 6s
    bool invert = false; // change every 3s

    int tScale = 1;
    int iSec = int(floor(iTime)) / tScale;

    invert = (iSec / 3) % 2 == 1;
    data = (iSec / 6) % 12;
    metric = (iSec / 72) % 9;


    vec4 cca, ccb, ccc;
    WorleyNoise2D(p, metric, invert, Map(sin(iTime * 3.0), -1.0, 1.0, 2.0, 8.0), cca, ccb, ccc);

    float value= 0.0;
    switch(data)
    {
        // data index (GREEN)
        case 0:value = cca.x; break; // f1
        case 1:value = cca.y; break; // f2
        case 2:value = cca.z; break; // f3
        case 3:value = cca.w; break; // f4
        case 4:value = ccb.x; break; // f2-f1
        case 5:value = ccb.y; break; // f1+f2
        case 6:value = ccb.z; break; // (f1+f2+f3+f4)/4
        case 7:value = ccb.w; break; // border distance
        case 8:value = ccc.x; break; // f4
        case 9:value = ccc.y; break; // rounded
        case 10:value= ccc.z; break; // f2^2 - f1^2
        case 11:value= ccc.w; break; // smoothed
    }

    vec4 col = MapColor(value, 1);

    // invert: blue. 2 = true, 1 = false
    if(InsideCount(pos, invert ? 2: 1, vec2(40.0,40.0))) col = vec4(0.0,0.0,1.0,1.0); 
    else
    // data: green. cc index
    if(InsideCount(pos, data + 1, vec2(40.0,60.0))) col = vec4(0.0,1.0,0.0,1.0);
    else
    // metric: red. 
    if(InsideCount(pos, metric + 1, vec2(40.0,80.0))) col = vec4(1.0,0.0,0.0,1.0);


    // Output to screen
    fragColor = col;
}

