#define ITERS 500
#define MAX_RAD 2.


vec2 mult( vec2 a, vec2 b) {

    vec2 result;
    result = vec2(a.x*b.x-a.y*b.y,a.y*b.x+a.x*b.y);
    return result;

}

float norm( vec2 num ){

    float norma = pow(num.x,2.) + pow(num.y,2.);
    norma = sqrt(norma);
    return norma;
}

vec2 mandelbrot( vec2 carry, vec2 num){

    vec2 next = mult(carry, carry) + num;
    return next;
}    

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //no se estaria usando
    //float speed = 5.*(iMouse.y/iResolution.y)+1.0;//medio raro
    float ratio = iResolution.x/iResolution.y;
    //float scale = pow(2.,1.-mod(iTime,20.));
    float scale = pow(2.,1.-mod(iTime,3.*5.465));

    vec2 center = vec2(-1.786448,0.);
    vec2 leftCorner = vec2(center.x-ratio*scale,center.y+scale);
    vec2 rightCorner = vec2(ratio*scale + center.x,center.y-scale);

    vec2 st = fragCoord/iResolution.xy*(rightCorner - leftCorner) + leftCorner;

    vec2 num = vec2(0.,0.);

    int i = 0;
    while(i<ITERS && norm(num)<MAX_RAD) {
        num = mandelbrot(num, st);
        i++;
    }

    vec3 color = hsb2rgb(vec3(mod(0.7+smoothstep(0.1,0.9,float(i)/float(ITERS)),1.)
                              ,0.9
                              ,smoothstep(-0.5,0.7,0.5+float(i)/float(ITERS))));
    color = vec3(step(MAX_RAD, norm(num)))*color;

    fragColor = vec4(color,1.);
}

#define S(x, y, t) smoothstep(x, y, t)
float N(float t) {
	return fract(sin(t*3456.)*6547.);//fract取小数部分,N函数取随机数
}
vec4 N14(float t){
    return fract(sin(t*vec4(123.,1024.,3456.,9576.))*vec4(6547.,345.,7585.,3212.));
}

struct ray {
	vec3 o, d;   
};

ray GetRay(vec2 uv, vec3 camPos, vec3 lookAt, float zoom) {
	ray cam;
    cam.o = camPos;

    vec3 f = normalize(lookAt-cam.o);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);

    vec3 c = cam.o + f * zoom;
    vec3 i = c + uv.x*r + uv.y*u;

    cam.d = normalize(i-cam.o);

    return cam;
} 

vec3 ClosestPoint(ray r, vec3 p) {
    // returns the closest point on ray r to point p
    return r.o + max(0., dot(p-r.o, r.d))*r.d;
}

float DistRay(ray r, vec3 p) {
	return length(p-ClosestPoint(r, p));
}

float Bokeh(ray r, vec3 p, float size, float blur) {//散焦模糊
	float d = DistRay(r, p);

    size *= length(p);
    float c = S(size, size*(1.-blur), d);
    c *= mix(.7, 1., S(size*.8, size, d));
    return c;
}

vec3 Streetlights(ray r, float t) {
    float side = step(r.d.x, 0.);//step返回1或0，比较两值大小
    r.d.x = abs(r.d.x);//镜像
    float s = 1./10.; // 0.1
    float m = 0.;
    for(float i=0.; i<1.; i+=s) {
        float ti = fract(t+i+side*s*.5);//镜像两边进程各差一半
    	vec3 p = vec3(2.,2.,100.-ti*100.);
    	m += Bokeh(r, p, .05, .1)*ti*ti*ti;
    }

    return vec3(1., .7, .3)*m;
}
vec3 Envlights(ray r, float t) {
    float side = step(r.d.x, 0.);
    r.d.x = abs(r.d.x);
    float s = 1./10.; // 0.1
    vec3 c=vec3(0.);

    for(float i=0.; i<1.; i+=s) {
        float ti = fract(t+i+side*s*.5);
        vec4 n=N14(i+side*100.);

        float fade=ti*ti*ti;
        float occlusion=sin(ti*6.28*10.*n.x)*.5+.5;//遮挡
        fade=occlusion;

        float x=mix(2.5,10.,n.x);
        float y=mix(.1,1.5,n.y);
    	vec3 p = vec3(x,y,50.-ti*50.);
        vec3 col=n.wzy;
    	c += Bokeh(r, p, .05, .1)*fade*col*.5;;
    }

    return c;
}



vec3 Headlights(ray r, float t) {

    t *= 2.;

    float w1 = .25;
    float w2 = w1*1.2;

    float s = 1./30.; // 0.1
    float m = 0.;
    for(float i=0.; i<1.; i+=s) {

        float n = N(i);

        if(n>.1) continue;

        float ti = fract(t+i);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
		float focus = S(.9, 1., ti);

        float size = mix(.05, .03, focus);

    	m += Bokeh(r, vec3(-1.-w1,.15, z), size, .1)*fade;
        m += Bokeh(r, vec3(-1.+w1,.15, z), size, .1)*fade;

        m += Bokeh(r, vec3(-1.-w2,.15, z), size, .1)*fade;
        m += Bokeh(r, vec3(-1.+w2,.15, z), size, .1)*fade;

        float ref = 0.;
        ref += Bokeh(r, vec3(-1.-w2, -.15, z), size*3., 1.)*fade;
        ref += Bokeh(r, vec3(-1.+w2, -.15, z), size*3., 1.)*fade;

        m += ref*focus;
    }

    return vec3(.9, .9, 1.)*m;
}

vec3 Taillights(ray r, float t) {

    t *= .25;

    float w1 = .25;
    float w2 = w1*1.2;

    float s = 1./15.; 
    float m = 0.;
    for(float i=0.; i<1.; i+=s) {

        float n = N(i); // 0 - 1

        if(n>.5) continue;

        // n = 0 - 0.5

        float lane = step(.25, n); // 0 1

        float ti = fract(t+i);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
		float focus = S(.9, 1., ti);

        float size = mix(.05, .03, focus);

        float laneShift = S(1., .96, ti);
        float x = 1.5 - lane * laneShift;

        float blink = step(0., sin(t*1000.))*7.*lane*step(.96, ti);

    	m += Bokeh(r, vec3(x-w1,.15, z), size, .1)*fade;
        m += Bokeh(r, vec3(x+w1,.15, z), size, .1)*fade;

        m += Bokeh(r, vec3(x-w2,.15, z), size, .1)*fade;
        m += Bokeh(r, vec3(x+w2,.15, z), size, .1)*fade*(1.+blink);

        float ref = 0.;
        ref += Bokeh(r, vec3(x-w2, -.15, z), size*3., 1.)*fade;
        ref += Bokeh(r, vec3(x+w2, -.15, z), size*3., 1.)*fade*(1.+blink*.1);

        m += ref*focus;
    }

    return vec3(1., .1, .03)*m;
}
vec2 Rain(vec2 uv, float t){
    t*=40.;
    //uv*=3.;
    vec2 a=vec2(3.,1.);
    vec2 st=uv*a;//另一组uv
    vec2 id=floor(st);
    st.y+=t*.22;//网格排列下降，使看不出雨滴上升

    float n=fract(sin(id.x*716.34)*768.34);
    st.y+=n;//列随机上下排布，让雨滴不平行落下
    uv.y+=n;//使随机数不会抵消，雨滴不会在同一高度
    id=floor(st);
    st=fract(st)-0.5;

    t+=fract(sin(id.x*76.34+id.y*1457.3)*768.63)*6.283;//使各个雨滴不同时落下;
    float y=-sin(t+sin(t+sin(t)*0.5))*.43;//缓慢落下又快速上升，0.43使其不离开边缘
    vec2 p1=vec2(0.,y);//只做y轴运动
    vec2 o1=(st-p1)/a;//st/a恢复圆形，-p1为运动
    float d=length(o1);

    float m1=S(.07,.0,d);//遮罩，即雨滴形状

    vec2 o2=(fract(uv*a.x*vec2(1.,2.))-.5)/vec2(1.,2.);//从三个小雨滴变成六个再恢复圆形
    d=length(o2);

    float m2=S(.3*(.5-st.y),.0,d)*S(-.1,.1,st.y-p1.y);//拖尾的雨滴，第一个s雨滴越往下越大，第二个s跟随大雨滴后面 
    return vec2(m1*o1*30.+m2*o2*10.);//乘以偏移量
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;

    float t = iTime*.05+m.x;

    vec3 camPos = vec3(.5, 0.2, 0.);
    vec3 lookAt = vec3(.5, 0.2, 1.);

    vec2 rainDistort=Rain(uv*5.,t)*.5;
    rainDistort+=Rain(uv*7.,t)*.5;

    uv.x+=sin(uv.y*70.)*.003;
    uv.y+=sin(uv.x*170.)*.001;

    ray r = GetRay(uv-rainDistort*.5, camPos, lookAt, 2.);



    vec3 col = Streetlights(r, t);
    col += Headlights(r, t);
    col += Taillights(r, t);
    col += Envlights(r,t);

    col += (r.d.y+.25)*vec3(.2,.1,.5);//天空渐变

	fragColor = vec4(col, 1.);
}

