// Space parametrization of a circle.
//
// It shows a global coordinate system were things stretch at the
// sides BUT coodinates are global and physical, and also a local
// coordinate system that is a regular grid full of circles).

// List of all shape parametrizations: https://www.shadertoy.com/playlist/XclfRs



float dot2( in vec2 v ) { return dot(v,v); }
float cro(in vec2 a, in vec2 b) { return a.x*b.y-a.y*b.x; }

// x = local dist
// y = local perimeter dist
// z = total local perimeter
// w = global distance (sdf)
vec4 paCircle( in vec2 p, float r, float band ) 
{
    float d = length(p) - r;
    float ra = band*round(d/band);
    float l = (r+ra)*(atan(p.x,p.y)+3.1415927);
    return vec4( d-ra, l, 6.283185*(r+ra), d );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    const float ra = 0.5 ;
    const float bandSize = 0.15;

    // distance
    vec4 b = paCircle(uv,ra,bandSize);

    // base color
    float d = b.w;
    vec3 col = vec3(1.0,0.68,0.35) + vec3(-0.35,0.15,0.6)*step(d,0.0);
    col *= 1.0 - 0.6*exp(-64.0*abs(d));
    col *= 1.0-smoothstep(0.47,0.50,abs(fract(d/bandSize)-0.5));
	col *= 0.9 + 0.2*smoothstep(0.26,0.24,abs(fract(0.5*d/bandSize+0.25)-0.5));
    col += smoothstep(0.004, 0.002, abs(d));


    if( d>-bandSize*0.5 )
    {

	vec2 q = b.xy;
    // optional - ensure periodicity, but break physicallity
    q.y *= floor(b.z/bandSize)*(bandSize/b.z);

    // animate circles
    q.y -= iTime*0.1;

    // draw circles
    vec2 uv = fract(q/bandSize+0.5)-0.5;
    float l = length(uv);
    col *= 0.1 + 0.9*smoothstep(0.01,0.04,abs(l-0.35));
    col *= 0.1 + 0.9*smoothstep(0.10,0.11,l);
    }

	fragColor = vec4(col, 1.0);
}

#define USE_NOISE 1
#define USE_BLUE_NOISE 1

float gSeed = 0.0;
vec2 gUV;

float hash( float n )
{
    return fract(sin(n + gSeed)*43758.5453);
}

float random() {

    return hash(fract(sin(dot(gUV, vec2(12.9898, 78.233)) * (gSeed += 1.0)) * 43758.5453));
}

mat2 rot(float r)
{
    float c = cos(r), s = sin(r);
    return mat2(c, s, -s, c);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float box(vec3 p, vec3 s)
{
    vec3 d = abs(p) - s;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float map(vec3 p)
{
    vec3 pr = p;
    pr.yz *= rot(1.6);
    float a = min(min(length(p + vec3(0.0, 0.5, 0.0)) - 0.7, sdTorus(pr + vec3(0.0, 1.0, 2.0), vec2(1.0, 0.4))), p.y + 1.0);
    float b = box(p + vec3(0.0, 0.0, -3.0), vec3(200.0, 200.0, 0.3));
    b = max(b, -box(p + vec3(0.0, -3.0, 0.0), vec3(1.0, 3.5, 20.0)));
    b = max(b, -box(p + vec3(0.0, -3.0, 0.0), vec3(2.0, 1.0, 20.0)));
    return min(a, b);
}

vec3 norm(vec3 p)
{
    const vec2 e = vec2(0.0, 0.001);
    return normalize(vec3(
        map(p + e.yxx) - map(p - e.yxx),
        map(p + e.xyx) - map(p - e.xyx),
        map(p + e.xxy) - map(p - e.xxy)
    ));
}

vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 color = vec3(0.0);
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    gUV = uv;
    gSeed = 0.0;

    uv.x *= iResolution.x / iResolution.y;

    bool useMouseCtrl = iMouse.z > 0.0;

    float mx = 1.4 * sin(iTime * 0.2);
    float my = 0.24;

    if (useMouseCtrl)
    {
	    mx = (iMouse.x / iResolution.x * 2.0 - 1.0);
    	my = 0.1 + (iMouse.y / iResolution.y);
    }

    vec3 l = vec3(0.0, my, 1.0);
    vec3 ro = vec3(0.0, 3.0, -20.0);
    vec3 rd = normalize(vec3(uv.x, uv.y - 0.2, 4.0));

    ro.xz *= rot(mx);
    rd.xz *= rot(mx);

    // moon rendering
    float lt = 0.0;
    float moonGlow = 0.0;
    for (int i = 0; i < 100; ++i)
    {

        float ld = (length((ro + rd * lt) - (l * 50.0)) - 0.2);
        moonGlow += 4.0/(0.1+pow(ld,2.0));
        if (ld < 0.1) 
        {
            color += 1.0;
            break;
        }
        lt += ld;

    }
    color += vec3(0.01) * moonGlow;

    float t = 0.0;

    for (int i = 0; i < 300; ++i)
    {
        float d = map(ro + rd * t);
        if (d < 0.01)
        {
            break;
        }
        t += d;
        if (t > 100.0) break;
    }

    // lambert reflectance
    if (t < 100.0)
    {
        vec3 p = ro + rd * t;
        vec3 n = norm(p);
        vec3 v = normalize(ro - p);
        vec3 h = normalize(v + l);
        #if 1
        float NdotH = dot(n, h) * 0.5 + 0.5;
        float NdotL = dot(n, l) * 0.5 + 0.5;
        #else
        float NdotH = max(dot(n, h),0.0);
        float NdotL = max(dot(n, l),0.0);        
        #endif
        vec3 checkerP = p * 1.0;
        float checker = 0.5+mod(floor(checkerP.x) + floor(checkerP.y) + floor(checkerP.z), 2.0);
        float sh = 1.0;

        // hard shadows 
        #if 1
        vec3 srd = l;
        vec3 sro = p + srd * 0.1;
        float st = 0.0;
        for (int i = 0; i < 64; ++i)
        {
            float d = map(sro + srd * st);
            if (d < 0.001)
            {
                sh = 0.0;
                break;
            }
            st += d;
            if (st > 100.0) break;
        }
        #endif
        vec3 checkerColor = vec3(0.1) + checker * vec3(0.1);
        color = checkerColor + vec3(0.6, 0.6, 1.0) * (NdotL*sh) + pow(NdotH * sh, 10.0)*0.1;
        color = mix(color, vec3(0.0), clamp(t / 40.0, 0.0, 1.0));
    }

    // volumetric light
    #if 1
    {    
        vec3 lc = vec3(0.0);
        float t1 = 0.0;
        for (int i = 0; i < 150; ++i)
        {
            vec3 o = ro + rd * t1;
            float t2 = 0.0;
            for (int j = 0; j < 60; ++j)
            {
                float d = map(o + l * t2);
                if (d < 0.01)
                {
                    break;
                }
                t2 += d;
                if (t2 > 100.0)
                {
                    lc += 0.4;
                    break;
                }
            }
            #if USE_NOISE
            #if USE_BLUE_NOISE
            t1 += 0.1 + (texture(iChannel0, (o + l * t2).xz).r) * 0.2;
            #else
            t1 += 0.1 + random() * 0.2;
            #endif
            #else
            t1 += 0.2;
            #endif
            if (t1 > t) break;
        }
        color += clamp(lc / 100.0, 0.0, 0.1) * (vec3(0.1, 0.1, 0.2) * 10.0);
    }
    #endif

    fragColor = vec4(acesFilm(color), 1.0);
}

// Ray intersection test with bilinear surface defined by 4 points
bool traceBilinearPatch(vec3 ro, vec3 rd, vec3 pa, vec3 pb, vec3 pc, vec3 pd,
                        inout vec3 outs, inout vec3 outt, inout vec3 outuvi)
{
    vec3 va = pc - pa;
    vec3 vb = (pd - pc) - (pb - pa);
    vec3 vd = ro - pa;
    vec3 vc = pa - pb;

    // Note that the coefficients are in reverse order here. Solving this
    // equation gives 1 / x and avoids a divide-by-zero case for coplanar controlpoints
    // by switching that case to c = 0 instead of a = 0, thus putting the denominator
    // in the solution as -b instead of 0.
    // Thanks to IQ for this trick!

    float c = dot(cross(vb, vc), rd);
    float b = dot(cross(va, vc) + cross(vb, vd), rd);
    float a = dot(cross(va, vd), rd);

    float desc = b * b - 4. * a * c;

    if(desc < 0.0)
        return false;

    // Put PA at the origin of the coordinate system

    ro -=pa;

    pc = va;

    pd -= pa;
    pb -= pa;
    pa -= pa;

    float i;
    float u, v;

    // Solve for U at each intersection point, which are two quadratics

    float u0 = (2. * a) / (-b - sqrt(desc));
    float u1 = (2. * a) / (-b + sqrt(desc));

    vec3 pu0 = pb * u0;
    vec3 pu1 = pb * u1;

    // Solve for V at each intersection point, geometrically

    vec3 vv0 = mix(pc, pd, u0) - pu0;
    vec3 m20 = ro - pu0 - vv0 * dot(vv0, ro - pu0) / dot(vv0, vv0);

    vec3 vv1 = mix(pc, pd, u1) - pu1;
    vec3 m21 = ro - pu1 - vv1 * dot(vv1, ro - pu1) / dot(vv1, vv1);


    float v0, v1;

    {
        vec3 n = cross(va + vb * u0, vd + vc * u0);
        vec3 m = cross(n, rd);

        float da = dot(pu0 - ro, m);
        float db = dot(pc + (pd - pc) * u0 - ro, m);

        v0 = da / (da - db);
    }

    {
        vec3 n = cross(va + vb * u1, vd + vc * u1);
        vec3 m = cross(n, rd);

        float da = dot(pu1 - ro, m);
        float db = dot(pc + (pd - pc) * u1 - ro, m);

        v1 = da / (da - db);
    }

    // Solve for the ray intersection distance at each intersection point

    float da20 = dot(ro - pu0, m20);
    float db20 = dot((ro + rd) - pu0, m20);
    float i0 = da20 / (da20 - db20);

    float da21 = dot(ro - pu1, m21);
    float db21 = dot((ro + rd) - pu1, m21);
    float i1 = da21 / (da21 - db21);

    // Resolve which valid intersection point is nearest to ray origin

    if(u0 < 0. || u0 > 1. || i0 < 0. || v0 < 0. || v0 > 1.)
    {
        u = u1;
        v = v1;
        i = i1;
    }
    else if(u1 < 0. || u1 > 1. || i1 < 0. || v1 < 0. || v1 > 1.)
    {
        u = u0;
        v = v0;
        i = i0;
    }
    else
    {
        u = mix(u0, u1, step(i1, i0));
        v = mix(v0, v1, step(i1, i0));
        i = min(i0, i1);
    }

    if(u < 0. || u > 1. || i < 0. || v < 0. || v > 1.)
        return false;

    outuvi = vec3(u, v, i);

    // Surface differentials in worldspace
    outs = pb * u - mix(pc, pd, u);
    outt = pc * v - mix(pb, pd, v);

    return true;
}

mat3 rotX(float a)
{
    return mat3(1., 0., 0.,
                0., cos(a), sin(a),
                0., -sin(a), cos(a));
}

mat3 rotY(float a)
{
    return mat3(cos(a), 0., sin(a),
                0., 1., 0.,
                -sin(a), 0., cos(a));
}

mat3 rotZ(float a)
{
    return mat3(cos(a), sin(a), 0.,
                -sin(a), cos(a), 0.,
                0., 0., 1.);
}

vec3 transformPoint(vec3 p, float t)
{
    mat3 m = rotX(cos(t) * 3.) * rotY(sin(t / 2.) * 3.) * rotZ(sin(t / 3.) * 3.);
    return m * p * (1. + cos(t * .7) * .2);
}

vec3 colourRamp(float x)
{
    return clamp(1. - sin((1. - x) * vec3(.5,9.5,3.5).brg * .7), 0., 1.5) * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    // Set up primary ray

    vec3 ro = vec3(0., 0., 4.);
    vec3 rd = normalize(vec3(uv.xy, -2.));

    vec3 col = vec3(0.01);

    float dt = 1. / 6.;

    float t0 = iTime - dt * 2.;
    float t1 = iTime - dt;
    float t2 = iTime;

    // 12 edges of the cube

	for(int axis = 0; axis < 3; ++axis)
    {
        for(int i = 0; i < 4; ++i)
        {
            vec3 p0, p1;

            p0[(axis + 1) % 3] = float(i & 1) * 2. - 1.;
            p0[(axis + 2) % 3] = float((i / 2) & 1) * 2. - 1.;

            p1 = p0;

            p0[axis] = -1.;
            p1[axis] = +1.;

		    vec3 s, t, uvi;
            vec3 pa, pb, pc, pd;

            pa = transformPoint(p0, t0);
            pb = transformPoint(p0, t1);
            pc = transformPoint(p1, t0);
            pd = transformPoint(p1, t1);

            if(traceBilinearPatch(ro, rd, pa, pb, pc, pd, s, t, uvi))
                col += colourRamp(uvi.x * .5);

            pa = transformPoint(p0, t1);
            pb = transformPoint(p0, t2);
            pc = transformPoint(p1, t1);
            pd = transformPoint(p1, t2);

            if(traceBilinearPatch(ro, rd, pa, pb, pc, pd, s, t, uvi))
                col += colourRamp(.5 + uvi.x * .5);
        }
    }

    //col=vec3(0);
	vec3 s, t, uvi;
    //if(traceBilinearPatch(ro, rd, vec3(-1,-1,0),vec3(+1,-1,0),vec3(-1,+1,0),vec3(+1,+1,0), s, t, uvi))
      //  col=vec3(1);

    fragColor.rgb = col * .9;

    // Gamma correction

    fragColor.rgb = pow(fragColor.rgb, vec3(1. / 2.2));
    fragColor.a = 1.;
}

