	vec3 col = vec3(0);
    vec3 ro = vec3(XPOS, YPOS, ZPOS);
//    ro.x -= step(10. * time;
    vec3 rd = normalize(vec3(uv, 1.));
    float d = rm(ro, rd);
    vec3 p = ro + d * rd;
    float dif = light(p);

    col = .2 * dif + glow * vec3(sin(p.z / 10.));

    fragColor = vec4(col,1.0);
}

