}

vec2 rot2d(vec2 uv, float deg) {
    return vec2(cos(deg) * uv.x - sin(deg) * uv.y, sin(deg) * uv.x + cos(deg) * uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
