vec2 rand(vec2 uv){
    uv.x += 0.0004;
    uv.y += 0.0007;
    uv.x = floor(uv.x / 0.001) * 0.001;
    uv.y = floor(uv.y / 0.001) * 0.001;
    uv += vec2(13., 63.);
    uv.x = fract(sin(uv.x * 10. * uv.y * uv.y)  * 438.5453);
    uv.y = fract(cos(uv.x * uv.x * 10. * uv.y)  * 561.5453);
    return uv;
}

vec2 uvtransform(vec2 uv, float strength){
    float clsz = 0.5; // cellsize
    //vec2 nwuv = float(int(uv / clsz)) * clsz;
    int X = int(uv.x / clsz);
    int Y = int(uv.y / clsz);
    vec2 nwuv = vec2(float(X), float(Y)) * clsz;
    vec2 A = mix(rand(nwuv + vec2(0., 0.)  ), rand(nwuv + vec2(clsz, 0.)  ), (uv - nwuv).x / clsz);
    /*vec2 A = mix(
        rand(vec2(float(X), float(Y))), 
        rand(vec2(float(X + 1), float(Y))), 
        (uv - nwuv).x / clsz
    );*/
    vec2 B = mix(rand(nwuv + vec2(0., clsz)), rand(nwuv + vec2(clsz, clsz)), (uv - nwuv).x / clsz);
    vec2 ans = mix(A, B, (uv - nwuv).y / clsz);
    return uv + (ans - vec2(0.5)) * 2. * strength;
}

vec2 distortion(vec2 uv, float strength, int power){
    for (int i = 0; i < power; i++){
        uv = uvtransform(uv, strength);
    }
    return uv;
}

