}

vec2 RayMarch (vec3 ro, vec3 rd) 
 {
 vec2 h, t=vec2( 0.);
 for (int i=0; i<100; i++) 
  {
 h = GetDist(ro + t.x * rd);
 if(h.x<SURF_DIST||t.x>MAX_DIST) break;
  t.x+=h.x;t.y=h.y;
 }
 if(t.x>100.) t.x=0.;
 return t;
}

