// Compute a normal from a vec3.
vec3 get_normal(vec3 p) 
{
    vec2 eps = vec2(0.1,0.);
    return normalize(
             vec3(
                  SDF(p+eps.xyy).x - SDF(p-eps.xyy).x, // Diff in X
                  SDF(p+eps.yxy).x - SDF(p-eps.yxy).x, // Diff in Y
                  SDF(p+eps.yyx).x - SDF(p-eps.yyx).x  // Diff in Z
             )
            );  // Math Vector
  }

