float diffuse_directional(vec3 n,vec3 l){

    float a =  max(0.,dot(n,normalize(l))); // realistic lighting
    float b =  dot(n,normalize(l))*.5+.5;   // less realistic lighting
    return (b+a)/2. ; 
}

// Main function
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

  // Initialization of the Ray Marching algorithm
  vec3 ray_origin = vec3(.01,.01,-8); // Were we start 

  vec3 ray_direction = normalize(vec3(uv,1.)); // going forward

  ray_direction.xz *=r(cos(iTime*.5)*.1);
  vec3 position = ray_origin ; 

  vec3 color = vec3(.0,.0,.0); // By default, everything will be black

  bool hit = false; 

  float shad = 0.;
  float part = 0.;


  for(float i=0.; i<ITER; ++i) { // We iterate the ray

    vec2 _distance = SDF(position); // Find the closest distance from the current position
    if(_distance.x <0.0001) { // If we are close enough, it's a hit, we put the shad and break the loop
        shad = i / ITER;
        hit = true;
        break;
     }
     part = _distance.y;
     position += _distance.x * ray_direction; // Otherwise we conditnue the marching, updating the ray position

  }

  if(hit) { // If there is a hit we want to perfom some coloring

    // I don't really understand here, just some lighting effect to get a good rendering
    vec3 n = get_normal(position);  
    vec3 l = vec3(0.01+sin(iTime)*10.,-1.5+cos(iTime)*10.,-5.);

     color =  vec3(diffuse_directional(n,l)); // light diffuse


      // Using the mix method toget some color
      if(part == 0.){
            vec3 color1 = mix(vec3(.1,0.1,0.1), vec3(0.0,0.9,0.2), color)*(1.-shad); 
             vec3 color2 = mix(vec3(.7,0.7,0.), vec3(0.0,0.5,0.6), color)*(1.6-shad); 
             color = mix(color1,color2,smoothstep(-0.5,0.9,sin(-iTime+length(uv)-2.*10.)));
     } else {
        color = mix(vec3(.9,0.9,0.9), vec3(0.9,0.0,0.2)+vec3(0.,0.,length(uv)), color)*(1.-shad); 
     }
  } 

  fragColor = vec4(color,1.);
}

