#define st (iTime*0.5)

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.4,65.14)))*1245.4);}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y>p.x) p = p.yx;
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z))) + length(max(q,0.));
}

float cyl (vec2 p, float r)
{return length(p)-r;}

float sc (vec3 p, float d)
{
    p = abs(p);
    p = max(p.xyz, p.yzx);
    return min(p.x,min(p.y,p.z))-d;
}

float bid;
float g1 = 0.;
float SDF (vec3 p)
{
    float per = 7.;
    bid = floor(p.z/per);
    p.z = mod(p.z, per)-per*0.5;
    float anim = (mod(bid,2.)==0.) ? bid+st : bid-st; 
    p.xy *= rot(anim);
    p.xz *= rot(PI/2.);
    mo(p.yz, vec2(2.));
    mo(p.xz, vec2(1.));
    p.x -= sin(iTime)*0.6;
    float d = max( 
        	abs(
        	max(
                -max(-sc(p, .3), box(p, vec3(1.))),
            	box(p,vec3(1.5,1.5,0.5))
            	)
    		   )
        	-0.04,
        	abs(p.z)-0.4
        		);
    g1 += 0.01/(0.01+d*d);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dith = hash21(uv);

    vec3 ro = vec3(0.001,0.001,-7.+iTime),
        p=ro,
    	rd = normalize (vec3(uv,1.)),
        col = vec3(0.);

    float shad =0.;
    bool hit = false;

    for (float i=0.;i<ITER;i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        d *= 0.8+dith*0.15;
        p += d*rd;
    }

    if (hit)
    {
        float glow = (mod(bid,2.) == 0.) ? g1*0.2 : shad*0.5;
        col = vec3(0.8,0.3,0.1)*glow;

    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float sdBox(vec3 p, vec3 s) {
    return length(max(abs(p) - s, 0.));
}

float sdTorus(vec3 p, vec2 r) {
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y)) - r.y;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ab = b - a;
    vec3 ap = p - a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t * ab;
    float d = length(p-c) - r;

    return d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5* (b-a/k), 0., 1.);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float GetDist(vec3 p) {
    vec4 s = vec4(0, 1, 6, 1.5);

    float s1 = length(p - vec3(0, 1, 6) + (vec3(sin(iTime), 0, cos(iTime)) * 1.5)) - .5;
    float s2 = length(p - vec3(0, 1, 6) + (vec3(sin(-iTime * 1.7), 0, cos(-iTime * 1.7)) * .5)) - .5;
    float planeDist = p.y;

    //float cd = sdCapsule(p, vec3(0, 1, 6), vec3(1, 2, 6), .2);
    float td = sdTorus(p - vec3(0, 1, 6), vec2(1.5, .25));

    vec3 bp = p - vec3(-1, 1, 6);
    bp.xz *= Rot(iTime);
    float bd = sdBox(bp, vec3(1));

    float d = smin(s1, s2, .7);
    //d = min(d, planeDist);
    d = smin(d, td, .7);
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if (dO > MAX_DIST || dS < SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);
    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));
    return normalize(n);

}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(3, 5, 3.);
    //lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n * SURF_DIST * 10., l);
    if (d < length(lightPos - p)) dif *= .4;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) /iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y - .6, 1.0));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;

    float dif = GetLight(p);

    col = vec3(dif);

    fragColor = vec4(col,1.0);
}

