////////////////////////////////////////////////////////////////////////////////
//
// Riemann zeta function using reflection.
// Original by Justaway: https://www.shadertoy.com/view/4lcBDs
// Use log gamma function to avoid overflow
//
// Method: http://numbers.computation.free.fr/Constants/Miscellaneous/zetaevaluations.pdf
//
// Also optimize log sin and log cos:
// https://uk.mathworks.com/matlabcentral/fileexchange/32947-logarithm-of-complex-sine-and-cosine-avoiding-overflow
//
// Grid lines at Re(z) = 10n, Im(z) = 0.5 (the critical line)
//
// Seems accurate up to about Re(z) = 50 or so, then the reflection starts
// to be misaligned and the purported roots come away from the critical line.
//
// <mouse> to move
// g: show grid
// r: don't do the reflection
// x: show log gamma instead of zeta
// z: zoomed view
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.141592653589793;
const float LOGPI = 1.1447298858494002;
const float LOG2PI = 1.8378770664093453;
const float LOG2 = 0.6931471805599453;

vec2 cmul(vec2 z1, vec2 z2) {
  return mat2(z1.x,z1.y,-z1.y,z1.x)*z2;
}

vec2 cinv(vec2 z) {
  return vec2(z.x,-z.y)/dot(z,z);
}

vec2 cdiv(vec2 z1, vec2 z2) {
  return cmul(z1,cinv(z2));
}

vec2 cexp(vec2 z) {
  // If cos and sin were perfect we wouldn't need normalize
  return exp(z.x)*normalize(vec2(cos(z.y),sin(z.y)));
}

vec2 clog(vec2 z) {
  float r2 = dot(z,z);
  return vec2(0.5*log(r2),atan(z.y,z.x));
}

vec2 cpow(vec2 z1, vec2 z2) {
  return cexp(cmul(z2,clog(z1)));
}

vec2 cpow(int n, vec2 z2) {
  return cpow(vec2(n,0),z2);
}

vec2 cpow(float x, vec2 z2) {
  return cpow(vec2(x,0),z2);
}

vec2 csin(vec2 z) {
    return cmul(vec2(0,0.5),cexp(cmul(vec2(0,-1),z))-cexp(cmul(vec2(0,1),z)));
}

vec2 ccos(vec2 z){
  z += vec2(0.5*PI,0);
  return cmul(vec2(0,0.5),cexp(cmul(vec2(0,-1),z))-cexp(cmul(vec2(0,1),z)));
}

// Approximations to log(sin(z)) and log(cos(z)) from David Young:
// https://uk.mathworks.com/matlabcentral/fileexchange/32947-logarithm-of-complex-sine-and-cosine-avoiding-overflow
vec2 clogsin(vec2 z) {
  float x = z.x, y = z.y;
  const float ylimit = 8.0; // Approx. -log(eps)/2.0, 32 bit eps = 6e-8 
  //z(posy) = y(posy) - 0.693147180559945 + 1i * (mod(1.5*pi-x(posy), 2*pi) - pi);
  //z(negy) = -y(negy) - 0.693147180559945 + 1i * (mod(0.5*pi+x(negy), 2*pi) - pi);
  //z(normal) = log(sin(z(normal)));
  if (y > ylimit) return vec2(y - LOG2, mod(1.5*PI-x, 2.0*PI) - PI);
  else if (y < -ylimit) return vec2(-y - LOG2, mod(0.5*PI+x, 2.0*PI) - PI);
  else return clog(csin(z));
}

vec2 clogcos(vec2 z) {
  float x = z.x, y = z.y;
  const float ylimit = 8.0; // Approx. -log(eps)/2.0, 32 bit eps = 6e-8 
  //z(posy) = y(posy) - 0.693147180559945 + 1i * (mod(pi-x(posy), 2*pi) - pi);
  //z(negy) = -y(negy) - 0.693147180559945 + 1i * (mod(pi+x(negy), 2*pi) - pi);
  //z(normal) = log(cos(z(normal)));
  if (y > ylimit) return vec2(y - LOG2, mod(PI-x, 2.0*PI) - PI);
  else if (y < -ylimit) return vec2(-y - LOG2, mod(PI+x, 2.0*PI) - PI);
  else return clog(ccos(z));
}

// Lanczos approximation for Gamma function
// See Numerical Recipes
const float[8] p = float[](
  676.5203681218851,
  -1259.1392167224028,
  771.32342877765313,
  -176.61502916214059,
  12.507343278686905,
  -0.13857109526572012,
  9.9843695780195716e-6,
  1.5056327351493116e-7
);

vec2 cloggammaR(vec2 z) {
  z -= vec2(1,0);
  vec2 x = vec2(-1.9007e-13,0); // 0.99999999999980993 - 1
  vec2 pval;
  int N = 8;
  // Add smallest first for hopefully increased accuracy.
  for (int i = N-1; i >= 0; i--) {
    x += p[i]*cinv(z+vec2(float(i+1),0));
  }
  x += vec2(1,0);
  vec2 t = z + vec2(float(N)-0.5,0);
  // Original non-log function
  //return cmul(cmul(cmul(vec2(sqrt(2.0*PI),0.0),cpow(t,z+vec2(0.5,0.0))),cexp(-t)),x);
  vec2 a = vec2(0.5*LOG2PI,0);
  vec2 b = cmul(clog(t),z+vec2(0.5,0));
  vec2 c = -t;
  vec2 y = a+b+c+clog(x);
  return y;
}

vec2 cloggammaL(vec2 z) {
  vec2 a = vec2(LOGPI,0);
  vec2 b = clogsin(PI*z);
  vec2 c = cloggammaR(vec2(1,0)-z);
  vec2 y = a-b-c;
  return y;
}

vec2 cloggamma(vec2 z) {
  if(z.x > 0.0) return cloggammaR(z);
  else return cloggammaL(z);
}

vec2 zeta(vec2 s) {
  const int N = 20;
  const float[N] ek = float[N](
    1048575.000000,1048555.000000,1048365.000000,
    1047225.000000,1042380.000000,1026876.000000,
    988116.000000,910596.000000,784626.000000,
    616666.000000,431910.000000,263950.000000,
    137980.000000,60460.000000,21700.001953,
    6196.000488,1351.000122,211.000000,21.000000,1.000000);

  vec2 sum1 = vec2(0);
  float a = 1.0;
  for(int i = 1; i <= N; i++) {
    sum1 += a*cinv(cpow(float(i), s));
    a = -a;
  }
  vec2 sum2 = vec2(0);
  a = 1.0;
  for(int i = N + 1; i <= 2 * N; i++) {
    sum2 += a*ek[i - N - 1]*cinv(cpow(float(i),s));
    a = -a;
  }
  sum2 = sum2/1048576.0; // 2**N
  return cdiv(sum1 + sum2, vec2(1,0) - cpow(2, vec2(1,0) - s));
}

vec3 gradient(float theta) {
    vec3 color = vec3(0);
    color += vec3(1,0,0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((2.0 * PI-theta) * PI / 3.0), 0.0, 1.0));
    color += vec3(1,0,0) * smoothstep(0.0, 1.0, clamp(1.0 - abs(-theta * PI / 3.0), 0.0, 1.0));
    color += vec3(1,1,0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec3(0,1,0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 2.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec3(0,1,1) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI - theta) * PI / 3.0), 0.0, 1.0));
    color += vec3(0,0,1) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 4.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec3(1,0,1) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 5.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    return color*(fract(theta / PI * 10.0) / 2.0 + 0.5);
}

vec3 plotDomain(vec2 z) {
    float theta = mod(atan(z.y, z.x), 2.0 * PI);
    return gradient(theta) * (fract(log2(length(z))) / 2.0 + 0.5);
}

vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1), rgb, c.y);
}

bool keypress(int key) {
    return texelFetch(iChannel0, ivec2(key,2),0).x != 0.0;
}

const int CHAR_G = 71;
const int CHAR_R = 82;
const int CHAR_X = 88;
const int CHAR_Z = 90;

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
    vec2 offset = vec2(3.0*iTime,0);
    if (iMouse.x > 0.0) {
      offset = -(2.0*iMouse.xy - iResolution.xy)/iResolution.x*100.0;
    }
    float zoom = 10.0;
    if (keypress(CHAR_Z)) zoom = 2.0;
    vec2 z = (zoom*uv + offset).yx; // Scale and shift
    vec2 z0 = z; // Starting value of z
    if (keypress(CHAR_X)) {
        z = cloggamma(z);
    } else if (keypress(CHAR_R) || z.x > 0.0){
      z = zeta(z);
    } else {
      // Reflection
      vec2 temp = zeta(vec2(1,0)-z);
      temp = clog(temp);
      temp -= cloggamma(z)+vec2(LOG2,0);
      temp += LOG2PI*z;
      temp -= clogcos(0.5*PI*z);
      temp = cexp(temp);
      z = temp;
    }
    vec3 color = plotDomain(z);
    //color = hsv2rgb(vec3(atan(z.y,z.x)/(2.0*PI),1,1));
    //float k = log(length(z));
    //k = max(k,1.0/k);
    //color = hsv2rgb(vec3(k,1,1));
    if (keypress(CHAR_G)) {
      color = mix(vec3(0),color, smoothstep(0.0,0.03,abs(z0.x-0.5)));
      color = mix(vec3(0),color, smoothstep(0.0,0.03,abs(z0.y-10.0*round(z0.y/10.0))));
    }
    fragColor = vec4(color,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy) /iResolution.y;
    float t = iTime*3.;


    float circle = length(uv) - 0.23;
    float rect =  max(abs(uv.x),abs(uv.y)) - 0.1;

    //interpolate weight (0,1) 0: circle, 1: rect
    float w = -sin(t+sin(t+sin(t)))*0.5+0.5; 

    float f = circle*(1.-w)+ w*rect; //linear lerp


    f = smoothstep(0.0,-0.01,f) - smoothstep(-0.01,-0.02,f);    
    vec3 col = vec3(f);
    fragColor = vec4(col,1.0);
}

vec3 black_hole_pos = vec3(0.0,0,1.5); 		//黑洞的位置
float event_horizon_radius = 0.3;		//黑洞的事件视界半径
float HitTest(vec3 p){
	return length(p) - event_horizon_radius;
}
vec3 GetBg(vec3 p)
{
    return smoothstep(vec3(0),vec3(1.),fract(p*1.));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //映射到0~1之间
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.  - 1.;	
    uv.x *= iResolution.x / iResolution.y;	
	vec3 eye = vec3(0.,0.,-2);    //eye or camera postion 相机位置
    vec3 sd = vec3(uv.x,uv.y,-1); //screen coord 屏幕坐标
    vec3 ray_dir = normalize( sd - eye);//ray direction 射线方向

    vec3 col = vec3(0.);

	float hitbh = 0.;

    const int maxStep = 400;//光线最大步进数
    float st = 0.;      
    vec3 p = sd;
    vec3 v = ray_dir;
    float dt = 0.01;
    float GM = 0.8;   
    vec3 cp = black_hole_pos + 2.*vec3(1.5*sin(iTime),sin(1.31*iTime),0.);
    for(int i = 0;i<maxStep;++i)
    {
       	//F = G * M * m / r^2;
    	//a = F/m
    	//v = v + a * dt;
    	//p = p + v * dt;
        p += v * dt;
        vec3 relP = p - cp; //黑洞相对原点的位置       
        float r2 = dot(relP,relP);
        vec3 a = GM/r2 * normalize(-relP); //加速度的方向朝向黑洞，为-relP
        v += a * dt;        
        float hit = HitTest(relP); //hit表示距物体的最小距离
		hitbh = step(hit,0.);   
        if(hitbh > 0.5)break;
    }
    col = GetBg(p)*(1.-hitbh);
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
#define amplitude .5
#define frequency 2.
#define speed 2.

vec3 DrawAxis(vec2 uv){
    vec3 axis = vec3(0);   

    vec2 uvs = fract(uv * 10. * 5.);    
    vec2 absUv = abs(uvs);
    if (absUv.x < .1 || absUv.x > .9) axis = vec3(.1);
    if (absUv.y < .1 || absUv.y > .9) axis = vec3(.1);

    vec2 uvm = fract(uv * 5.);
    absUv = abs(uvm);
    if (absUv.x < .01 || absUv.x > .99) axis = vec3(.5);
    if (absUv.y < .01 || absUv.y > .99) axis = vec3(.5);

    if (abs(uv.x) < .005) axis = vec3(1.);
    if (abs(uv.y) < .005) axis = vec3(1.);

    return axis;
}

float Line(float n, float pw, float a){
    return smoothstep(n - pw/iResolution.x, n, a) - smoothstep(n, n  + pw/iResolution.x, a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord/iResolution.xy) - .5;
    uv.x *= iResolution.x/iResolution.y;    

    vec3 col = vec3(.8,.95,1.);
    col += DrawAxis(uv);
    col *= .5;

    float y = uv.x;  

    float sine = sin(y * PI * frequency + iTime * speed) * amplitude;
    float sinCurve = Line(sine, 5., uv.y);

    float cosine = cos(y * PI * frequency + iTime * speed) * amplitude;
    float cosCurve = Line(cosine, 5., uv.y );

    col += vec3(sinCurve * .2, sinCurve * .35, sinCurve * .5);
    col += vec3(cosCurve * .5, cosCurve * .15, cosCurve * .2); 


    fragColor = vec4(col,1.0);
}

// /!\ move everything fast and flashy #define time tan(iTime*16.)
