vec2 id;

float sdIso(vec3 p, vec2 id){
	float d = 10e6;
    //p.z -= 0.;
    vec3 q = p;

    // ME
    p.x -= id.y*xOffs;
    p.y += id.y*yOffs;
    p.xz = pmod(p.xz, modDist);
    p.xy *= rot(pi*0.25);
    d = min(d, sdMain(p, id));

    vec2 idD = id;

    // BOTTOM
    p = q;
    idD.y += 1.;
    p.x -= idD.y*xOffs;
    p.y += idD.y*yOffs;
    p.xz = pmod(p.xz + vec2(0,0. - id.y), vec2(modDist.x,modDist.y*3.));

    if (p.x > 0.){
        idD.x -= 1.;
    }
    p.xy *= rot(pi*0.25);
    d = min(d, sdMain(p, idD));


    // RIGHT
    p = q;
    idD = id;
    idD.x -= 1.;
    p.x -= idD.y*xOffs;
    p.y += idD.y*yOffs;
    p.xz = pmod(p.xz + vec2(modDist.x*1.- idD.x*modDist.x*1.,0), vec2(modDist.x*3.,modDist.y));
    p.xy *= rot(pi*.25);
    d = min(d, sdMain(p, idD));

    // LEFT
    p = q;
    idD = id;
    idD.x += 1.;
    p.x -= idD.y*xOffs;
    p.y += idD.y*yOffs;
    p.xz = pmod(p.xz + vec2(modDist.x*1.- idD.x*modDist.x*1.,0), vec2(modDist.x*3.,modDist.y));
    p.xy *= rot(pi*.25);
    d = min(d, sdMain(p, idD));

    // TOP
    idD = id;
    idD.y -= 1.;
    p = q;
    p.x -= idD.y*xOffs;
    p.y += idD.y*yOffs;
    p.xz = pmod(p.xz + vec2(0.,-1. - id.y), vec2(modDist.x,modDist.y*3.));
    if (p.x < 0.){
      idD.x += 1.;
    }
    p.xy *= rot(pi*0.25);
    d = min(d, sdMain(p, idD));


	return d;
}

vec2 map(vec3 p){
	vec2 d = vec2(10e6);
    p.x += 0.5;
    id = floor(p.xz/modDist);
    id.x = floor((p.x - modDist.x*0.5*id.y)/modDist.x);

    d.x = min(d.x, sdIso(p, id));

    d.x *= 0.55;
    return d;
}

