    return max(c, p.x);
}

vec4 hex2d(vec2 uv)
{
    vec2 r = vec2(1., 1.73);
    vec2 h = r * 0.5;
    vec2 a = mod(uv, r) - h;
    vec2 b = mod(uv - r * 0.5, r) - h;
    vec2 gv = length(a) < length(b) ? a : b;

    vec2 id = gv - uv;

    float x = atan(gv.x, gv.y);
    float y = 0.5 - hexDist(gv);

    return vec4(x, y, id);
}

