mat3 camera(vec3 ro, vec3 ta, float cr )
{
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    timeline(mod(iTime,17.0));

    vec2 uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x,iResolution.y);


    mat3 c = camera(ro, target, 0.0);
    vec3 rd = c * normalize(vec3(uv, fov));


    vec3 lightPos = target;
    vec3 lightPos2 = target;
    vec3 lightPos3 = target;


    vec3 an = vec3(0.0,ly,0.0);
    an.xy *= rot(iTime*2.0);

    lightPos2 += an;
    lightPos3 -= an;


    vec3 col = vec3(0.0);
    vec3 rPos = ro;
    float depth = 0.0;
    float mat;
    float l2min = 99.0;
    float l3min = 99.0;

    for(int i = 0 ; i < 225;i++){
        mat = map(rPos);

        if(mat < 0.001){
            col = vec3(1.0-float(i)/250.);
            break;
        }

        if(depth > 500.0){
            depth = 400.0;
            break;
        }

        //https://qiita.com/ukeyshima/items/221b0384d39f521cad8f
        depth += min(min((step(0.0,rd.x)-fract(rPos.x))/rd.x, (step(0.0,rd.z)-fract(rPos.z))/rd.z)+0.01,mat);
        rPos = ro + rd * depth;

        l2min = min(l2min,length(lightPos2 - rPos)/3.5);
        l3min = min(l3min,length(lightPos3 - rPos)/3.5);

    }

    l2min *= step(0.5,l2min);
    l3min *= step(0.5,l3min);

    vec4 backcol = tracewall(ro,rd);

    vec3 n = getNormal(rPos);
    vec3 r = normalize(reflect(rd,n));

    ///light1
    vec3 lv = lightPos - rPos;
    vec3 ld = normalize(lv);
    //ld = normalize(vec3(0.2,0.6,0.3));

    float a = max(1.0-(length(lv)/20.5),0.0)*1.5;
    float d = max(dot(ld,n),0.);
    float s = pow(max(dot(ld,r),0.), 30.0) * a;

    ///light2
    lv = lightPos2 - rPos;
    ld = normalize(lv);
    //ld = normalize(vec3(0.2,0.6,0.3));

    float a2 = max(1.0-(length(lv)/25.5),0.0)*3.5;
    float d2 = max(dot(ld,n),0.);
    float s2 = pow(max(dot(ld,r),0.), 30.0) * a;

    vec3 lc2 = vec3(a2+d2+s2)*vec3(1.0,0.0,0.0);


    ///light3
    lv = lightPos3 - rPos;
    ld = normalize(lv);

    float a3 = max(1.0-(length(lv)/25.5),0.0)*1.5;
    float d3 = max(dot(ld,n),0.);
    float s3 = pow(max(dot(ld,r),0.), 30.0) * a;

    vec3 lc3 = vec3(a3+d3+s3)*vec3(0.0,1.0,0.0);



    float _Threshold = 0.1;
    vec3 glow = max(abs(dot(rd, n)-_Threshold), 0.0)*vec3(0.1,0.5,0.6);
    vec3 grid = vec3(step(2.9,mod(rPos,3.0)));
    float k = 10.;
    vec3 gridd = max(grid.x,max(grid.y,grid.z))*vec3(0.1,0.5,0.8);

    col = vec3(d+s+a)+lc2+lc3;

    col *= glow;
    gridd *= exp(k*sin(length(rPos)))/exp(k);
    col += 5.0*gridd;

    depth *= 10.0;

    vec3 bgc = vec3(0.0,0.3,0.3);
    //bgc = vec3(1.0);
    if(depth > 1000.){
        col = vec3(bgc);
    }


    //https://qiita.com/aa_debdeb/items/b78975c5bcb063e28a08
    float f = exp2Fog(backcol.a, 0.0014);
    //backcol.rgb = mix(vec3(0.2,0.5,0.8), backcol.rgb, f);
    backcol.rgb = mix(bgc, backcol.rgb, f);


    if(depth > backcol.a){
        col = backcol.rgb;
    }
    //  col = backcol.rgb;
    col += max((1.0 - l2min)*vec3(1.0,0.0,0.0),0.0);
    col += max((1.0 - l3min)*vec3(0.0,0.0,1.0),0.0);


    //ネガポジ反転//
    //col = vec3(1.0) - col;


    // zoom line  http://sayachang-bot.hateblo.jp/entry/2019/02/09/200303    
    float t = length(uv)*0.07 - 0.05;

    float rr = length(uv);
    rr = 0.8 * rr - 0.8;

    a = atan2(uv.y, uv.x);
    a = abs(cos(70. * a) + sin(30. * a));

    d = a - rr;
    float nn = smoothstep(0.1, 0.4, clamp(d, 0.0, 1.0));

    col += max((.5 - nn)*t*concentrationline,0.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}

