float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;

    d=999.9;
    float planeDist = p.y;

   vec3 pp;
    pp=p;



    vec2 res;
    res = vec2(9999, 0);



     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          { res=vec2(res.x,8); }
        else
         { 
            //res=vec2(res.x, 9);

             res=vec2(res.x, 9);

         } 
     }



    p.x = mod(p.x , 12.0);
    p.z = mod(p.z , 11.0)+0.5;

    float  sd1= sdBox(  p-vec3(0,1.1,0.0), vec3(9.0,9.,7.0) );
    float  sd2= sdBox(  p-vec3(0,0.5,0.0), vec3(12.0,9.5,12.0) );
    float  sd3= sdBox(  p-vec3(0,0.0,0.0), vec3(12.0,0.5,12.0) );


    float  d1= differenceSDF(sd2, sd1);
    res= opU(res, vec2(d1,14));
    res= opU(res, vec2(sd3,13));
    res= opU(res, vec2(1.0,8));



    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd)
{
	float dO=0.2;
    //Determines size of shadow

    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);

        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) 
        {
            //mObj.dist=dO; 	
            gres2 = vec2(dO,dS); 
            break;
    	}    




    }

    return dO;
}




vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}



