mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

float mycross(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *=iResolution.x/iResolution.y;
    vec3 color = vec3(0.0);

    if(AA==0) {
        // To move the cross we move the space
    	vec2 translate = vec2(cos(iTime),sin(iTime));
    	st += translate*0.25;

    	// Show the coordinates of the space on the background
    	color = vec3(st.x,st.y,.3);

    	// Add the shape on the foreground
    	color += vec3(mycross(st,0.3));
    }
    if(AA==1) {
        // move space from the center to the vec2(0.0)
    	st -= vec2(0.5);
    	// rotate the space
    	st = rotate2d( sin(iTime)*PI ) * st;
    	// move it back to the original place
    	st += vec2(0.5);

    	// Show the coordinates of the space on the background
    	// color = vec3(st.x,st.y,0.0);

    	// Add the shape on the foreground
    	color += vec3(mycross(st,0.4));
    }
    if(AA==2) {
        st -= vec2(0.5);
        st = scale( vec2(sin(iTime)+1.0) ) * st;
        st += vec2(0.5);

        // Show the coordinates of the space on the background
        color = vec3(st.x,st.y,0.0);

        // Add the shape on the foreground
        color += vec3(mycross(st,0.2));
    }

    if(AA==3) {
        // UV values goes from -1 to 1
    	// So we need to remap st (0.0 to 1.0)
    	st -= 0.5;  // becomes -0.5 to 0.5
    	st *= 2.0;  // becomes -1.0 to 1.0

    	// we pass st as the y & z values of
    	// a three dimensional vector to be
    	// properly multiply by a 3x3 matrix
    	color = yuv2rgb * vec3(0.6, st.x, st.y);
    }

    if(AA==4) {
        // UV values goes from -1 to 1
    	// So we need to remap st (0.0 to 1.0)
    	st -= 0.5;  // becomes -0.5 to 0.5
    	st *= 2.0;  // becomes -1.0 to 1.0

        st *= rotate2d(sin(iTime)*PI/3.);

    	// we pass st as the y & z values of
    	// a three dimensional vector to be
    	// properly multiply by a 3x3 matrix
    	color = yuv2rgb * vec3(0.6, st.x, st.y);
    }
    fragColor = vec4(color,1.0);
}

