//Anti-Aliasing level. Use 2, 3 or 4 for better quality.
#define AA 1.

vec3 dir = normalize(vec3(-4,1,3));

float soft(float a, float b, float n)
{
 	return log(exp(a*n)+exp(b*n))/n;   
}
vec3 hash(vec3 p)
{
 	return fract(sin(p*mat3(45.32,32.42,-41.55,65.35,-43.42,51.55,45.32,29.82,-41.45))*vec3(142.83,253.36,188.64));
}
vec3 value(vec3 p)
{
    vec3 f = floor(p);
    vec3 s = p-f;
    s *= s*(3.-s-s);
    const vec2 o = vec2(0,1);

    return mix(mix(mix(hash(f+o.xxx),hash(f+o.yxx),s.x),
        		   mix(hash(f+o.xyx),hash(f+o.yyx),s.x),s.y),
               mix(mix(hash(f+o.xxy),hash(f+o.yxy),s.x),
                   mix(hash(f+o.xyy),hash(f+o.yyy),s.x),s.y),s.z);
}
float worley(vec3 p)
{
    float d = 1.;
    for(int x = -1;x<=1;x++)
    for(int y = -1;y<=1;y++)
    for(int z = -1;z<=1;z++)
    {
    	vec3 f = floor(p+vec3(x,y,z));
        vec3 v = p-f-hash(f);
        d = soft(d,dot(v,v),-6.);
    }
    return d;
}
float seed(vec3 p)
{
    float d = 1.;
    for(int x = -1;x<=1;x++)
    for(int y = -1;y<=1;y++)
    {
    	vec3 f = floor(vec3(p.xy,0)+vec3(x,y,0));
        vec3 h = hash(f)*vec3(1,1,63);
        vec3 v = mat3(cos(h.z),sin(h.z),0,sin(h.z),-cos(h.z),0,0,0,1)*(p-f-h*.9)*vec3(1.7,1,0);
        d = min(d,dot(v,v)+step(9.,length(f+.6))+step(p.z,2.));
    }
    return max(.05-d,0.);
}
float cheese(vec3 p)
{
    p.z += -.27+.03*p.x*p.x+.1*soft(dot(p.xy,p.xy)-3.5,0.,10.);
 	return (length(max(abs(p)-vec3(1.6,1.6,0),0.))-.02)*.8;
}
float model(vec3 p)
{
    float d = length(p)-2.5;
    float m = soft(length(p.xy)-3.,pow(p.z-soft(d,0.,20.)*.7+1.1,2.)-.01,10.);
    m = min(m,soft(p.z+1.2,-abs(mod(p.y+3.,4.)-2.),12.));
    if (d<.1)
    {
    	vec3 c = vec3(p.xy,max(p.z-.35,0.)*1.6);
    	float b = soft(length(c+.05*sin(c.yzx*2.))*.6-1.15,.41-abs(p.z+.15)-.02*c.x*c.x,40.);
        m = min(m,soft(b,-1.-p.z,20.));
    	m = min(m,soft(length(p.xy+.1*sin(c.yx*2.))-2.1,pow(p.z-.03+.03*p.x*p.x,2.)-.04,12.));
    	m = min(m,soft(length(p)-1.9,abs(p.z+.4-.03*p.y*p.y)-.1,80.));
    	m = min(m,cheese(p));
        vec3 r = value(p/dot(p,p)*vec3(14,14,1))-.5;
        vec3 l = p+vec3(0,0,.46)+vec3(0,0,length(p.xy)-1.8)*.3*cos(r.x*5.-r.y*5.);
    	m = min(m,soft(length(l)-2.1-.4*r.z,abs(l.z)-.02,28.)*.8);

        float s = .2*seed(p*5.);
        return m-s;
    }
	return min(d,m);
}
float bump(vec3 p)
{
    float b = .007*max(1.-1.5*abs(p.z),0.)*worley(p*18.);
          b += .001*(worley(p*30.)+.5*worley(p*70.))*step(-.99,p.z-max(length(p.xy)-2.,0.));
 	return model(p)+b*step(.01,cheese(p));
}
vec3 normal(vec3 p)
{
	vec3 n = vec3(-1,1,0)*.001;
    return normalize(bump(p+n.xyy)*n.xyy+bump(p+n.yxy)*n.yxy+bump(p+n.yyx)*n.yyx+bump(p+n.xxx)*n.xxx);
}

float light(vec3 p,vec3 n)
{
    float d = pow(2.-1.*dot(n,dir),-1.);
    d /= (1.-min(dot(dir,p),0.)*exp(.6-length(cross(dir,p))));

    float a = .5+.5*model(p+n*.05)/.05;
    a *= .6+.4*model(p+n*.1)/.1;
    a *= .7+.3*model(p+n*.2)/.2;
    return  d*a;
}
vec3 Tex(vec3 p,vec3 r)
{
    vec3 Rad = value(p/dot(p,p)*vec3(14,14,.2))-.5;
    vec3 l = p+vec3(0,0,.46)+vec3(0,0,length(p.xy)-1.8)*.3*cos(Rad.x*5.-Rad.y*5.);

    float t = max(length(p)-1.9,abs(p.z+.4-.03*p.y*p.y)-.1);

    vec3 n = normal(p);
    float w = worley(p*11.)*(.05+.95*smoothstep(.7,.4,abs(p.z+.04)))*(abs(n.z)*.7+.3);

 	float m = abs(p.z-.03+.03*p.x*p.x)-.3;
    float d = light(p,n);
    float s = max(dot(reflect(r,n),dir),0.);
    float f = length(p.xy);

    vec3 b = vec3(1,.635,.32)+.8*smoothstep(.9,.4,abs(p.z+.1)+.4*w)+.4*s*s+vec3(.7+.3*s*s*s)*seed(p*5.)/.1;
    vec3 c = mix(b,vec3(.7,.4,.2)-.6*w+.6*s*s*s,step(m,-.08));
    c = mix(c,vec3(.5,.8,.2)-.4*w+.6*s,step(abs(l.z),.04));
    c = mix(c,vec3(1,.6,.1)+.6*s*s,step(cheese(p),.002));
    c = mix(c,vec3(1,.2,.1)+s*s,step(t,.001));
    c = mix(c,vec3(1.5+.3*smoothstep(.03,.04,abs(f-2.6)))+s*s*s*s,step(p.z-max(f-2.,0.),-.99));
    c = mix(c,pow(texture(iChannel0,p.xy/3.).rgb,vec3(1,.9,1.2))*.7+.1,step(p.z,-1.18));

    return c*d;
}
void mainImage( out vec4 Color, in vec2 Coord)
{
    vec2 a = vec2(iTime*.1,.2*cos(iTime*.1)+1.8);
    vec3 x = vec3(cos(a.x)*sin(a.y),sin(a.x)*sin(a.y),cos(a.y)),
         y = normalize(cross(x,vec3(0,0,-1))),
    	 z = normalize(cross(x,y));

    mat3 m = mat3(x,y,z);

    vec3 c = vec3(0);

    for(float i = 0.;i<AA;i++)
    for(float j = 0.;j<AA;j++)
    {
        vec4 p = vec4(m*vec3(-6,0,2.*cos(a.y)+.2),0);
        vec3 r = m*vec3(1,(vec2(i,j)/AA+Coord-.5*iResolution.xy)/iResolution.y);
        for(int I = 0;I<800;I++)
        {
            float s = model(p.xyz);
            p += vec4(r,1)*s;
            if ((p.w>20.) || (s<.001)) break;
        }
    	c += mix(Tex(p.xyz,r),vec3(.9)+.1*dot(dir,r),smoothstep(.5,1.,length(p.xyz)/10.));
    }
    Color = vec4(c/AA/AA,1);
}

int mat = 0;
float epsi = 0.001;

float random(vec2 p){

    vec3 p3  = fract(vec3(p.xyy) * .10031);
    p3 += dot(p3, p3.yyx + 33.33);
    return fract((p3.x + p3.y) );
}
float noise(vec2 uv){

    vec2 id = floor(uv*8.);
    vec2 lc = smoothstep(0.,1.,fract(uv*8.));
    float a = random(id);
    float b = random(id + vec2(1.,0.));
    float c = random(id + vec2(0.,1.));
    float d = random(id + vec2(1.,1.));

    float ud = mix(a,b,lc.x);
    float lr = mix(c,d,lc.x);
    float fin = mix(ud,lr,lc.y);
    return fin;

}

float octaves(vec2 uv,int octs){

    float amp = 0.5;
    float f = 0.;
    for(int i =1; i<octs+1;i++){
        f+=noise(uv)*amp;
        uv*=2.;
        amp*=0.5;
    }

    return f;
}
mat2 rot(float a){
    return mat2(sin(a),-cos(a),cos(a),sin(a));
}
float sdSphere(vec3 p){
    return length(p)-1.;
}
float sdRCyl( vec3 p, float ra, float rb, float h ){

  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );

  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float sdRing(vec3 p,float size,mat2 rotate){
    p.zy*=rotate;
    float or =  sdRCyl(p,size,0.,0.4);
    float ir =  sdRCyl(p,size+0.1,0.,0.3);
    return (max(-or,ir));
}
float sdRing2(vec3 p,float size,mat2 rotate){
    p.xy*=rotate;
    float or =  sdRCyl(p,size,0.,0.4);
    float ir =  sdRCyl(p,size+0.1,0.,0.3);
    return (max(-or,ir));
}

float map(vec3 p){

    float sphere = sdSphere(p);



    float ring = sdRing(p,0.85,rot(iTime));
    float ring2 = sdRing2(p,0.65,rot(iTime));
    float ring3 = sdRing(p,1.1,rot(-iTime));
     //p.y+=(sin(length(p.xz*0.9)-iTime*3.)*.5);
          float plane = p.y +3.5;

    float best = min(min(min(min(sphere,plane),ring),ring2),ring3);
    if(best == sphere){
        mat = 1;
    } else if(best == plane){
        mat = 2;
    } else if(best == ring || best == ring2||best == ring3 ) {
        mat = 3;
    }else{mat = 4;}
    return best;
}
vec3 normal(vec3 p){
    vec2 e = vec2(epsi,0);
    return normalize(vec3(map(p+e.xyy)-map(p-e.xyy),
                        map(p+e.yxy)-map(p-e.yxy),
                        map(p+e.yyx)-map(p-e.yyx)));
}

float tr(vec3 ro,vec3 rd){
    float tot = 0.;
    float dst = 0.;
    for(int i = 0; i< 180; i++){
        dst = map(ro+rd*tot);
        tot+=dst;
        if(dst <epsi||tot>180.)break;
    }

    if(dst >epsi){
        mat = 0;
    }
    return tot;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord.xy-0.5* iResolution.xy)/iResolution.y;
    vec3 ro = vec3(2,-1.5,-9);
    vec3 rd = normalize(vec3(uv,1));
    ro.zy*=rot(2.1);
    rd.zy*=rot(2.1);
    ro.zx*=rot(iTime);
    rd.zx*=rot(iTime);
    vec3 color = vec3(1);

    for(int i =0;i<7;i++){
        vec3 p = (ro+rd*tr(ro,rd));


        if(mat == 0){


            uv.x+=iTime/60.;
            uv.x*=0.7;
            color *= vec3(octaves(uv,9)+0.5);



        }if(mat == 1){
         vec3 n = normal(p);
           float fresnel = pow(1.-dot(- rd,n),5.);
            color*= fresnel;
               ro = p+1.;
                rd = reflect(rd,n);
        }
        if(mat == 2){
         vec3 n = normal(p);
            color*= vec3(0.25,0.3,0.3);
              ro = p+1.;
            rd = reflect(rd,n);
        }
        if(mat ==3){
         vec3 n = normal(p);
            color *= vec3(0.9,0.7,0.3);
               ro = p+1.;
        rd = reflect(rd,n);
        }

    }


    fragColor = vec4((color),1.0);
}

float PI = 3.14159;

vec2 PolarToUv(vec2 polar)
{
    return vec2(polar.y*cos(polar.x),polar.y*sin(polar.x));
}

vec2 UvToPolar(vec2 uv)
{
    return vec2(atan(uv.y,uv.x),length(uv));
}


vec2 Rotate(vec2 uv, vec2 origin, float deg)
{
    deg = -radians(deg);
    vec2 centered = uv-origin;
    return origin + vec2(centered.x*cos(deg)-centered.y*sin(deg),
                         centered.x*sin(deg)+centered.y*cos(deg));
}

float CircleSDF(vec2 uv, vec2 pos, float radius)
{
    return length(uv-pos)-radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

 	vec2 circlePos = vec2(0.3,0.1);
    uv/=1.2;
    uv = Rotate(uv,vec2(0),45.0*iTime);

    vec2 polar = UvToPolar(uv);
    polar.x = mod(polar.x,PI/5.0); 
    uv = PolarToUv(polar);
    float dist = floor(CircleSDF(uv,circlePos,0.3))+1.0;
  	int n = 0;
    while( n <2)
    {
        if(dist<=0.0)
        {
            uv = (uv-circlePos)*5.0;
            vec2 polar = UvToPolar(uv);
            polar.x = mod(polar.x,PI/3.0); 
            uv = PolarToUv(polar);
            dist = floor(CircleSDF(uv,vec2(0.3,0.1),0.2))+1.0;
        }
        n++;
    }


    vec3 col = vec3(0.0,dist,0.0);


    fragColor = vec4(col,1.0);
}

struct Ball {
	vec2 pos;
    vec2 speed;
    float s;
    vec4 color;
};

void move(inout Ball box, float fFrame) {
    float xPos = box.pos.x + box.speed.x * fFrame;
    box.pos.x = mod(xPos, iResolution.x);
    float tx = floor(mod(xPos / iResolution.x, 2.0));
    box.pos.x = ((1. - tx) * box.pos.x) + (tx * (iResolution.x - box.pos.x));

    float yPos = box.pos.y + box.speed.y * fFrame;
    box.pos.y = mod(yPos, iResolution.y);
    float ty = floor(mod(yPos / iResolution.y, 2.0));
    box.pos.y = ((1. - ty) * box.pos.y) + (ty * (iResolution.y - box.pos.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float fFrame = float(iFrame) / 1.;

    Ball[] balls = Ball[](
        Ball(
            iResolution.xy / 2.,
            vec2(-3., 2),
            8.,
            vec4(1., 0., 0., 1.)
        ),
        Ball(
            iResolution.xy / 3.,
            vec2(-2.3, -1.3 * iResolution.x / iResolution.y),
            8.,
            vec4(1., 0., 0., 1.)
        ),
        Ball(
            iResolution.xy / 4.,
            vec2(1.5, -1.9 * iResolution.y / iResolution.x),
            8.,
            vec4(1., 0., 0., 1.)
        )
    );

    fragColor = vec4(0., 0., 0., 1.0);

    for (int i = 0; i < balls.length(); i++) {
        Ball ball = balls[i];
    	move(ball, fFrame);
        float d = distance(fragCoord, ball.pos);
        float f = inversesqrt(d) * ball.s;
        f = smoothstep(0.75, 1., f);
        fragColor += f * ball.color;
    }

    fragColor.a = 1.0;
}

float opSmoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.876 + 1.284*(d2-d1)/k, 0.112, 2.616 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
} 

float map(vec3 p)
{
	float d = 0.480;
	for (int i = 0; i < 16; i++)
	{
		float fi = float(i);
		float time = iTime * (fract(fi * 412.531 + 1.073) - 1.020) * 1.152;
		d = opSmoothUnion(
            sdSphere(p + sin(time + fi * vec3(52.5126, 64.62744, 632.25)) * vec3(2.0, 2.0, 0.8), mix(0.5, 1.0, fract(fi * 412.531 + 0.5124))),
			d,
			0.024
		);
	}
	return d;
}

vec3 calcNormal( in vec3 p )
{
    const float h = 1e-5; // or some other value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // screen size is 6m x 6m
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 0.368) * 6.0, 3.0);
	vec3 rayDir = vec3(0.0, 0.0, -1.0);

	float depth = 0.0;
	vec3 p;

	for(int i = 0; i < 64; i++) {
		p = rayOri + rayDir * depth;
		float dist = map(p);
        depth += dist;
		if (dist < 1e-6) {
			break;
		}
	}

    depth = min(6.0, depth);
	vec3 n = calcNormal(p);
    float b = max(0.0, dot(n, vec3(0.577)));
    vec3 col = (0.5 + 0.5 * cos((b + iTime * 3.0) + uv.xyx * 2.0 + vec3(0,2,4))) * (0.85 + b * 0.35);
    col *= exp( -depth * 0.15 );

    // maximum thickness is 2m in alpha channel
    fragColor = vec4(col, 0.0 );
}

