// derived from https://shadertoy.com/view/3tVGRz
// see also https://shadertoy.com/view/wl3Sz2

// a simple testbed for rendering an antialiased striped plane.
// can contrast various filtering methods.
// no texture samplers used!

// probably should stay below .5 though, or may as well reverse the colors!
const float stripeThickness = .2; //.1; //.25; //.02; //

const vec3 Cfill = vec3(1.,1.,.9); //1.,1.,1.); //1.,1.,0.); //
const vec3 Cstripe = vec3(0.,0.,0.); //1.,0.,1.);
const vec3 Csky = vec3(.8,.9,1.);

// refactored out the common derivative filtering portion:
// trivial to change to any dimension.  
// can ignore the axis not being striped
float filterWidth1(float u)
{
    float dx = dFdx(u), dy = dFdy(u);
    return dx * dx + dy * dy + .0001;
}

float filterWidth2(vec2 uv)
{
     vec2 dx = dFdx(uv), dy = dFdy(uv);
    return dot(dx, dx) + dot(dy, dy) + .0001;
}
   // btw fwidth totally works too
//    vec2 fw = fwidth(uv); return dot(fw, fw) + .0001; //max(fw.x, fw.y) + .0001; //
//    return max(dot(dx, dx), dot(dy, dy)) + .0001; // tried; looks same to me

float filterWidth3(vec3 uvw)
{
    vec3 dx = dFdx(uvw), dy = dFdy(uvw);
    return dot(dx, dx) + dot(dy, dy) + .0001;
}

// exp2 filters return 1 when should blur completely out
// g is a positive distance to an edge to be smoothed
// something wrong with one side though; sign/bias problem?
float smoothGrid(float x, float s, float g, float w)
{
    return mix(x, s, exp2(-g / w));    
}

float smoothCheck(float x, float g, float w)
{
    return mix(x, .5, exp2(-g / w));    //smoothGrid(x, .5, g, w); //
}

// basically a 1D checkerboard because gridThickness is always .5
float stripeThickUnfiltered(vec2 p)
{
    return float(int(floor(p.x))&1);
}

// it's simple because it uses simple tent function
// and simple fwidth with relatively simple rational function inlined
float stripeThickAASimple(vec2 p)
{
    vec2 f = fract(p); //p - floor(p); //
    float x = stripeThickUnfiltered(p)
    , g = min(f.x, 1.-f.x) * 2. //1.-2.* abs(f.x-.5) //
    , w = fwidth(p.x) // + fwidth(p.y) //max(fwidth(p.x), fwidth(p.y)) //
    , r = 2.*iResolution.y
    , l = r*g / (1. + r*w); // can try different functions
    return mix(.5, x, clamp(l, 0., 1.));
}

// somewhat filtered thick stripes 
float stripeThickFade(vec2 p)
{
    float x = stripeThickUnfiltered(p);
    vec2 f = fract(p);
    float g = f.x * (1. - f.x) * 4.; // parabola
    return smoothCheck(x, .125 * g, filterWidth1(p.x));
}


float stripeThinUnfiltered2(vec2 p)
{
    return step(.5-.5*stripeThickness, abs(fract(p.x)-.5));
}

float stripeThinUnfiltered(vec2 p)
{
    p -= .5*stripeThickness; // center
    return step(1.-stripeThickness, fract(p.x));
}

float stripeAASimple(vec2 p)
{
    vec2 f = fract(p);
    float g = min(f.x, 1.-f.x) * 2. - stripeThickness
    , x = step(g, 0.) //stripeThinUnfiltered(p)
    , w = fwidth(p.x)
    , r = 2.*iResolution.y
    , l = r*abs(g) / (1. + r*w) // can try different functions
    // up close, should blend toward 0.5,
    // but far away should blend toward stripeThickness!
    , t = mix(.5, stripeThickness, min(w, 1.));
    return mix(t, x, clamp(l, 0., 1.));
}

float stripeSmooth(vec2 p)
{
    vec2 q = p;
    q += .5;
    q -= floor(q);
    q = (stripeThickness + 1.) * .5 - abs(q - .5);
    float w = 12.*filterWidth2(p);
    float s = stripeThickness;
    return smoothstep(.5-w*s,.5+w, q.x);
}

// the half stripeThickness bias is fairly tricky; 
// honestly I shouldn't need to involve smoothstep at all here, hold on
// meh, still not working worth a dern, maybe someday
float stripeLinear(vec2 p)
{
    vec2 q = p;
    q += .5;
    q -= floor(q);
    q = (stripeThickness + 1.) * .5 - abs(q - .5);
    float w = 12.*filterWidth2(p);
    float s = stripeThickness;
    return clamp((q.x - .5-w*s) / (w*(s+1.)), 0., 1.);
}

// filtered thin stripes! finally something half-decent.  PITA!
float stripeThinFade(vec2 p)
{
    float x = stripeThinUnfiltered(p);
    vec2 f = fract(p);
    float g = max(0., .5 - abs(f.x - .5) - .5 * stripeThickness);
    //g = pow(g, 1. + 1.5 * abs(stripeThickness - .5));
    float s = stripeThickness;
    return smoothGrid(x, s, .125 * g, filterWidth1(p.x)); // lame, really; just fades out    
}

// FIXME all these using filterWidth2 are probably wrong or overly complex
// since we really just want the pixel's rate of change over the
// x dimension of the grid, perpendicular to the stripes.

float stripeExp(vec2 p)
{
    const float fadePower = 16.;
    vec2 f = fract(p);
    f = .5 - abs(.5 - f);
    float g = f.x;
    g = max(0., g - .5*stripeThickness);
    g = exp2(-fadePower*g);
    float s = stripeThickness;
    return mix(g, s, exp2(-.02 / filterWidth2(p)));    
}

float stripePow(vec2 p)
{
    const float fadePower = 16.;
    vec2 f = fract(p);
    f = .5 - abs(.5 - f);
    f = max(vec2(0), 1. - f + .5*stripeThickness);
    f = pow(f, vec2(fadePower));
    float g = f.x;
    float s = stripeThickness;
    return mix(g, s, exp2(-.01 / filterWidth2(p)));    
}

// originally from https://shadertoy.com/view/WlVGDh
// this is just not the way to do grids, blurs
// the corners too much, but it sort of works:
float stripeSine(vec2 p)
{
    p *= 3.1415927;
    float g = sin(p.x); // grid texture
    g *= g; g = max(0., 1. - g);
    g = pow(g, 8./stripeThickness);
    float s = stripeThickness;
    g = mix(g, s, min(1., 1.*filterWidth2(p))); //length(fwidth(p)))); // aa
	return g;
}

// similar but with parabola - still just wrong at corners
float stripePara(vec2 p)
{
    vec2 q = fract(p);
    q = max(vec2(0), .5 - stripeThickness*.5 - abs(.5 - q));
    q *= 1.-q; q = 4.*q;
    float g = 1.-q.x; //*q.y;
    g = pow(g, 1./stripeThickness);
    float s = stripeThickness;
    g = mix(g, s, min(1., 4.*filterWidth2(p))); //length(fwidth(p)))); // aa
	return g;
}

// shown on right side of split screen
float ImageR(vec2 uv)
{
    return stripeAASimple(uv);
    return stripeSmooth(uv);
    return stripePow(uv);
    return stripePara(uv);
    return stripeExp(uv);
    return stripeSine(uv);
    return stripeLinear(uv);
///    return stripeThinFade(uv); // borked
    return stripeThickAASimple(uv);
    return stripeThickFade(uv);
}
// shown on left half of split screen
float ImageL(vec2 uv)
{
    return stripeThinUnfiltered(uv);
    return stripeThickUnfiltered(uv);
}


const float maxd = 85000.;

// halfspace
float tracePlaneY(vec3 rp, vec3 rd)
{
    return rp.y <= 0. ? 0. :
    	rd.y >= 0. ? -1. :
    	rp.y / -rd.y;
}


vec2 rot(inout vec2 v, vec2 cs)
{
    float c = cs.x, s = cs.y; // cs is a cosine,sine pair representing angle of rotation
    return v = vec2(v.x * c - v.y * s, v.x * s + v.y * c);
}

vec2 rot(inout vec2 v, float radians)
{
    return rot(v, vec2(cos(radians), sin(radians)));
}


vec3 CameraRay(vec2 p)
{
    vec2 q = (2. * p - iResolution.xy) / iResolution.y;
    vec3 rd = normalize(vec3(q / 2., 1.));
    float pitch = .4, yaw = iTime * .04;
    rot(rd.yz, pitch);
    rot(rd.xz, yaw);
    return rd;
}



void mainImage(out vec4 c, in vec2 p)
{
    // a simple camera
    vec3 rp = vec3(0,1,0);
    rp.y += sin(iTime * 0.07) * .99; // bob slowly up and down
    vec3 rd = CameraRay(p);

    // Y plane distance
    float d = maxd;
    float dplane = tracePlaneY(rp, rd);
    vec2 cc = vec2(1, 0);
    if (dplane >= 0.) {
        d = min(d, dplane);
	    cc = rp.xz + rd.xz * d; // grid uv at hit point
    	cc *= 4.; // tiling
    }

    // split screen at mouse click
    float splits = p.x - iMouse.x;
    bool left = splits < 0.;
    // depending on last mouse click, split screen between two different functions
    float stripes = left 
        ? ImageL(cc)
        : ImageR(cc);

    c.rgb = mix(Cfill, Cstripe, stripes); // colorize

    // no actual lighting in this simple test,
    // but a few dot products would do it.

    float fog = 1.0 - exp2(-.03*d); //0.; //
    c.rgb = mix(c.rgb, Csky, fog);
    // green vertical line at split
    c.rgb = mix(c.rgb, vec3(.0,.5,.0), exp2(-2. * abs(splits)));

    c.rgb = pow(c.rgb, vec3(1./2.2)); // to srgb gamma
    c.a = 1.;
}


