#define SHAPE(N) cos(round(a / (pi * 2. / float(N))) * (pi * 2. / float(N)) - a) * dist

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.1415926535;

    float rot = mod(iTime, 5.) > 4.5 ? (pi * 4. * (mod(iTime, 5.) - 4.5)) : 0.;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 coord = 3. * 2. * (uv - vec2(.5, .5));
    coord.x *= iResolution.x / iResolution.y;
    float a = mod(atan(coord.y, coord.x) + rot, pi * 2.);
    float dist = length(coord);
	float r = pi * 2. / float(6);

    vec3 col = vec3(0.);

    float ma = mod(atan(iMouse.y - iResolution.y / 2.,
                       (iMouse.x - iResolution.x / 2.)), pi * 2.);
    float pw = 0.1 - (dist - 1.5) * 0.75;
    if(dist > 1.5 && dist < 1.65 && ma > a - pw / 2. && ma < a + pw / 2.)
    {
        col = vec3(1.);
    }

    float cOff = 0.1 * sin(iTime * pi * 2.);

    for(int i = 0; i < 5; i++)
    {
        float spd = 3.;
        float spawn_spd = .6;
    	if(SHAPE(6) > .8 + spd - spd / spawn_spd * mod(iTime, spawn_spd) + float(i) * spd 
           && SHAPE(6) < 1. + spd - spd / spawn_spd * mod(iTime, spawn_spd) + float(i) * spd)
    	{
            int num = i + int(floor(iTime / spawn_spd));
            for(int j = 1; j <= 6; j++)
            {
                float sine = sin(float(num) * float(j) * 100.);

                int all_white = 1;
                for(int s = 1; s <= 6; s++)
            	{
                    if(sine < .5)
                    {
                    	all_white = 0;
                    }
                }

                int seg = int(floor((a + r * 0.5) / r));
                if(a < r / 2.) seg = 6;
                int clear_seg = 1 + int(round(2.5 + 2.5 * sin(float(num) * 100.)));
                if(seg == j && sine < .5)
                {
                    if(j != clear_seg && all_white == 0)
                    {
        				col = vec3(1.);
                    }
                }
            }
    	}
    }
    if(SHAPE(6) > .8 + cOff && SHAPE(6) < 1. + cOff)
    {
        col = vec3(1.);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv;

    uv = vec2(0.5 * 3.14159, 0.5 * 3.14159) + (vec2(iResolution.x / 2.0, iResolution.y / 2.0) - fragCoord.xy) / (8.0);

    float value = sin(uv.x) * cos(uv.y) * iTime * 6.0;
    float color = sin(value) * 3.0;

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.5;
    if(color > 0.0) {
	  fragColor = vec4(med, high, med,1.0);
    } else {
      fragColor = vec4(min(0.5, med / 2.0), high, med,1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float fFrame = float(iFrame) / 100.;
    vec2 center = vec2(iResolution) / 2.;
