    fragColor = vec4(0, 0, 0, 1);
    if ((distance(fragCoord, pos1) + distance(fragCoord, pos2)) < distance(pos1, pos2) + 20.)
        fragColor.r = 1.;

    if (distance(fragCoord, pos1) < 3. || distance(fragCoord, pos2) < 3.)
        fragColor.bg = vec2(1.);
}

