#define PI 3.14159

vec3 palette(float l) {
    float m = 2.0*PI * l;
	return vec3(
    	cos(m),
        cos(m + 2.0 * PI / 3.0),
        cos(m + 4.0 * PI / 3.0)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    //uv += vec2(0.0, -1.5);
    float th = atan(uv.y, uv.x);
    float baseLength = length(uv);
    vec2 wavy = vec2(
        sin(th * 19.0 + iTime),
        cos(th * 23.0 + iTime)
    );
    float waveScale = max(0.01, baseLength * 0.2);
    wavy *= waveScale;
    float f = length(uv + wavy);
    f -= iTime / 10.0;
    f = floor(8.0 * f)/8.0;

    vec3 col = palette(f);
    col = pow(col, vec3(1.0/2.2));

    // Output to screen
    fragColor = vec4(col,1.0);

}

float logmap(float l, float v)
{
	return l*v*((1.0)-v);   
}

float iterate(vec2 uv, float toff)
{
	int maxI = 128;
    int i = 0;
    float le = 0.0;
    float x = uv.x;
    while (i < maxI)
    {
		x = logmap( 2.5+sin(iTime+toff), x+uv.y);
        le = x;
        if (le < 0.0001)
        {
            return le;
        }
        i ++;
    }
    return le;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-0.5 * iResolution.xy)/iResolution.y;
	uv *= 2.6;
    float off = (sin(iTime)*0.5+0.5)*0.1;
	vec3 col = vec3(pow(iterate(abs(uv),-off),4.0),pow(iterate(abs(uv),0.0),4.0),pow(iterate(abs(uv),off),4.0));
    fragColor = vec4(col,1.0);
}

