// Space parametrization of a polygon. It only works for convex
// polygons, has some problems, and I still need to optimize it.
//
// It shows a global coordinate system were things stretch at the
// sides BUT coodinates are global and physical, and also a local
// coordinate system that is a regular grid full of circles).


// List of all shape parametrizations: https://www.shadertoy.com/playlist/XclfRs


float dot2( in vec2 v ) { return dot(v,v); }
float cro(in vec2 a, in vec2 b) { return a.x*b.y-a.y*b.x; }

// https://www.shadertoy.com/view/wdBXRW
float sdPoly( in vec2 p, vec2 verts[5], in float r ) 
{
    const int num = verts.length();
	float s = 1.0;
    float d = length(p-verts[0]);
    for( int i=0; i<num; i++ )
    {
        vec2 a = verts[i];
        vec2 b = verts[(i+1)%num];

        vec2  pa = p-a;
        vec2  ba = b-a;
        float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
        d = min(d,length( pa - ba*h ));

        bvec3 cond = bvec3( p.y>=a.y, p.y<b.y, ba.x*pa.y>ba.y*pa.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  
    }

    return s*d-r;
}

float angle( in vec2 a, in vec2 b )
{
    float n = atan(dot(a,b),cro(a,b));
    if( n<0.0 )n+=6.283185;
    return n;
}

// x = local dist
// y = local perimeter dist
// z = total local perimeter
// w = global distance (sdf)
vec4 paPoly( in vec2 p, vec2 verts[5], float r, float band ) 
{
    const int num = verts.length();

    float od = sdPoly( p, verts, r );

    float ra = band*round(od/band);

    float d = length(p-verts[0])-ra;
    float l = 0.0;
    float t = 0.0;
    for( int i=0; i<num; i++ )
    {
        vec2 a = verts[ i       ];
        vec2 b = verts[(i+1)%num];
        vec2 c = verts[(i+2)%num];

        vec2  pa1 = p-a; vec2 ba1 = b-a;
        vec2  pa2 = p-b; vec2 ba2 = c-b;
        float h1 = dot(pa1,ba1)/dot(ba1,ba1);
        float h2 = dot(pa2,ba2)/dot(ba2,ba2);
        float tmp = length( pa1 - ba1*clamp(h1,0.0,1.0) ) - (r+ra);

        float lba = length(ba1);

        if( tmp<d || ((i==num-1) && cro(ba1,pa1)>0.0) )
        {
            d = min(d,tmp);
            if( h1>=0.0 && h1<=1.0 )
            {
                l = t + h1*lba;
            }
            else if( h1>1.0 && h2<0.0)
            {
                l = t+lba;
                l += (r+ra)*angle(ba1,pa2);
            }
        }
        t += lba+(r+ra)*angle(ba1,vec2(-ba2.y,ba2.x));
    }

    return vec4(d,l,t,od);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    // animate polygon
    float id = floor((iTime+0.1)/3.0);
    vec2 kVerts[5];
    for( int i=0; i<5; i++ )
    {
        kVerts[i] = 0.75*cos( float(i)*vec2(1.1,1.3) + vec2(0,2) + id*11.0 );
    }

    const float bandSize = 0.15;

    // distance
    vec4 b = paPoly(uv,kVerts,0.15,bandSize);



    // base color
    float d = b.w;
    vec3 col = vec3(1.0,0.68,0.35) + vec3(-0.35,0.15,0.6)*step(d,0.0);
    col *= 1.0 - 0.6*exp(-64.0*abs(d));
    col *= 1.0-smoothstep(0.47,0.50,abs(fract(d/bandSize)-0.5));
	col *= 0.9 + 0.2*smoothstep(0.26,0.24,abs(fract(0.5*d/bandSize+0.25)-0.5));
    col += 1.0-smoothstep(0.0, 0.005, abs(d)-0.003);


    if( d>-bandSize*0.5 )
    {

	vec2 q = b.xy;
    // optional - ensure periodicity, but break physicallity
    q.y *= floor(b.z/bandSize)*(bandSize/b.z);

    // animate circles
    q.y -= iTime*0.1;

    // draw circles
    vec2 uv = fract(q/bandSize+0.5)-0.5;

    float l = length(uv);
    col *= 0.1 + 0.9*smoothstep(0.0,0.02,abs(l-0.35)-0.03);
    col *= 0.1 + 0.9*smoothstep(0.0,0.02,l-0.10);
    }

	fragColor = vec4(col, 1.0);
}

//--------------------------------------------------------------------------------------------------------------------------------------//  Brick wall with self-shadowing.
//
//  set BRICK_PATTERN   to select the pattern to compile  ( or add more patterns...)
//
//  The surface is seen as an array of tesselating rectangular cells. 
//  Each cell has a series of entries. Each entry represents a single brick (0,0)  to  shiftAndSizes[entry].zw  in the cell corner. 
//  shiftAndSizes[entry].xy  has the offset of the cell, for that entry  (cells effectively overlap per entry).
//
//  This enables the definition of different brick patterns.
//  
//  
//  CELLSIZEU and CELLSIZEV are set for the pattern.  If we add to these, we space the cells out.
//
//  All code, feel free to use, abuse and improve...
//
//  'BRICK_PATTERN'   set this for a few simple patterns I set up - 0,1,2,3.  
//--------------------------------------------------------------------------------------------------------------------------------------

#define  USE_SHADOW 
#define  BRICK_PATTERN 3

// LIGHT_DIRECTION:   y  is  negative as coming from ABOVE.
//                    (  y close to 0  has numerical stability issues for shadow  (ie. light straight down).
//                      producing sparkle on brick edges. )

const vec3   LIGHT_DIRECTION =  normalize(vec3(  0.917,  -0.62,  -0.4 ));  
const float  LIGHT_AMBIENT_INTENSITY = 0.5f;   

 // TODO:   have brick sizes per 'shiftAndSizes' entry.  More than one entry can have the same 'V' offset so should be easy
 //         to have mixed size bricks on one row.  This would make it possible to do herringbone etc.  

#if BRICK_PATTERN == 0		// regular brick

	const float BRICK_ULEN = 0.1; 
	const float BRICK_UGAP = 0.05;

	const float BRICK_VLEN = 0.35; 
	const float BRICK_VGAP = 0.05; 

	const int maxLayers = 2;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0,     BRICK_ULEN, BRICK_VLEN ),  
      vec4( BRICK_ULEN + BRICK_UGAP, 0.5 * (BRICK_VLEN + BRICK_VGAP ),  BRICK_ULEN, BRICK_VLEN ) 
	);
    const float brickDepth[maxLayers] = float[]
    (
        0.013, 0.057
    );

	const float CELLSIZEU  = (BRICK_ULEN + BRICK_UGAP ) * 2.0 + 0.001;
	const float CELLSIZEV  = (BRICK_VLEN + BRICK_VGAP ) + 0.001;


	const float  y1 = -0.47439;

#elif BRICK_PATTERN == 1      //  short and long alternate

	const float BRICK_ULEN = 0.1; 
	const float BRICK_UGAP = 0.05;

	const float BRICK_VLEN = 0.35; 
	const float BRICK_VGAP = 0.05; 
	const float BRICK_VLEN_SHORT = 0.11 ;


	const float dispIn = (BRICK_VGAP + BRICK_VLEN_SHORT) * 0.5 ; 
	const int maxLayers = 4;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0,   BRICK_ULEN, BRICK_VLEN_SHORT), 
      vec4( 0, BRICK_VLEN_SHORT + BRICK_VGAP,  BRICK_ULEN, BRICK_VLEN ), 
      vec4( BRICK_ULEN + BRICK_UGAP,  dispIn,  BRICK_ULEN, BRICK_VLEN ) ,
      vec4( BRICK_ULEN + BRICK_UGAP, dispIn + BRICK_VLEN  + BRICK_VGAP,    BRICK_ULEN, BRICK_VLEN_SHORT ) 
	);

	const float CELLSIZEU  = (BRICK_ULEN + BRICK_UGAP ) * 2.0 + 0.001;
	const float CELLSIZEV  = (BRICK_VLEN + BRICK_VGAP ) + (BRICK_VLEN_SHORT + BRICK_VGAP ) + 0.001;

  	const float brickDepth[maxLayers] = float[]
    (
         0.03, 0.023, 0.033, 0.021
    );
	const float  y1 = -0.47439;

#elif BRICK_PATTERN == 2        // square cycle

	const float SCL = 0.8;	// scale to what looks pretty...
	const float BRICK_SA = 0.1 * SCL;
	const float BRICK_SB = 0.05* SCL;
	const float BRICK_SC = 0.25* SCL;
    const float BRICK_SD = 0.05* SCL; 

	const int maxLayers = 4;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0, BRICK_SC, BRICK_SA ), 
      vec4( 0,                    BRICK_SA + BRICK_SB, BRICK_SA, BRICK_SC  ) , 
      vec4( BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA,   BRICK_SC, BRICK_SA ) ,  
      vec4( BRICK_SC + BRICK_SB, 0,  BRICK_SA, BRICK_SC ) 
	);

	const float CELLSIZEU  =  BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD  + 0.001;
	const float CELLSIZEV  =  BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD  + 0.001; 
	const float  DepthOfBrick = 0.04439; 

	const float brickDepth[maxLayers] = float[]
    (
         0.04, 0.033, 0.043, 0.030
    );
	const float  y1 = -0.47439;

#elif BRICK_PATTERN == 3        // square cycle staggered

	const float SCL = 0.65;// scale to what looks pretty...
	const float BRICK_SA = 0.1 * SCL;
	const float BRICK_SB = 0.05* SCL;
	const float BRICK_SC = 0.25* SCL;
    const float BRICK_SD = 0.05* SCL; 

	const float staggerU = (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD);
	const float staggerV = (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD) * 0.5;

	const int maxLayers = 8;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0, BRICK_SC, BRICK_SA ), 
      vec4( 0, BRICK_SA + BRICK_SB, BRICK_SA, BRICK_SC  ) , 
      vec4( BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA,   BRICK_SC, BRICK_SA ) ,  
      vec4( BRICK_SC + BRICK_SB, 0,  BRICK_SA, BRICK_SC ),

      vec4( staggerU, staggerV, BRICK_SC, BRICK_SA   ), 
      vec4( staggerU, BRICK_SA + BRICK_SB + staggerV, BRICK_SA, BRICK_SC  ) , 
      vec4( staggerU + BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA + staggerV,   BRICK_SC, BRICK_SA  ) ,  
      vec4( staggerU + BRICK_SC + BRICK_SB, staggerV,  BRICK_SA, BRICK_SC) 
	);

    const float brickDepth[maxLayers] = float[]
    (
        0.013, 0.047, 0.033, 0.027, 0.043, 0.019, 0.051, 0.041
    );


	const float CELLSIZEU  =  2.0 * (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD)  + 0.001;
	const float CELLSIZEV  =  (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD)  + 0.001;

	const float  y1 = -0.47439;
 #endif



const float  xLo =  - 1.2; 
const float  xHi =    1.56;
const float  zLo =    0.48;  //-2.0; 
const float  zHi =    2.22;

	// Depth of brick from mortar



	// colors per face. For testing

const vec3 colr0[5] = vec3[]
(
    vec3(0,0,0),
    vec3(1,0.5,0),  
	vec3(0.2,0.3,0.4),  
	vec3(1,0,0),
	vec3(0.7,0.5,0.39)  // 4 is mortar 
); 
const vec3 colr1[5] = vec3[]
(
    vec3(0,0,0),
    vec3(0.85,0.5 ,0.44 ), 
    vec3(1,0.5,0),  
	vec3(0.8,0.5,0.2),
	vec3(0.7,0.5,0.39)  // 4 is mortar 
);   

float  RECIP_CELLSIZEU = 1.0 / CELLSIZEU;
float  RECIP_CELLSIZEV = 1.0 / CELLSIZEV;  

//const float  y1 =  y0 - DepthOfBrick;

const vec4 boundsA = vec4(zLo, -zHi, xLo, -xHi );
const vec4 boundsB = vec4(-1.0, 1.0, -1.0, 1.0);

	//  FPT limits, needed for ensuring numerical stability as ray axes approach alignment with the world space.
    //  https://en.wikipedia.org/wiki/IEEE_754-1985

const float SMALLEST_POSITIVE_IEEE754_FPT =  intBitsToFloat(1); // ±2^−23 × 2^−126  are closest representable numbers to 0
const float BIGGEST_IEEE754_FPT = 	intBitsToFloat(0x7F7FFFFF);  

// Fed up with typing  'clamp(..., 0.0, 1.0)'   when we know it will go to a saturate and
// probably get it for free on the GPU.

float saturate(float x)
{
    return clamp(x, 0.0, 1.0);
}
vec2 saturate(vec2 x)
{
    return clamp(x, 0.0, 1.0);
}
vec3 saturate(vec3 x)
{
    return clamp(x, 0.0, 1.0);
} 
vec4 saturate(vec4 x)
{
    return clamp(x, 0.0, 1.0);
}

float random2(vec2 st)
{
    // Quick random-ish used for determining a random scalar for a brick (for color, etc.)
    //  modded from hash function at:    https://www.shadertoy.com/view/XlXcW4
    //  ( taken one xor line,  good enough for this test! )

    uint  a = floatBitsToUint(st.x);
    uint  b = floatBitsToUint(st.y);

    float h = float(((a>>8U)^b)*1103515245U) / float(0xffffffffU); 
    return h;              
} 


int BrickWall( in vec3 ro,  vec3 rd,  out bool inShadow, out vec3 normal,  out vec2 idx,  inout vec3 posn)  
{ 
    vec3 iSect[maxLayers];

    inShadow = false;
    normal = vec3(0.0, -1.0, 0.0);
    idx = vec2(0);
    posn = vec3(0); 


 	// NOTE:  if rd.y == 0  (ie. ray is coplanar, then  intersect0 == 0, and the bounds test passes, (if we had used the
    // nieve approach, then  intersect0 would approach infinity as rd.y approaches 0, because we would be using 1/rd.y - 
    // but we multiply up by rd.y^2  to avoid divisions AND maintain the inequality tests.
    //  (In the case of rd2.y very close to 0, then early out). 
    vec3  rd2 = rd * rd;  
    if( rd2.y <  SMALLEST_POSITIVE_IEEE754_FPT )   // consider ray as coplanar,  rd2.y = y^2 so always +ve 
        return 0; 


    float bestFaceCode = 4.0;		// Default to Mortar

    float  recip_rd2y = 1.0 / rd2.y;    

    float rdxrdz = rd.x * rd.z;
    float rdxrdz2 = rdxrdz * rdxrdz; 
    vec2 C01 =  rdxrdz * rd.zx; 
    float C6  =  rdxrdz2 * recip_rd2y; 

    // +/- 1 depending on half-space for each axis X, Z

    vec2 hsPlusMinus =  vec2 (  ( rd.x >= 0.0 ) ? 1.0 : -1.0 ,  ( rd.z >= 0.0 ) ? 1.0 : -1.0 );    

    float bestT = BIGGEST_IEEE754_FPT;  
    float topFaceT =   0.0;  

    vec2  F01 = vec2(CELLSIZEU, CELLSIZEV) * C01 ; 
    vec2  D01 = -(vec2(CELLSIZEU,CELLSIZEV) + ro.xz ) * C01; 

    float bestSy = 0.0;
    vec2  fl2 =  vec2(CELLSIZEU, CELLSIZEV); 

    float S1 =  ro.y * rdxrdz2;
    vec4  vecE0E1 = vec4( 0.0, 1.0,  0.0, 1.0); 
    if( C01.x < 0.0 )
        vecE0E1.xy = vec2(1.0, 0.0);
    if( C01.y < 0.0 )
        vecE0E1.zw = vec2(1.0, 0.0); 

    vec4 C5 = vecE0E1 * C01.xxyy; 


    for( int layer = 0; layer < maxLayers; ++layer)
    {
        vec4 ss = shiftAndSizes[layer]; 

        float y0 = y1 + brickDepth[layer];
        float t0 =  ( -ro.y + y0) *  rd.y; 
   	    float t1 =  ( -ro.y + y1) *  rd.y;

    	float sum = t0 + t1;
   	    t0 = min(t0,t1); 
    	if( t0 < 0.0 )     // early out if intersection is behind camera
        	return 0;	 
   	    t1 = sum - t0;  		

        vec3  intersect0 = ro * rd2.y + t0 * rd;
        if( dot(saturate( boundsA * rd2.y + intersect0.zzxx * boundsB ), vec4(1.0)) ==  0.0 )
        {

        	intersect0 *= recip_rd2y;    // We have already checked that rd2.y >=  SMALLEST_POSITIVE_IEEE754_FPT,  above
 			iSect[layer] = intersect0;

 			vec2 fufv   = floor((intersect0.xz - ss.xy ) * vec2(RECIP_CELLSIZEU, RECIP_CELLSIZEV)); 
			vec2 cellUV = fufv  * vec2(CELLSIZEU, CELLSIZEV) + ss.xy ; 

            vec2 currentIdx =  fufv  + float(layer); 

        	vec2 a = saturate( intersect0.xz - cellUV.xy -  ss.zw );
        	if ((a.x + a.y) == 0.0)  
    		{
       	 	 	// brick face 
                float temp =  t0 * C6;    // convert parametric * rd2.y to  parametric * rdxrdz2  to match the multiplied parametrics
                						  // used for the vertical end/
                if( temp < bestT )
                {
                    topFaceT = temp;
                    bestT = temp;
            		idx =   currentIdx; 
            		posn = intersect0; 
                }
    		} 

       	 	vec4 sortOffset = C5 * ss.zzww; 

        	vec2  C4  = vec2(y0,y1) * rdxrdz2 ;  


       	    float dIdxV = currentIdx.y;

            vec2 dudvYstart = cellUV.y * C01.y + D01.y + sortOffset.zw; 

       	    vec4 t;
       	    t.xy = cellUV.x * C01.x + D01.x  + sortOffset.xy; 
        	for( int u = 0; u < 3; u++ )
        	{  
         	   	currentIdx.y = dIdxV ;
         	  	currentIdx.x++; 
    	        t.zw  = dudvYstart;       
   	         	for(int v = 0; v  < 3; v++ )
    	        {
                	//  s is an associated value for tv,  so that we know after ordering  tv, whether a line segment
                	//  crosses / intersects a cell   ( when s.x != s.y ).

                	currentIdx.y++;

        			vec4 tv = t;
                	vec3 s =  vec3(0.0,0.0,1.0);

	                if(tv.x > tv.z) { tv.z = tv.x; s.xz = vec2(1.0, 0.0);  }  
   					if(tv.y > tv.w) { tv.y = tv.w; s.y = 1.0;   } 
   					if(tv.y > tv.z) { tv.y = tv.z; s.y = s.z;  }  

    				float ds = s.x - s.y;
                	float p =  S1 + tv.y * rd.y;  

    				if( (  
                     saturate(1.0 - ds*ds) + 
                     saturate( tv.y - bestT) +  
                     saturate( p  - C4.x ) +  
                     saturate( C4.y - p  )) == 0.0)
            		{  
                  		bestT = tv.y; 
                        bestSy =  s.y; 
                        idx =  currentIdx; 
            		}

                 	t.zw += F01.y;
            	}
            	t.xy += F01.x; 
        	}
        	fl2 += float(maxLayers); 
    	} 
    }

    if( bestT == topFaceT)
    { 
        bestFaceCode = 3.0;  
    }
    else
    if( bestT != BIGGEST_IEEE754_FPT )
    {
		normal = vec3( hsPlusMinus.x * (1.0 -bestSy), 0.0, hsPlusMinus.y * bestSy  ) ;  
        bestFaceCode = bestSy + 1.0;  
    }

    // Trim edge    
   float t1 =  ( -ro.y + y1) *  rd.y;
   if (bestFaceCode != 4.0)
   {  
        rd2.y = rdxrdz2;
        t1 = bestT; 
   }

   vec3  coord = ro  * rd2.y + t1 * rd;  // ray intersection point multiplied by rd2.y,  ie. ray-direction 'y' squared

   if( dot(saturate( boundsA * rd2.y + coord.zzxx * boundsB ), vec4(1.0)) !=  0.0 )
   {
        return 0;   
   } 

   #if defined(USE_SHADOW)

    // If light source is from behind wall,  or behind a brick end or side, then always in shadow..

    bool  shadow = ((saturate( LIGHT_DIRECTION.y )  + saturate( -dot( normal.xz, LIGHT_DIRECTION.xz ))  ) != 0.0);

     // Otherwise examine adjacent cells for projecting the eye ray intersection point along the reverse light direction
     // (Since all edges are vertical in the plane, we only need to detect the XZ ray crossing the brick area of a cell.

    if( !shadow)
    {  
         float rdxrdz = LIGHT_DIRECTION.x * LIGHT_DIRECTION.z; 
    	 vec2  C01    =  -rdxrdz * vec2( LIGHT_DIRECTION.z, LIGHT_DIRECTION.x); 
         float rdxrdz2   = rdxrdz * rdxrdz * rd2.y; 
         float G1 = rdxrdz2 * rd2.y; 

   	     vec2 hsPlusMinus =  vec2 (  ( LIGHT_DIRECTION.x <= 0.0 ) ? 1.0 : -1.0 ,  ( LIGHT_DIRECTION.z <= 0.0 ) ? 1.0 : -1.0 );   

    	 vec2  D01 = -(vec2(CELLSIZEU, CELLSIZEV)  * rd2.y + coord.xz) * C01; 
         vec3  G0 = -LIGHT_DIRECTION * rd2.y;   //-ve to allow + in loop,  easier to mad
         vec4  S0 = boundsA * G1;
         vec3  S1 = coord * rdxrdz2;

        vec4  vecE0E1 = vec4( 0.0, 1.0,  0.0, 1.0); 
    	if( C01.x < 0.0 )
        	vecE0E1.xy = vec2(1.0, 0.0);
    	if( C01.y < 0.0 )
        	vecE0E1.zw = vec2(1.0, 0.0); 

         vec2  C23 = C01  * rd2.y;  
    	 vec2  F10 = vec2(CELLSIZEU, CELLSIZEV) * C23 ;  
         vec4  C5 = C23.xxyy * vecE0E1; 

    	 for( int layer = 0; layer < maxLayers; ++layer)
    	 {
        	vec4 ss = shiftAndSizes[layer];

            vec3 intersect0 = iSect[layer];
    		float y0 = y1 + brickDepth[layer];

            float t0 =  ( y0 * rd2.y -coord.y ) ; 
        	intersect0.xz = coord.xz - t0 * LIGHT_DIRECTION.xz; 
    	 	vec2  C4  = vec2(y0,y1) * G1 ; 
         	intersect0 /= rd2.y; 

            vec4 sortOffset = C5 * ss.zzww;
    		float fu = (intersect0.x - ss.x ) * RECIP_CELLSIZEU;
    		float fv = (intersect0.z - ss.y ) * RECIP_CELLSIZEV; 

    		float cellU = floor(fu ) * CELLSIZEU + ss.x ;
    		float cellV = floor(fv ) * CELLSIZEV + ss.y ; 

            vec2 dudvYstart = cellV * C23.y + D01.y + sortOffset.zw;

            vec4 t ; 
            t.xy =  cellU * C23.x + D01.x  + sortOffset.xy;
        	for( int u = 0; u < 3; u++ )
        	{   
                t.zw  = dudvYstart; 
               	for(int v = 0; v  < 3; v++ )
            	{  
        			vec4 tv = t;  //  tv.x <=  tv.y,    tv.z <= tv.w
                	vec3 s =  vec3(0.0,0.0,1.0);

                	if(tv.x > tv.z) { tv.z = tv.x; s.xz = vec2(1.0, 0.0);  }  
   					if(tv.y > tv.w) { tv.y = tv.w; s.y = 1.0;   } 
   					if(tv.y > tv.z) { tv.y = tv.z; s.y = s.z;  }    

                    float ds = s.x - s.y;
                    vec3  p  = S1 + tv.y * G0;   
                    vec4  a  = saturate( S0 + p.zzxx * boundsB ); 

                    // could break out as soon as shadow is true.  probably worth doing (wavefronts ? )

                    shadow = shadow ||
    				   ( 
                         (saturate(1.0 - ds*ds) + 
                          saturate( -tv.y ) +  
       				      saturate(  p.y - C4.x ) +
                          saturate(  C4.y - p.y ) +
                          a.x + a.y + a.z + a.w               // using dot(a,vec4(1.0)) is not good on GCN chips.
                         ) == 0.0
                       ) ;

                	t.zw += F10.y;
            	}   
            	t.xy += F10.x;
        	} 
    	}  
    } 
    inShadow = shadow;

   #endif

   posn = coord / rd2.y;

   return int(bestFaceCode);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-1.0 + 2.0*fragCoord.xy / iResolution.xy) *  vec2(iResolution.x/iResolution.y, 1.0);

	vec3 ro;
    ro.y = -0.2;

    vec2 maxSpan = vec2( xHi - xLo, zHi - zLo ) * 0.4; 

    float pt = iTime;
    ro.x = xLo + (  xHi - xLo) * 0.5 + sin(pt * 0.2) * maxSpan.x;
    ro.z = zLo + (  zHi - zLo) * 0.5 + cos(pt * 0.2) * maxSpan.y;
    float theta = 1.4 + iMouse.x * 0.01;
    float c = cos(theta);
    float s = sin(theta);
    mat2 rotn;
    rotn[0][0] = c;
    rotn[0][1] = -s;
    rotn[1][0] =  s;
    rotn[1][1] = c;

    vec3 dirn = normalize(vec3(uv,1.0));
    dirn.xz *= rotn; 

    	// Now do something more interesting....
    bool inShadow = false;
    vec3 normal = vec3(0); 
    vec3 posn = vec3(0); 

    vec2 idx;   

    int faceCode = BrickWall( ro, normalize(dirn), inShadow, normal,  idx, posn);   

    float idCoeff = random2( idx.xy *  1.2341 ); 

    	// Apply the color,  simple dp lighting and darken if the point is in shadow,

    vec4  faceColor  =  vec4(  mix(colr0[faceCode], colr1[faceCode],idCoeff) , 1.0 );  

    if( faceCode != 0)		// Do not light if not on wall surface 
    {
   	 	float intensity =  mix( LIGHT_AMBIENT_INTENSITY, 1.0, clamp(dot(normal, LIGHT_DIRECTION), 0.0, 1.0) ) ;
   	 	faceColor.xyz *= intensity;
    	if(inShadow)
    	{
       	 	faceColor.xyz *= 0.6;   
   		} 
    }  

    fragColor = faceColor; 

}

#define PI 3.1415926535897932384626433

float sq(vec3 x)
{
    return dot(x, x);
}
float sq(vec2 x)
{
    return dot(x, x);
}
float sq(float x)
{
    return x*x;
}
int sq(int x)
{
    return x*x;
}

vec2 sic(float t)
{
    return vec2(cos(t), sin(t));
}

struct TorusKnotParameters
{
    float kp;
    float kq;
    float r1;
    float r2;
    float r3;
};

vec3 torusKnot(float t, TorusKnotParameters tkp)
{
    vec2 sicXY = sic(tkp.kp * t);
    vec2 sicRZ = tkp.r2 * sic(tkp.kq * t);

    return vec3((sicRZ.x + tkp.r1)*sicXY, sicRZ.y);
}
vec3 torusKnotDerivative(float t, TorusKnotParameters tkp)
{
    vec2 sicXY = sic(tkp.kp * t);
    vec2 sicRZ = tkp.r2 * sic(tkp.kq * t);

    vec2 dSicXY = tkp.kp * vec2(-1, 1) * sicXY.yx;
    vec2 dSicRZ = tkp.kq * vec2(-1, 1) * sicRZ.yx;

    return vec3(dSicRZ.x*sicXY + dSicXY*(sicRZ.x + tkp.r1), dSicRZ.y);
}

float torusKnotSqDistance(float t, vec3 p, TorusKnotParameters tkp)
{
    return sq(torusKnot(t, tkp) - p);
}
float torusKnotSqDistanceDerivative(float t, vec3 p, TorusKnotParameters tkp)
{
    return 2.*dot(torusKnot(t, tkp) - p, torusKnotDerivative(t, tkp));
}

float minimizeDistance(float t, vec3 p, TorusKnotParameters tkp)
{
    float lerningRate = 0.01/max(tkp.kq,tkp.kp);
	const int maxIterations = 30;

    for(int i = 0; i < maxIterations; i++)
    {
        float dt = torusKnotSqDistanceDerivative(t, p, tkp);

        if(abs(dt) < 0.001)
            break;

        t -= lerningRate*dt;
    }

    return t;
}

struct Ray
{
  vec3 ro;
  vec3 rd;
};

float torusKnotLineSqDistance(float t, Ray ray, TorusKnotParameters tkp)
{
    //|d| = 1

    //a^2 = c^2 - b^2
    //a^2 = c^2 - (b/c * c)^2
    //a^2 = c^2 - (cos() * c)^2

    return torusKnotSqDistance(t, ray.ro, tkp) - sq(dot(torusKnot(t, tkp) - ray.ro, ray.rd));
}
float torusKnotLineSqDistanceDerivative(float t, Ray ray, TorusKnotParameters tkp)
{
    //return torusKnotSqDistanceDerivative(t, ray.ro, tkp) - 2.*dot(torusKnot(t, tkp) - ray.ro, ray.rd)*dot(torusKnotDerivative(t, tkp), ray.rd);
    //  ⇓
	//return 2.*dot(torusKnot(t, tkp) - ray.ro, torusKnotDerivative(t, tkp)) - 2.*dot(torusKnot(t, tkp) - ray.ro, ray.rd)*dot(torusKnotDerivative(t, tkp), ray.rd);
	//  ⇓

    vec3 tk = torusKnot(t, tkp);
    vec3 tkd = torusKnotDerivative(t, tkp);

    //return 2.*dot(tk - ro, tkd) - 2.*dot(tk - ro, rd)*dot(tkd, rd);
    //  ⇓
    //return 2.*(dot(tk - ro, tkd) - rdot(tk - ro, rd)*dot(tkd, rd));
    //  ⇓
    //return 2.*(dot(tk - ro, tkd) - rdot(tk - ro, rd*dot(tkd, rd)));
    //  ⇓
    return 2.*(dot(tk - ray.ro, tkd - ray.rd*dot(tkd, ray.rd)));
}

vec3 firstTorusKnotLineSqDistanceMinimumInside(Ray ray, TorusKnotParameters tkp)
{
    int sections = 3*int(max(tkp.kq,tkp.kp));
    float sectionLength = 2.*PI/float(sections);

    float lerningRate = 0.008/(max(tkp.kq,tkp.kp)-0.75);
    const int maxIterations = 100;//50

    float minDist;
    float bestT;
    bool found = false;

    for(int j = 0; j < sections; j++)
    {
        float t = sectionLength * float(j);
        bool failed = false;

        for(int i = 0; i < maxIterations; i++)
        {
            float dt = torusKnotLineSqDistanceDerivative(t, ray, tkp);

            if(abs(dt) < 0.1)break;

            t -= lerningRate*dt;


            if(t != clamp(t, sectionLength * (float(j)-1.), sectionLength * (float(j)+1.)))
            {
                failed = true;
                break;
            }
        }
        if(failed)continue;

        float lineSqDist = torusKnotLineSqDistance(t, ray, tkp);

        if(lineSqDist <= sq(tkp.r3))
        {
            float distAlongD = dot(torusKnot(t, tkp) - ray.ro, ray.rd);

            if(!found || distAlongD < minDist)
            {
                minDist = distAlongD;
                bestT = t;
            }

            found = true;
        }
    }

    return vec3(bestT, minDist, found ? 1. : 0.);
}

float dot01(vec3 a, vec3 b)// [-1, 1] => [0 ,1]
{
    return (dot(a, b) + 1.)/2.;
}

Ray intersect(Ray ray, TorusKnotParameters tkp)
{
    const int maxIterations = 50;

    vec3 res = firstTorusKnotLineSqDistanceMinimumInside(ray, tkp);

    if(res[2] < 0.5)
    {
        return Ray(vec3(0), vec3(0));
    }

    float t = res[0];
    vec3 p = ray.ro + res[1] * ray.rd;

    for(int i = 0; i < maxIterations; i++)
    {
        t = minimizeDistance(t, p, tkp);
        float d = sqrt(torusKnotSqDistance(t, p, tkp)) - tkp.r3;
        p += d * ray.rd;

        if(abs(d) < 0.01)break;
    }

    vec3 normal = normalize(torusKnot(t, tkp) - p);

    return Ray(p, normal);
}

int gcd(ivec2 v)
{
    while(v.x != v.y)
    {
        if(v.x > v.y)
            v.x -= v.y;
        else
            v.y -= v.x;
    }

    return v.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.y;

    vec2 angles = /*false &&*/ iMouse.z > 0.5 ? PI*(2.*iMouse.xy/iResolution.xy - 1.) : sic(0.5*iTime);
    vec2 sic0 = sic(angles[0]);
    vec2 sic1 = sic(angles[1]);

    vec3 f = vec3(sic1.x * sic0, -sic1.y);
    vec3 u = vec3(sic1.y * sic0, sic1.x);
    vec3 r = -cross(u, f);

    vec3 ro = -10.*f;

    float zoom = 1.;

    ////////////////////////////////////The torus knot parameters//////////////////////////////////////////
    float kp = 3., kq = 5., r1 = 2., r2 = 1., r3 = 0.2 + 0.3*(sin(iTime)+1.)/2.;//Change kp and kq!
    // p and q are flipped. The parameters: 'lerningRate', 'maxIterations' and 'sections' also may need to be changed.

    /*
    ivec2 kpkq = ivec2(10.*iMouse.xy/iResolution.xy) + 1;
    kpkq /= gcd(kpkq);

    kp = float(kpkq.x);
    kq = float(kpkq.y);
	*/

    TorusKnotParameters tkp = TorusKnotParameters(kp, kq, r1, r2, r3);

    vec3 rd = normalize(zoom * f + uv.x*r + uv.y*u);


    Ray lr = intersect(Ray(ro, rd), tkp);

    vec3 col;

    if(sq(lr.rd) < 0.5)
    {
        col = vec3(0);
    }
    else
    {
        //reflections
        for(int i = 0; i < 0; i++)
        {
            Ray nr = intersect(lr, tkp);
            if(sq(nr.rd) < 0.5)
                break;

            nr.ro -= 0.01*nr.rd;
            lr = nr;
        }

        float offset = 2.*iTime;

        vec2 sicRZ = sic(0.);

        vec3 redLight = vec3(sicRZ.x * sic(offset), sicRZ.y);
        vec3 blueLight = vec3(sicRZ.x * sic(2.*PI/3. + offset), sicRZ.y);
        vec3 greenLight = vec3(sicRZ.x * sic(2.*2.*PI/3. + offset), sicRZ.y);

        col = vec3(dot01(lr.rd, -redLight), dot01(lr.rd, -blueLight), dot01(lr.rd, -greenLight));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

