#define PI 3.141592
#define TAU (2.*PI)
#define ITER 64.
#define BPM (110./60.)

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float hash11 (float x)
{return fract(sin(x)*1245.5);}

void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y>p.x) p = p.yx;
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float plane (vec3 p, vec3 n, float d)
{return dot (p,normalize(n)) + d;}

float cut_planes(vec3 p)
{
    p = abs(p)-0.7;

    mo(p.xz, vec2(0.8,1.));   
    mo(p.yz, vec2(0.6,0.4));
    mo(p.xy, vec2(0.05)); 
    return plane(p, vec3(0.5,0.8,0.7), 0.);
}

float SDF (vec3 p)
{
    p.y += sin(iTime)*0.1;
    p.xz *= rot(iTime*0.2);

    return max(cut_planes(p),box(p,vec3(1.)));
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float spec (vec3 rd, vec3 l, vec3 n)
{
    vec3 h = normalize(l-rd);
    float dotNH = dot(h, n);
    return pow(max(dotNH,0.), 10.);
}

float fresnel (vec3 rd, vec3 n, float p)
{return pow(clamp(1.-dot(-rd,n),0.,1.), p);}

float lighting (vec3 n, vec3 l)
{return max(dot(n,l),0.);}

vec3 palette (float t)
{
    vec3 a,b = vec3(0.7);
    vec3 c = vec3(.5);
    vec3 d = vec3(0.,0.8,0.2);
    return a+b*cos(TAU*(c*t+d));
}

vec3 background (vec2 uv, float detail)
{
    vec2 uu = uv;

    uv.y *= detail; 
    float horizon_id = floor(uv.y)-0.5;
    uv.y = fract(uv.y)-0.5;

    float m1 = smoothstep(0.05, 0.01,abs(uv.y)-(1.-abs(uv.x)*0.2)*(1./(detail*8.)));
    float m2 = step(uv.x+hash11(horizon_id)*2.,0.5+exp(-fract(iTime*BPM)));
    float horizon_m = clamp(m1*m2,0.,1.);

    uv = uu;
    uv.x *= detail;
    float vertical_id = floor(uv.x)-0.5;
    uv.x = fract(uv.x)-0.5;

    float m3 = smoothstep(0.05, 0.01,abs(uv.x)-(1.-abs(uv.y))*(1./(detail*7.)));
    float m4 = step(uv.y+hash11(vertical_id)*2.,0.5+exp(-fract(iTime*BPM)));
    float vertical_m = clamp(m3*m4,0.,1.);

    float end_m = clamp(horizon_m + vertical_m,0.,1.);

	return vec3(.98)*(1.-end_m)
        +vec3(hash11(horizon_id),hash11(horizon_id)*0.8,0.9)*(horizon_m)
        + vec3(hash11(vertical_id)*0.2,0.8,hash11(vertical_id))*(vertical_m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0.001,0.001,-2.1),
        rd = normalize(vec3(uv,1.)),
        p = ro,
        l = normalize(vec3(-1.5,-1.,-2.5)),
        col = background(abs(uv)-0.2, 5.);

    float shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        p += d * rd;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        col = palette(clamp(dot(-rd,n),0.,1.));
        col += lighting(n,l);
        col += spec(rd,n,l)*0.8;
        col += fresnel(rd, n, 4.);

        col *= (1.-shad);
    }

	// vignetting (from iq)
	vec2 q = fragCoord.xy / iResolution.xy;
	col *= .5 + 0.5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), 0.2);

    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

# define ttime floor(iTime*.2) + pow(fract(iTime*.2),1.25)

mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));  }


vec3 fig(vec2 uv, float p) {
    float d =abs(atan(uv.x,uv.y))+3.141592;
    return floor(p*vec3(d)/(3.141592*2.))/p;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;

    uv *=r(3.14*.85);

    uv*=4.;
    vec3 a = fig(uv*r(3.1415/4.+cos(iTime)*.1)+vec2(0.5,0.5),10.+sin(ttime));
    vec3 b = fig((uv*r(3.1415*4.+sin(iTime)*.1))-vec2(0.5,0.5),10.+cos(ttime));

     vec3 c = fig(uv*r(3.1415*4.+sin(iTime)*.1)+vec2(0.5,0.5),10.+cos(ttime));
    vec3 d = fig((uv*r(3.1415/4.+cos(iTime))*.1)-vec2(0.5,0.5),10.+sin(ttime));

    vec3 kk = smoothstep(0.69,.75+sin(iTime)*.01,(b+a+c+d)/4.);
    kk = mix(vec3(0.9),vec3(0.1,0.2,0.3),kk);
    fragColor = vec4(kk,1.0);
}

