float PI = 3.14159;
vec3 rotate3D(vec3 point, vec3 rotation) {
    vec3 r = rotation;
	mat3 rz = mat3(cos(r.z), -sin(r.z), 0,
                   sin(r.z),  cos(r.z), 0,
                   0,         0,        1);
    mat3 ry = mat3( cos(r.y), 0, sin(r.y),
                    0       , 1, 0       ,
                   -sin(r.y), 0, cos(r.y));
    mat3 rx = mat3(1, 0       , 0        ,
                   0, cos(r.x), -sin(r.x),
                   0, sin(r.x),  cos(r.x));
    return rx * ry * rz * point;
}
float sdfSphere(vec3 position, vec3 center, float radius) {
    return distance(position, center) - radius;
}
float sdfEllipsoid(vec3 position, vec3 center, vec3 radii) {
    position -= center;
    float k0 = length(position/radii);
    float k1 = length(position/(radii*radii));
    return k0*(k0-1.0)/k1;
}
float sdfEllipsoidRotated(vec3 position, vec3 center, vec3 radii, vec3 rotation) {
	position -= center;
    position = rotate3D(position, rotation);
    float k0 = length(position/radii);
    float k1 = length(position/(radii*radii));
    return k0*(k0-1.0)/k1;
}
float sdfPlane( vec3 position, vec4 n ) {
    return dot(position, normalize(n.xyz)) + n.w;
}
float sdfRoundBoxRotated(vec3 position, vec3 center, vec3 box, vec3 rotation, float radius) {
    position -= center;
    position = rotate3D(position, rotation);
    vec3 q = abs(position) - box;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - radius;
}
float dot2(vec2 v) {
	return dot(v, v);
}
vec3 bendSpaceZ (vec3 position, float degree) {
    //position = rotate3D(position, vec3(0.0, PI/2.0, 0.0));
    float k = degree;
    float c = cos(k*position.y);
    float s = sin(k*position.y);
    mat2  m = mat2(c,-s,s,c);
    vec2  q = m*position.xy;
    return vec3(q, position.z);
}
vec4 sdfJoint3DSphere(vec3 position, vec3 start, vec3 rotation, float len, float angle, float thickness) {
    vec3 p = position;
    float l = len;
    float a = angle;
    float w = thickness;
    p -= start;
    p = rotate3D(p, rotation);

    if( abs(a)<0.001 ) {
        return vec4( length(p-vec3(0,clamp(p.y,0.0,l),0))-w, p );
    }

    vec2  sc = vec2(sin(a),cos(a));
    float ra = 0.5*l/a;
    p.x -= ra;
    vec2 q = p.xy - 2.0*sc*max(0.0,dot(sc,p.xy));
    float u = abs(ra)-length(q);
    float d2 = (q.y<0.0) ? dot2( q+vec2(ra,0.0) ) : u*u;
    float s = sign(a);
    return vec4( sqrt(d2+p.z*p.z)-w,
               (p.y>0.0) ? s*u : s*sign(-p.x)*(q.x+ra),
               (p.y>0.0) ? atan(s*p.y,-s*p.x)*ra : (s*p.x<0.0)?p.y:l-p.y,
               p.z );
}
float smin(float d1, float d2, float k) {
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}
float smax(float d1, float d2, float k) {
    float h = max(k-abs(d1-d2),0.0);
    return max(d1, d2) + h*h*0.25/k;
}

vec2 aangHead(vec3 position) {
	vec3 symPosX = vec3(abs(position.x), position.yz);
    float material = 1.0;
    float d, d1, d2;

    // Basic head sphere
    d = sdfEllipsoid(position, vec3(0.0), vec3(0.4, 0.5, 0.5));
    // basic jaw
    d1 = sdfRoundBoxRotated(position, vec3(0.0, -0.3, -0.1), vec3(0.08, 0.2, 0.20),
                            vec3(0.0), 0.2);
    d2 = sdfRoundBoxRotated(position, vec3(0.0, -0.90, 0.1), vec3(0.5, 0.8, 0.3),
                            vec3(PI/2.5, 0.0, 0.0), 0.0);
    d1 = smax(d1, -d2, 0.3);
    d2 = sdfRoundBoxRotated(symPosX, vec3(0.30, -0.56, -0.40), vec3(0.38, 0.45, 0.08),
                            vec3(-0.6, -1.3, -0.2), 0.0);
    //d1 = smin(d1, d2, 0.1);
    d1 = smax(d1, -d2, 0.15);
    d2 = sdfRoundBoxRotated(symPosX, vec3(0.35, -0.56, -0.50), vec3(0.58, 0.53, 0.18),
                            vec3(-0.0, -1.1, -0.0), 0.0);
    //d1 = smin(d1, d2, 0.1);
    d1 = smax(d1, -d2, 0.1);
    d = smin(d, d1, 0.1);
    // sculpt head
    d1 = sdfRoundBoxRotated(symPosX, vec3(0.51, 0.0, 0.0), vec3(0.07, 1.0, 1.0),
                            vec3(0.0, 0.0, -0.1), 0.01);
    d = smax(d, -d1, 0.1);
    // ears
    d1 = sdfEllipsoidRotated(symPosX, vec3(0.38, -0.15, 0.1), vec3(0.12, 0.15, 0.03),
                             vec3(-0.3, -PI/9.9,- 0.3));
    d2 = sdfSphere(symPosX, vec3(0.43, -0.15, 0.1), 0.001);
    d1 = smax(d1, -d2, 0.1);
    d = smin(d, d1, 0.01);

    // eye sockets
    d1 = sdfRoundBoxRotated(position, vec3(0.0, -0.04, -0.57), vec3(0.2, 0.1, 0.1),
                            vec3(PI/4.0, 0.0, -0.0), 0.01);
    d = smax(d, -d1, 0.2);
    // nose
    d1 = sdfRoundBoxRotated(position, vec3(0.0, -0.12, -0.45), vec3(0.005, 0.12, 0.05),
                            vec3(0.7, 0.0, 0.0), 0.02);
    d = smin(d, d1, 0.1);
    d1 = sdfRoundBoxRotated(position, vec3(0.0, -0.22, -0.53), vec3(0.05, 0.005, 0.05),
                            vec3(0.0, PI/4.0, 0.0), 0.0);
    d = smin(d, d1, 0.1);
    // mouth
    d1 = sdfEllipsoid(vec3(symPosX.x, symPosX.y-4.9*pow(symPosX.x, 2.3), symPosX.z),
                      vec3(0.0, -0.42, -0.5), vec3(0.15, 0.07, 0.6));
    d = smax(d, -d1, 0.01);
    d1 = sdfSphere(position, vec3(0.0, -0.42, -0.35), 0.13);
    if (d1 < d) {
        material = 6.0;
    }
    d = smin(d, d1, 0.01);
    d1 = sdfJoint3DSphere(position, vec3(0.08, -0.36, -0.45),
                          vec3(PI/2.0, -PI/1.4, 0.0),
                          0.2, PI/4.0, 0.01).x;
    if (d1 < d) {
        material = 2.0;
    }
    d = smin(d, d1, 0.01);
    d1 = sdfJoint3DSphere(position, vec3(0.08, -0.47, -0.45),
                          vec3(PI/2.0, -PI/1.4, 0.0),
                          0.2, PI/4.0, 0.01).x;
    if (d1 < d) {
        material = 2.0;
    }
    d = smin(d, d1, 0.01);
    // cheeks
    d1 = sdfSphere(symPosX, vec3(0.2, -0.22, -0.35), 0.05);
    d = smin(d, d1, 0.2);
    // eyes
    d1 = sdfSphere(symPosX, vec3(0.15, -0.07, -0.35), 0.11);
    if (d1 < d) {
        material = 2.0;
    }
    d = smin(d, d1, 0.01);
    d1 = sdfSphere(symPosX, vec3(0.15, -0.07, -0.41), 0.06);
    if (d1 < d) {
        material = 3.0;
    }
    d = smin(d, d1, 0.01);
    d1 = sdfSphere(symPosX, vec3(0.15, -0.07, -0.445), 0.03);
    if (d1 < d) {
        material = 4.0;
    }
    d = smin(d, d1, 0.01);
    // eye brows
    d1 = sdfJoint3DSphere(symPosX, vec3(0.1, 0.13+0.01*sign(position.x), -0.455),
                          vec3(0.0, -0.9, -PI/3.2),
                          0.2, 0.9, 0.01).x;
    if (d1 < d) {
        material = 5.0;
    }
    d = smin(d, d1, 0.01);

    return vec2(d, material);
}

int calculateArrow(vec3 position) {
    // To calculate the arrow, we take the origin as 0. If facing back (z=1)
    // is 0, we trace a circle on the yz plane, and calculate the angle made
    // we use that to calculate the stem of the arrow, and the position of the
    // pointyhead.
    if (abs(position.x) > 0.20) {
        return 0;
    }
    float angle = atan(position.y, position.z);
    float maxAngle = PI-0.2;
    float arrowAngle = PI/6.0;
    if (-PI/2.0 < angle && angle < maxAngle) {
        // pointy head
        if (angle > maxAngle-arrowAngle) {
            float rem = (angle-(maxAngle-arrowAngle)) / arrowAngle;
            rem = -1.0 * (rem-1.0);
            if (abs(position.x) < 0.20*rem) {
                return 1;
            }
        } else if (abs(position.x) < 0.11) {
            return 1;
        }
    }
    return 0;
}

vec4 distanceField(vec3 position) {
	vec2 d = aangHead(position);
    return vec4(d, 0.0, 0.0);
}

vec3 calcNormal(vec3 p) {
    // We calculate the normal by finding the gradient of the field at the
    // point that we are interested in. We can find the gradient by getting
    // the difference in field at that point and a point slighttly away from it.
    const float h = 0.0001;
    return normalize( vec3(
        			       -distanceField(p).x+ distanceField(p+vec3(h,0.0,0.0)).x,
                           -distanceField(p).x+ distanceField(p+vec3(0.0,h,0.0)).x,
                           -distanceField(p).x+ distanceField(p+vec3(0.0,0.0,h)).x
    				 ));
}

vec4 raymarch(vec3 direction, vec3 start) {
    // We need to cast out a ray in the given direction, and see which is
    // the closest object that we hit. We then move forward by that distance,
    // and continue the same process. We terminate when we hit an object
    // (distance is very small) or at some predefined distance.
    float far = 15.0;
    vec3 pos = start;
    float d = 0.0;
    vec4 obj = vec4(0.0, 0.0, 0.0, 0.0);
    for (int i=0; i<100; i++) {
    	obj = distanceField(pos);
        float dist = obj.x;
        pos += dist*direction;
        d += dist;
        if (dist < 0.01) {
        	break;
        }
        if (d > far) {
        	break;
        }
    }
    return vec4(d, obj.yzw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalise and set center to origin.
    vec2 p = fragCoord/iResolution.xy;
    p -= 0.5;
    p.y *= iResolution.y/iResolution.x;

    float mouseX = ((iMouse.x/iResolution.x)-0.5) * 2.0 * 3.14159/2.0;
    mouseX = -0.3;
    mouseX = 0.4*sin(iTime/3.6);
    vec3 cameraPosition = vec3(0.0, 0.0, -3.0);
    vec3 planePosition = vec3(p, 1.0) + cameraPosition;

    mat2 camRotate = mat2(cos(mouseX), -sin(mouseX), sin(mouseX), cos(mouseX));
    cameraPosition.xz = camRotate * cameraPosition.xz;
    planePosition.xz = camRotate * planePosition.xz;

    float yRotate = 0.1;
    yRotate = 0.2*sin(iTime/4.2);
    camRotate = mat2(cos(yRotate), -sin(yRotate), sin(yRotate), cos(yRotate));
    cameraPosition.yz = camRotate * cameraPosition.yz;
    planePosition.yz = camRotate * planePosition.yz;

    vec3 lookingDirection = (planePosition - cameraPosition);

    // This was fun to sort out, but is it the best way?
    float lightTime = iTime/3.0;
    float multiplier = -1.0 + (step(-0.0, sin(lightTime*3.14159)) *2.0);
    float parabola = (4.0 * fract(lightTime) * (1.0-fract(lightTime)));
    float lightX = multiplier*parabola *-1.2;
    vec3 lightPoint = normalize(vec3(lightX, 1.0, -1.0));
    vec3 lightFacing = lightPoint - vec3(0.0);
    // lightFacing = vec3(1.0, 1.0, -0.3) - vec3(0.0);

    // raymarch to check for colissions.
    vec4 obj = raymarch(lookingDirection, planePosition);
    float dist = obj.x;
    vec3 color = vec3(0.01);
    if (dist < 15.0) {
        vec3 normal = calcNormal(planePosition+ dist*lookingDirection);
        int arrow = calculateArrow(planePosition+ dist*lookingDirection);
        float light = dot(lightFacing, normal);
        light = max(light, 0.0);
        if (obj.y < 1.5) {
            // skin
        	color = vec3(0.505, 0.205, 0.105);
            color += 0.3* smoothstep(0.1, 1.0, light);
            if (arrow==1) {
        	    color = vec3(0.21, 0.21, 0.31);
                color += 0.1* smoothstep(0.1, 1.0, light);
            }
        } else if (obj.y < 2.5) {
            //eyes
        	color = vec3(0.75, 0.75, 0.85);
            color += 0.1 * smoothstep(0.5, 1.0, light);
        } else if (obj.y < 3.5) {
        	color = vec3(0.21, 0.21, 0.31);
            color += 0.7 * smoothstep(0.4, 1.0, pow(light, 5.0));
        } else if (obj.y < 4.5) {
        	color = vec3(0.01);
            color += 0.5 * smoothstep(0.4, 1.0, pow(light, 5.0));
        } else if (obj.y < 5.5) {
            // eyebrows
        	color = vec3(0.05, 0.02, 0.01);
            color += 0.05 * smoothstep(0.4, 1.0, pow(light, 5.0));
        } else if (obj.y < 6.5) {
            // mouth
        	color = vec3(0.15, 0.02, 0.01);
            color += 0.1 * smoothstep(0.4, 1.0, pow(light, 5.0));
        }
    }
    // gamma correction
    color = pow( color, vec3(1.0/2.2) );
    fragColor = vec4(color,1.0);
}

#define S(a, b, t) smoothstep(a, b, t)

float distLine(vec2 p, vec2 a, vec2 b){
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*t);
}

float line(vec2 p, vec2 a, vec2 b){
	float d = distLine(p, a, b);
    float m = S(0.03, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.2, 0.8, d2) * 0.5 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float distTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float triangle(vec2 p, vec2 a, vec2 b, vec2 c){
	float d = distTriangle(p, a, b, c);
    float m = S(0.03, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.2, 0.8, d2) * 0.5 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float N21(vec2 p){
	p = fract(p * vec2(233.34, 851.73));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p){
	float n = N21(p);
    return vec2(n, N21(p + n));
}

vec2 getPos(vec2 id, vec2 offset){
    vec2 n = N22(id + offset) * iTime;
    return offset + sin(n) * 0.4;
}

float layer(vec2 uv){
	vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec2 p[9];
    int i = 0;
    for(float y = -1.0; y <= 1.0; y++){
    	for(float x = -1.0; x <= 1.0; x++){
        	p[i++] = getPos(id, vec2(x, y));
    	}    
    }


    float t = iTime * 10.0;
    float m = 0.0;
    for(int i = 0; i < 9; i++){
    	m += line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 20.0;
        float sparkle = 1.0 / dot(j, j);

        m += sparkle * (sin(t + fract(p[i].x) * 10.0) * 0.5 + 0.5);

        for(int yi= i + 1; yi < 9; yi++){
    		for(int zi= yi + 1; zi < 9; zi++){

                float len1 = abs(length(p[i] - p[yi]));
                float len2 = abs(length(p[yi] - p[zi]));
                float len3 = abs(length(p[i] - p[zi]));

                if((len1 + len2 + len3) < 2.8){
                	m += triangle(gv, p[i], p[yi], p[zi]) * 0.8;
                }
    		}
    	}
    }
    m += line(gv, p[1], p[3]);
    m += line(gv, p[1], p[5]);
    m += line(gv, p[7], p[3]);
    m += line(gv, p[7], p[5]);

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float m = 0.0;
    float t = iTime * 0.1;

    float gradient = uv.y;

    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2(c, -s, s, c);
    uv *= rot;

    for(float i = 0.0; i < 1.0; i += 1.0 / 4.0){
        float z = fract(i + t);
        float size = mix(10.0, 0.5, z);
        float fade = S(0.0, 0.5, z) * S(1.0, 0.8, z);

        m += layer(uv * size + i * 20.0) * fade;
    }


    vec3 base = sin(t * 5.0 * vec3(0.345, 0.456, 0.567)) * 0.4 + 0.6;
    vec3 col = m * base;

    col -= gradient * base;

    //vec2 gv = fract(uv) - 0.5;
    //if(gv.x > 0.48 || gv.y > 0.48){
    //	col = vec3(1.0, 0.0, 0.0);
    //}

    fragColor = vec4(col,1.0);
}

#define time .7 * iTime
#define ZPOS -10.

float PI = acos(-1.);

float wave(float tempo) {
    return .5 * sin(time * tempo) + .5;
}

mat2 rot2d(float a) {
    float c = cos(a), s = sin(a);

    return mat2(c, s, -s, c); 
}

vec3 kifs(vec3 p) {
    float s = 1.;
//    float t = floor(time) + smoothstep(.0, 1., fract(time));
  	float t = time * .3;
    for (float i = 0.; i < 1.; i++) {
        p.xy *= rot2d(t);
        p.yz *= rot2d(.9 * t + i * .7);
        p = abs(p);
        p -= s;
//        s *= .6 + .2 * sin(time / 1.5);
        s *= .7;
    }

    return p;
}


float sphere(vec3 p, float r) {
    return length(p) - r;
}

float tube(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ab = b - a;
    vec3 ap = p - a;
    float t = dot(ab, ap) / dot(ab, ab);
//    t = clamp(t, 0., 1.);
    vec3 c = a + ab * t;

    return length(p - c) - r;
}

vec3 rep(vec3 p, vec3 r) {
    vec3 q = mod(p, r) - .5 * r;

    return q;
}

float at = 0.;
float map(vec3 p) {
//    p.xy *= rot2d(cos(time) * sin(p.z / 3.));
    vec3 rawP = p;
//    p = kifs(p);
    p = rep(p, vec3(1. + wave(.65), 2. + wave(.6), 1.5 + wave(.7)));

    float d = 5000.;

//    d = min(d, sphere(p, 1.));
    d = min(d, tube(p, vec3(0, -2, 0), vec3(0, 2, 0), .1));
    d = min(d, tube(p, vec3(-2, 0, 0), vec3(2, 0, 0), .1));
    d = min(d, tube(p, vec3(0, 0, 2), vec3(0, 0, -2), .1));
//    d = max(d, -sphere(rawP, 10.));

    at += .05 / (.1 + 5. * d);

    return d;
}

vec3 glow = vec3(0);
float rm(vec3 ro, vec3 rd) {
    float d = 0.;

    for (int i = 0; i < 300; i++) {
        vec3 p = ro + d * rd;
        float ds = map(p);

        if (ds < 0.01 || ds > 100.) {
            break;
        }

        d += ds * .5;
//        glow += .015 * at * vec3(.8, .5 * sin(time / 1.) + .5, .5 * cos(time / 5.) + .5);
        glow += .001 * at * vec3(.8, wave(.5) * cos(.1 * p.z), 0);
    }

    return d;
}

vec3 normal(vec3 p){
    vec2 e = vec2(0.01, 0);

    vec3 n = normalize(map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
    ));

   return n;                 
}

float light(vec3 p) {
    vec3 lp = vec3(0, 0, ZPOS);
    vec3 tl = lp - p;
    vec3 tln = normalize(tl);
    vec3 n = normal(p);
    float dif = dot(n, tln);
    float d = rm(p + .01 * n, tln);

    if (d < length(tl)) {
        dif *= .1;
    }

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0, 0, ZPOS);
    vec3 rd = normalize(vec3(uv, 1.));
    float d = rm(ro, rd);
	vec3 p = ro + d * rd;
    float dif = light(p);

    vec3 col = dif * glow;
//    vec3 col = vec3(dif);

    // Output to screen
    fragColor = vec4(col,1.0);
}

//inspired by https://www.shadertoy.com/view/3sSGWt
//which is made by Art of Code, teaching about how to make hexagonal tiling
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;

    uv *=8.;

   // uv.y+=0.01*sin(fract(212.*uv.y)*231.);
    vec2 offset = vec2(1.7,1.);
    //creating 2 grid that offset with each other
    vec2 st1 = mod(uv,offset)-vec2(0.5*offset.x,0.);
    vec2 st2 = mod(uv+0.5*vec2(offset.x,-offset.y),offset)-vec2(0.5*offset.x,0.);
    st1.x=abs(st1.x);
    st2.x=abs(st2.x);
    float a= length(st1);
    float b= length(st2);
    vec2 gv;//this will record the vector which grid this pixel belongs to
    vec2 st;//this will translate the Cartesian coordinate system into polor coordinate system
    int i=0;
    float r=1.;

    st1.x*=0.5*abs(sin(iTime*1.5))+0.5;
    st2.x*=-0.5*abs(sin(iTime*1.5))+1.1;

    //the grid is now created and distinguished
    if (b>r){
    	gv=vec2(atan(st1.y,st1.x),a);
        st= st1;
    }
    else{ 
        if (atan(st2.y,st2.x)<3.14/6.||((a<r)&&(st2.y<0.5)))
        {
            gv=vec2(atan(st1.y,st1.x),a);
            st=st1;
        }
        else{
        	gv=vec2(atan(st2.y,st2.x),b);
            i=1;
            st=st2;
        }
    }

    //try to figure out the mouse position and its belonging grid id
    vec2 mouse =(iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 mid= floor(mouse/offset);
    vec2 id= floor((uv)/offset);
    if (i==1){
        mid = floor((mouse)/offset-0.5)+vec2(0.5);
    	id =floor((uv)/offset-0.5)+vec2(0.5);
    }

    id-=mouse;

    //applying color onto the grid
    vec4 col = 1.0*texture(iChannel0,vec2(gv.x,gv.y*5.)-vec2(0.,0.3*iTime)+id*3.7)+0.05+(id.x+id.y)*0.05;
    float s=fract(id.x)-0.2;
    col*=vec4(0.6+s*0.5,0.6+s*0.3,0.7-s*0.3,1.)*1.2*sqrt(abs(sin(0.1*iTime))*smoothstep(0.5,1.0,gv.x)+1.4*abs(cos(0.1*iTime))*smoothstep(0.0,0.6,gv.y))+.3;


    //if you want to see the grid, uncomment the following sentence
    // col=vec4(id*0.2+0.5,0.,1.);
    // Output to screen
    fragColor = col;
}

