float det = .001;
vec3 objcol=vec3(0.);
float snd;


mat2 rot(float a) {
	float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

float fractal(vec3 p) {
    vec3 c = p;
    float m = 100.;
    for(int i = 0; i < 10; i++) {
		p=abs(p + 1.) - abs( p - 1.) - p;
        p=p / clamp(dot(p,p), 0.1, 1.) - c;
    	m=min(m, abs(length(p) - 3.5));
    }
    m = max(0., 1. - m);
    objcol = abs(p) * .6;
    return m * m * (.2+snd);
}

float de(vec3 p) {
    p.yz *= rot(iTime);
    p.xz *= rot(.2);
    float f = fractal(p);
	float d = length(p) - 3.5 + f * 1.5 - snd * 3.;
    d = min(d, length(p.xy) - .25);
	d = min(d, length(p.yz) - .25);
	d = min(d, length(p.xz) - .25);
    return (d - f * .5) * .5;
}

vec3 march(vec3 from, vec3 dir) {
	vec3 col = vec3(0.), p;
    float td = 0., d;
    for (int i = 0; i < 150; i++) {
    	p = from + td * dir;    
		d = de(p);
        td += max(det, abs(d));
		if (td > 20.) break;
        col += objcol * exp(-.005 * td * td);
    }
    vec3 rescol = col * .01 * (.3 + snd * 1.5);
    return rescol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    snd = texture(iChannel0,vec2(.15)).r;
    vec2 p = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;
    vec3 dir = normalize(vec3(p, .7));
    float t = iTime*.5;
    vec3 from = vec3(sin(t) * 2., 0., -10.);
    from.xz *= rot(t);
    dir.xz *= rot(t);
	det += max(0. ,5. - iTime)*.02;
    vec3 col = march(from, dir);
    col += pow(abs(.5 - fract(dir.x * 20.)) * 2., 10.) * .2;
    col += pow(abs(.5 - fract(dir.y * 20.)) * 2., 10.) * .2;
    float spec = pow(texture(iChannel0, vec2(floor(dir.x*20.)/20. * .5 + .5, .1)).r,2.);
	vec2 sq = abs(.5-fract(dir.xy*20.));
    col+=abs(1.-length(max(vec2(0.),abs(sq.xy)-.1)))*step(dir.y+.4,spec)*length(fract(dir.xy*10.))*.4;
    col=max(col, vec3(.5, .2, 0.) - smoothstep(0.,.03,abs(dir.y - spec + .35)));
    fragColor = vec4(col, 1.0);
}

#define INFINITY 100.0
#define EPSILON 0.01
#define PRECIS 32
#define AA 8
#define PI 3.14159

struct HIT
{
    vec3 point;
    float dist;
};

float plane(vec3 p)
{
    return p.y;
}

float world(in vec3 p, in float t)
{
    float plane1 = plane(p);

    return plane1;
}

HIT trace(in vec3 ro, in vec3 rd, in float t)
{
    vec3 p = ro;
    for(int i=0; i<PRECIS; i++)
    {
        float d = world(p,t);

        if(d >= INFINITY)
            return HIT(p, INFINITY);

        p += rd * d;

        if(d <= EPSILON)
            break;
    }

    return HIT(p, length(p-ro));
}

vec3 material(in vec3 p)
{
    const float SIZE = 0.5;
    bool inx = mod(p.x*SIZE, 1.0) > 0.5;
    bool inz = mod(p.z*SIZE, 1.0) > 0.5;
    bool ing = inx ^^ inz;

    return vec3(0.4) * (ing ? 1.2 : 1.0);
}

vec3 image(in vec2 uv, in float t)
{
    float tt = 2.0*sin(t*0.9);

	vec3 ro = vec3(cos(tt)*15.0, 10.0, 9.0*-sin(tt));
    vec3 ta = vec3(0., 0., 0.);
    vec3 up = normalize(vec3(0.5+0.5*sin(t), 1., 0.));
    vec3 cw = normalize(ta-ro);
    vec3 cu = normalize(cross(cw,up));
    vec3 cv = normalize(cross(cu,cw));
    mat3 cam = mat3(cu,cv,cw);
	vec3 rd = cam * normalize(vec3(uv, 2.0));

	HIT hit = trace(ro, rd, t);
	float d = hit.dist;
    vec3 p = hit.point;

    vec3 ambient = vec3(0.025,0.05,0.1);

	if(d < INFINITY)
	{
        vec3 albedo = material(p);

        float diff = 1.0;

        float fog = smoothstep(0.0, 1.0, d*0.015);

	    vec3 pc = ambient + albedo*diff - fog;
		pc = max(ambient, pc);

	    return clamp(pc, 0., 1.);
	}
    else
    {
        return ambient;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize UV and aspect correct
    vec2 uv = fragCoord/iResolution.xy;

    // Offset UV to -1...1
    uv *= 2.;
    uv -= 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec3 c = vec3(0.);

    for(int m=0; m<AA; m++)
        for(int n=0; n<AA; n++)
        {
            // AA offset
            vec2 o = vec2(float(m),float(n)) / float(AA) / iResolution.xy * 2.0;
            vec2 ouv = uv+o;

            // Motion blur time-offset
            float t = iTime - texture(iChannel0, ouv+vec2(m,n)/float(AA)).r * .03;

            // Capture image
			c += image(ouv, t);
        }

    // Output to screen
    c /= float(AA * AA);
    //c = pow(c,vec3(0.8));
    fragColor = vec4(c,1.0);
}

float hash2(vec2 val)
{
    return fract(cos(val.x*12.45252)*55346.325+sin(val.y)*43758.5453123);
}

float poly(float x, float a, float b, float c, float d)
{
    return a*x*x*x+b*x*x+c*x+d;
}

float cubic(float x, float A, float B, float C, float D)
{
    float d = B;
    float c = (C-A)/2.;
    float a = (D-B)/2. - 2.*C + c + 2.*d;
    float b = C - a - c - d;

    return poly(x, a, b, c, d);
}

float bicubic(vec2 p, mat4 data)
{
    float A = cubic(p.y, data[0][0], data[0][1], data[0][2], data[0][3]);
    float B = cubic(p.y, data[1][0], data[1][1], data[1][2], data[1][3]);
    float C = cubic(p.y, data[2][0], data[2][1], data[2][2], data[2][3]);
    float D = cubic(p.y, data[3][0], data[3][1], data[3][2], data[3][3]);
	return cubic(p.x, A, B, C, D);
}

float bicubicNoise(vec2 p)
{
    vec2 pos = floor(p);
    vec2 dpos = p-pos;

    mat4 dat;
    dat[0] = vec4(hash2(pos+vec2(-1., -1.)), hash2(pos+vec2(-1., 0.)),hash2(pos+vec2(-1., 1.)),hash2(pos+vec2(-1., 2.)));
    dat[1] = vec4(hash2(pos+vec2(0., -1.)), hash2(pos+vec2(0., 0.)),hash2(pos+vec2(0., 1.)),hash2(pos+vec2(0., 2.)));
    dat[2] = vec4(hash2(pos+vec2(1., -1.)), hash2(pos+vec2(1., 0.)),hash2(pos+vec2(1., 1.)),hash2(pos+vec2(1., 2.)));
    dat[3] = vec4(hash2(pos+vec2(2., -1.)), hash2(pos+vec2(2., 0.)),hash2(pos+vec2(2., 1.)),hash2(pos+vec2(2., 2.)));

    return bicubic(dpos, dat);
}

float noise(vec2 p)
{
    p *= 4.;
    float H = 0.9;
    float G = pow(2., -H);
    float a = 1.;
    float f = 1.;
    float t = 0.;

    for(int i = 0; i < 3; i++)
    {
        t += a * bicubicNoise(p*f);
        a *= G;
        f *= 2.;
    }
    return t*0.5;
}


