#define time iTime
#define ZPOS -130.

float PI = acos(-1.);

mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

float sph(vec3 p, float r) {
  return length(p) - r;
}

float wave(float t, float a) {
    return a * (.5 * sin(time) + .5);
}

vec3 kifs(vec3 p, float r, float s, float tf, float it) {
    float t = tf * time;
    for (float i = 0.; i < it; i++) {
        p.xy *= rot2d(t * .6);
        p.yz *= rot2d(t * .7 - i);
        p = abs(p);
        p -= s;
        s *= r;
    }

    return p;
}

float tube(vec3 p, vec3 a, vec3 b, float r) {
  vec3 ab = b - a;
  vec3 ap = p - a;
  float t = dot(ab, ap) / dot(ab, ab);
  t = clamp(t, 0., 1.);
  vec3 c = a + ab * t;

  return length(p - c) - r;
}

vec3 rep(vec3 p, vec3 r) {
  vec3 q = mod(p, r) - .5 * r;

  return q;
}

float at = 0.;
float map(vec3 p) {
    p = kifs(p, .8, 3., .5, 2.);
    p = kifs(p, .5, 2. + length(p) * .3, .5, 4.);
    float d = 5000.;

    float obj = tube(p, vec3(-2, 0, 0), vec3(2, 0, 0), 1.);
	obj = min(obj, tube(p, vec3(0, -2, 0), vec3(0, 2, 0), 1.));
	obj = min(obj, tube(p, vec3(0, 0, -2), vec3(0, 0, 2), 1.));
    obj = max(obj, sph(p, 2.));
    obj = mix(obj, sph(p, 1.), wave(1., 1.));
    d = min(d, obj);

    at += .05 / (.2 + d);

    return d;
}

vec3 glow = vec3(0, 0, 0);
float rm(vec3 ro, vec3 rd) {
  float d = 0.;

  for (int i = 0; i < 100; i++) {
    vec3 p = ro + d * rd;
    float ds = map(p);

    if (ds < 0.01 || ds > 100.) {
      break;
    }

    d += ds * .8;
    glow += .002 * at * vec3(.5, .3 + wave(.5, .4), 0.);
  }

  return d;
}

vec3 normal(vec3 p) {
  vec2 e = vec2(0.01, 0);

  vec3 n = map(p) - vec3(
    map(p - e.xyy),
    map(p - e.yxy),
    map(p - e.yyx)
  );

  return normalize(n);
}

float light(vec3 p) {
  vec3 lp = vec3(2, 5, ZPOS);
//  lp.xz *= rot2d(time);
  vec3 tl = lp - p;
  vec3 tln = normalize(tl);
  vec3 n = normal(p);
  float dif = dot(n, tln);
  float d = rm(p + .01 * n, tln);

  if (d < length(tl)) {
    dif *= .1;
  }

  return dif;
}


