//iq's SDFs
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float addCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float addCube(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.))-.08;
}

float addSphere(vec3 p,vec3 spherePos, float r){

   float sphereDistance = length(p - spherePos.xyz)-r;   

    return sphereDistance;
}


vec2 opUnion(vec2 r1, vec2 r2){
    return r1.x < r2.x ? r1 : r2;
}

vec2 mapScene(vec3 p){
    vec2 res;

    vec3 spherePos = vec3(0,3.8,1);
    float sphere = addSphere(p,spherePos,.6);
    res = vec2(sphere,1.0);

    float baseCube = addCube(p-baseCubePos,vec3(2.1,.5,2.1))-.2;
   	res = opUnion(res,vec2(baseCube,3.0));

    vec3 polePos = baseCubePos + vec3(0,2,0);
    float pole = addCylinder(p-polePos,.3,1.2);
    res = opUnion(res,vec2(pole,7.0));

    vec3 buttomPos = p -baseCubePos + vec3(-1.39,-.6,-1.2);
   // buttom.yz *=Rot(2000.*1.57);
    float prisma = sdRoundedCylinder( buttomPos,.35,.35,.01);
    res = opUnion(res,vec2(prisma,4.));



        vec3 flagPos = p - baseCubePos + vec3(-1.8,-4.6,.6);
	vec3 uv = flagPos;
       //Wave from https://www.shadertoy.com/view/3lyGRd
         float w = sin((uv.x - uv.y*uv.y - iTime * 2.75 + sin(4.5 * uv.x + 4.5 * uv.y) * PI * .3)
                  * PI * .6); 
              p.xy *= 1. + (.036 - .036 * w);
        uv *= 1. + (.036 - .036 * w);

   float flag = addCube(uv,vec3(1.7,.7,.001))*.4;
    res = opUnion(res,vec2(flag,5.));

    res = opUnion(res,vec2(p.y,1.1));

    return res;
}


vec2 rayMarch(vec3 ro,vec3 rd){
    float dO = 0.;
    vec2 d;
    for(int i=0;i<100;i++){
     vec3 p = ro + rd*dO;
        d = mapScene(p);


        dO+=d.x;
        if(d.x >20.0|| d.x<.01)
            break;

    }
        float id=d.y;
        float dist = dO;

    return vec2(dist,id);
}

vec3 getNormal(vec3 p){

    vec2 d = mapScene(p);

    vec2 e= vec2(.01,.0);

    vec3 normal = d.x -vec3(mapScene(p-e.xyy).x,
                          mapScene(p-e.yxy).x,
                          mapScene(p-e.yyx).x);


    return normalize(normal);
}    

float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 18;
    const float delta = 1.5;

    float a = 0.0;
    float weight = .5;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - mapScene(p + n*d).x);
        weight *= 0.6;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

