    float val = inner * outer;

    vec3 col0 = vec3(1, 0, 0);
    vec3 col1 = vec3(0, 1, 0);
    vec3 colMix = mix(col0, col1, sin(uv.y * 5. + iTime * .5) * 0.5 + 0.5);

    vec3 col = mix(colMix, vec3(1.), val);

    float blink = 1. - step(sin(iTime * 2.), -0.9) * (sin(iTime * 50.) * 0.5 + 0.5);


    col = col * val * blink;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI  3.14159265359f
#define PI2 6.28318530718f

const int FISH_COUNT = 12;
const float INV_FISH_COUNT = 1.0f / float(FISH_COUNT);
const vec3 BASE_COLOR = vec3(1.0f, 1.0f, 1.0f);
const vec4 BACKGROUND_COLOR = vec4(0.0f, 0.5f, 0.7f, 0.0f);


struct FishData
{
	vec4  color;
    float sdf;
};


// https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float nsin(float a)
{
	return sin(a) * 0.5f + 0.5f;
}

float osin(float a)
{
	return (max(-0.3f, sin(a)) + 0.4f) / 1.4f;
}

float st(float sdf, float scale)
{
	float w = (1.0f / iResolution.y) * scale;
	return smoothstep(w, 0.0f, sdf);
}

float st(float sdf)
{
	float w = (1.0f / iResolution.y) * 1.6f;
	return smoothstep(w, 0.0f, sdf);
}

float circle(vec2 uv, float radius)
{
	return length(uv) - radius; 
}

float edge(vec2 uv, vec2 a, vec2 b, vec2 center)
{    
    vec2 ab = normalize(b - a);
    vec2 normal = vec2(ab.y, -ab.x);
    vec2 middle =  a + ab * distance(a, b) * 0.5f;
    vec2 cm = normalize(middle - center);

    float flip = dot(normal, normalize(cm));
    float angle = atan(ab.y, ab.x);

    float x = cos(angle);
    float y = sin(angle);

    uv = (uv - middle) * mat2(x, y, y, -x);

    return uv.y * -flip;
}


float triangle(vec2 uv, vec2 a, vec2 b, vec2 c, float roundness)
{    
    vec2 center = (a + b + c) * 0.3333333f;

	float edge1 = edge(uv, a, b, center);
    float edge2 = edge(uv, b, c, center);
    float edge3 = edge(uv, c, a, center);

    return smin(edge1, smin(edge2, edge3, roundness), roundness) * -1.0f;
}

float circlePattern(vec2 uv, vec2 a, vec2 b, int count, float rOffset)
{
    float invCount = 1.0f / float(count);

    float maxSize = 0.03f;
    float minSize = 0.01f;

    float maxOffset =  0.04f;
    float minOffset = -0.04f;

    float maxRound = 0.03f;
    float minRound = 0.0f;

    float dist = distance(a, b);

    vec2 ab = normalize(b - a);

    vec2 right = vec2(ab.y, -ab.x);

    float sdf = 1.0f;

    for(int i = 0; i < count; ++i)
    {
        float alpha = float(i) * invCount;
    	float rand = texture(iChannel0, vec2(alpha + rOffset)).r;

		float size   = mix(minSize,   maxSize,   rand);
        float offset = mix(minOffset, maxOffset, rand);
        float roundness = mix(minRound, maxRound, rand);


        vec2 p = a + ab * dist * alpha + right * offset;

        sdf = smin(sdf, circle(uv - p, size), roundness);

    }

    return (sdf);
}

FishData fish(vec2 uv, float size, float depth, float rand)
{    

    float x = uv.x * 6.0f;
    uv.y += sin(x - iTime) * 0.015f;

    float roundness = size * 0.2f;

    // Tail ----------------

    float tailW = size * 3.f;

    float tailMinHeight = size * 0.1f;
    float tailMaxHeight = size * 0.7f;

    float tailH1 =  mix(tailMinHeight, tailMaxHeight, osin(x - iTime));
    float tailH2 = -mix(tailMinHeight, tailMaxHeight, osin(x - iTime + PI));

    float tail = triangle(uv, vec2(0.0f), vec2(tailW, tailH1), vec2(tailW, tailH2), roundness);


    // Fins ----------------

    float finSize = size * 1.5f;

    float finMoveR = mix(0.0f, size * 0.3f, nsin(x - iTime));
    float finMoveL = mix(0.0f, size * 0.3f, nsin(x - iTime + PI * 0.8f));

    float fins = triangle(uv, vec2(-finSize, 0.0f), vec2(finMoveR, finSize), vec2(finMoveL, -finSize), roundness);

    // Eyes ----------------

    float eyeRadius = size * 0.1f;
    float eyeX = size * 1.6f;
    float eyeY = size * 0.7f;

    float eyeR = circle(uv + vec2(eyeX,  eyeY), eyeRadius);
    float eyeL = circle(uv + vec2(eyeX, -eyeY), eyeRadius);

    float eyes =  st(-min(eyeR, eyeL), 0.5);

    // Body ----------------

    uv.y = uv.y * (uv.x + 0.6f) * 2.0f;
    uv.x *= 0.5f;

	float body = circle(uv, size);


    float sdf = smin(body, tail, roundness);
	sdf = min(sdf, eyes);	
    float bte = sdf;

    sdf =  smin(sdf, fins, roundness);

    float mask = st(sdf);
    float finsMask = (mask - st(bte));
    float bodyMask = st(body);

    float patternMask = circlePattern(vec2(uv.x * 1.6f, uv.y), vec2(0.05f, rand * 0.1f), vec2(-0.2f, -rand * 0.1f), 5, rand);

    patternMask = smin(patternMask, circlePattern( vec2(uv.x * 1.6f, uv.y), vec2(rand * 0.1f, -0.1f), vec2(-rand * 0.2f, 0.1f), 10, rand), 0.03f);
	patternMask = st(patternMask, 1.0f);

    vec3 eyeColor  = vec3(0.0f);
    vec3 bodyColor = mask * BASE_COLOR;
    vec3 patternColor = vec3(1.0f,  rand * 0.5f, 0.0f);
    vec3 finColor  = patternColor * 0.2f + 0.7f;


    bodyColor = mix(bodyColor, patternColor, min(patternMask, bodyMask ));



   	vec3 color = mix(bodyColor, finColor, finsMask);
    color = mix(eyeColor, color, eyes);


    color.rgb = mix(BACKGROUND_COLOR.rgb, color.rgb, depth);

    return FishData(vec4(color, mask), sdf); 
}

FishData movingFish(vec2 uv, float speed, float rotation, float scale, float depth, float rand)
{

    float time = iTime * speed;

    float x = cos(rotation);
    float y = sin(rotation);

    uv *= mat2(x, y, y, -x); 
    uv = (fract(uv + vec2(time * 0.025f, sin(time) * 0.001f + 0.5f)) - 0.5) * scale;


    return fish(uv, 0.1f, depth, rand);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.y /= iResolution.x / iResolution.y;    

    float mask = 1.0f; 

    float maxSize = 2.0f;
    float minSize = 3.5f;

    float maxSpeed = 2.5f;
    float minSpeed = 0.5f;

    float maxOffset = 0.3f;
    float minOffset = -0.3f;

    float minDepth = 0.5f;
    float maxDepth = 0.9f;

    vec4 color = BACKGROUND_COLOR;

    float flip = -1.0f;

    for(int i = 0; i < FISH_COUNT; ++i)
    {
        flip *= -1.0f;

    	float alpha = float(i) * INV_FISH_COUNT;
        float rand = texture(iChannel0, vec2(alpha)).r;

        float speed  = mix(minSpeed, maxSpeed, alpha);
        float size   = mix(minSize, maxSize, alpha);
        float rot    = mix(PI2, 0.0f, alpha) * flip;
        float offset = mix(minOffset, maxOffset, rand) * flip;
        float depth  = mix(minDepth, maxDepth, alpha);

        FishData fish = movingFish(uv + offset, speed, rot, size, depth, rand);


        float fishShadow = smoothstep(0.013f, 0.0f, fish.sdf);

        color.rgb = mix(color.rgb, vec3(0.0f), fishShadow * color.a * 0.2f);
        color.rgb = mix(color.rgb, fish.color.rgb, fish.color.a);

        color.a = max(fish.color.a, color.a);
    }


    fragColor = vec4(color.rgb, 1.0f);
}

float sphere(vec3 ro, vec3 rd, vec4 sph)
{
    vec3 oc = sph.xyz-ro;
    float b = dot(rd, oc);
    float d2 = dot(oc,oc) - b*b;
    float h2 = sph.w*sph.w-d2;
    if (h2 <= 0.0) {
        return -1.0;
    }
    return b - sqrt(h2);
}

int mandelbrot(vec2 z, vec2 c)
{
    int i;
    for(i=0; i<50; i++) {
        if (z.x*z.x+z.y*z.y >= 4.0)
            return i;
        z = vec2(z.x*z.x - z.y*z.y + c.x, 2.*z.x*z.y + c.y);
    }
    return -1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (2.0*fragCoord - iResolution.xy)/min(iResolution.x,iResolution.y);

    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3(xy, 4.0));
    vec4 sph = vec4(0.0, 0.0, 5.0, 1.0);
    vec3 illum = normalize(vec3(1.0, 2.0, -2.0));

    mat3 rot = mat3( 
        vec3(+cos(iTime),  0.0, +sin(iTime)),
        vec3(+0.0,        +1.0, 0.0),
        vec3(-sin(iTime),  0.0, +cos(iTime))
    );

    vec3 col = vec3(float(0.0));
    float d_sph = sphere(ro, rd, sph);
    if (d_sph >= 0.0) {
        vec3 n = normalize(ro + d_sph*rd - sph.xyz);
        vec2 z = vec2(0.0);
        vec3 c3 = rot*n-vec3(0.0,0.0,0.0);
        vec2 c = 1.0*c3.xy/c3.z;
        int i = mandelbrot(z, c);
        vec3 m_col = vec3(0.0, 0.0, 0.0);
        if (i >= 0) {
            m_col = 0.5-0.5*cos(vec3(1.0,2.0,3.0)*float(i));
        }
        float b0 = clamp(dot(illum, n),0.0,1.0);
        vec3 reflect = illum - 2.*n*dot(n,illum);
        float b1 = pow(clamp(dot(reflect,rd),0.0,1.0), 5.0);
        col = m_col*(0.3+b0*0.3)+b1*0.4;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

