#define GOLDEN_RATIO (1.61803398874989484820)
#define GOLDEN_RATIO_MIN_ONE (GOLDEN_RATIO - 1.0)
#define GOOD_COLOR (vec3(0.8, 0.2, 1.0))

#define LINE (0.01666f)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t_second = fract(iTime);

    float x_spark = 1.0f - 
                                ((uv.y - abs(uv.y - t_second) 
                                 / mod(t_second, (GOLDEN_RATIO_MIN_ONE * 0.1f * t_second) 
                                       / (GOLDEN_RATIO_MIN_ONE * t_second))) * 0.5f + 0.5f );

    float y_scanline = smoothstep(0.0f, 0.666f, abs(uv.y - t_second));

    float scanline = LINE / ((LINE*0.666f) + (x_spark * 0.0666f) + y_scanline * GOLDEN_RATIO_MIN_ONE);


    fragColor = vec4(vec3(0.5f, 0.5f, 1.0f) * GOOD_COLOR * scanline, 1.0f);
}

//code for shadertoy.com
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /////////////////////////////////////--VARIABLES
    // In PTB variables will come from the MATLAB 
    // calling code via the vertex shader.
    // See http://psychtoolbox.org/docs/ProceduralShadingAPI
    //
	//
	//base color to blend from
	vec3 baseColor = vec3( 0.5, 0.5, 0.5 );
	//first color
	vec3 color1 = vec3( 1.0, 0.0, 0.0 );
	//second color
	vec3 color2 = vec3( 0.0, 1.0, 0.0 );
	//grating frequency
	float frequency = 0.08;
    //grating phase, here varies using playback time
	float phase = -iTime*4.0;
	// sigma < 0.0  = sinewave grating 
	// sigma == 0.0 = squarewave grating no smoothing
	// sigma > 0.0  = squarewave grating with smoothing in sigma pixels
	float sigma = -1.0;
	//contrast between 0 <-> 1
	float contrast = 1.0;
    // the centre of the viewport
    vec2 center = vec2(iResolution[0]/2.0, iResolution[1]/2.0);
    // the radius of a circular aperture
    float radius = iResolution[1]/2.05;

    ////////////////////////////////////--CODE
    // The actual drawing code...
    //

	// if a pixel is outside the radius, drop it
    if ( radius > 0.0 ) {
        float dist = distance( fragCoord.xy, center );
        if ( dist > radius ) discard;
    }

    //create our sinusoid in -1 to 1 range
	float sv = sin( fragCoord.x * frequency + phase );

	//if sigma >= 0, we want a squarewave grating, step or smoothstep does this depending on sigma value
	if ( sigma == 0.0 ) {
		sv = step( sigma, sv ); //converts into 0-1 range
	}
	else if ( sigma > 0.0 ) {
		sv = smoothstep( -sigma, sigma, sv ); //converts into 0-1 range
	}
	else {
		sv = (sv + 1.0) / 2.0; //simply get sv into 0 - 1 range (preserving sinusoid);
	}

    // start to mix our colors
	vec3 colorA = color1.rgb;
	vec3 colorB = color2.rgb;
	if ( contrast < 1.0 ) { //blend our colours from base colour if contrast < 1
		colorA = mix( baseColor, color1.rgb, contrast );
		colorB = mix( baseColor, color2.rgb, contrast );
	}

	// and then mix our two colors using sv (our position in the grating)
	vec3 colorOut = mix( colorA, colorB, sv );

    // this normalises the color range to a generic 2.2 gamma
    colorOut = pow( colorOut, vec3( 1./2.2 ) );

	// off to the display, byebye little pixel!
	fragColor = vec4( colorOut, 1.0 ); 
}

float distLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*t);
}
float line(vec2 p, vec2 a, vec2 b){
float d = distLine(p,a,b);
float m =smoothstep(0.04,0.007,distLine(p,a,b));

float d2 = length(a-b);
  m *= smoothstep(0.6- .1/(length(p)), .3 , d2)*.2 + smoothstep(.0122,.00121,abs(d2-.75)) ;//* (.25/length((p-a)+(p-b)));
  return m;
}
float n21(vec2 p){
  p = fract(p*vec2(123.213,853.4264));
  p += dot(p,p+6.65);
  return fract(p.x*p.y);

}
vec2 n22(vec2 p){
    float n = n21(p);
    return vec2(n, n21(p+n));
}

vec2 getPos(vec2 id, vec2 offset) {
    vec2 n = n22(id+offset)*iTime;
    return offset+sin(n)*.4;
}
mat2 r(float a){
    float c=cos(a), s=sin(a);
    return mat2(c,-s,s,c);
}
float layer(vec2 uv){

    float m = 0.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 gridPos[9];
    int ppos = 0;
    for(int y = -1; y<=1; y++) {
        for(int x = -1; x<=1; x++) {
            gridPos[ppos++] = getPos(id,vec2(x,y));


        }
    }
    for(int i=0;i<9;i++){
        m+=line(gv,gridPos[4],gridPos[i]);

        vec2 jj = (gridPos[i] - gv)*12.;
        float sparkle = 1./length(dot(jj,jj));
        m+=sparkle*(sin(iTime+ fract(gridPos[i].x) *10.)*.5+.5);
    }
     m+=line(gv,gridPos[1],gridPos[3]);
     m+=line(gv,gridPos[1],gridPos[5]);
     m+=line(gv,gridPos[7],gridPos[3]);
     m+=line(gv,gridPos[7],gridPos[5]);
     return m ;
}
