#define ROT (iTime/50.0)

const float tau = atan(1.0)*8.0;

// http://iquilezles.org/www/articles/palettes/palettes.htm
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}


vec3 color(float t) {
    t = mod(t, 1.0);
    return palette(t, vec3(0.5), vec3(0.5), vec3(1.0, 0.7, 0.4), vec3(0.00, 0.15, 0.20));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;

    uv.x += iTime/100.0;

    vec3 col = vec3(sin(uv.x) + sin(uv.y));
    for(int i=0; i<6; i++) {
        float fi = float(i);
        uv *= 3.0;
        uv *= mat2(cos(fi*ROT), -sin(fi*ROT), sin(fi*ROT), cos(fi*ROT));
        col += (distance(sin(uv.x + cos(uv.y*10.0)/10.0 + iTime),cos(uv.y + cos(uv.x*10.0)/10.0)))/
            	pow(1.85, fi);
    }
    col += 0.8;
    col *= col;
    col = color(col.x/35.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Number of bits reserved for the fractional part
#define SHIFT 28u

int   initFixed(float x) {return int(x * float(1u << SHIFT));}
ivec2 initFixed(vec2  v) {return ivec2(initFixed(v.x), initFixed(v.y));}

// Fixed point squaring
int squareFixed(int a) {
    uint ua = uint(abs(a));
    uint ah = ua >> 16u;
    uint al = ua & 0xFFFFu;
    return int((ah * ah << 32u - SHIFT) + (ah * al >> SHIFT - 17u) + (al * al >> SHIFT));
}
ivec2 squareFixed(ivec2 v) {return ivec2(squareFixed(v.x), squareFixed(v.y));}

void mainImage(out vec4 O, in vec2 u) {
    float whiteLine = (0.5 * sin(iTime) + 0.5) * iResolution.x;
    vec2 U = (2.0 * u - iResolution.xy) / iResolution.y;
    float zoom = 8e-7;
    vec2 Coord = vec2(-0.743644, 0.1318255);
    float maxIters = 3072.0, nbIters = 0.0;

    if (u.x < whiteLine) {
        // Fixed point version
	    ivec2 C = initFixed(U * zoom) + initFixed(Coord); // more precise doing that way
        ivec2 Z = C;

        // Check divergence against a square (not circle) of size 4
        // Numbers can be kept smaller and we can be more aggressive with SHIFT, before getting overflows
        while (nbIters < maxIters && all(lessThan(abs(Z), initFixed(vec2(2))))) {
            ivec2 Z2 = squareFixed(Z);
            Z = ivec2(Z2.x, squareFixed(Z.x + Z.y) - Z2.x) - Z2.yy + C;
            nbIters++;
        }
    } else {
        // Floating point version
	    vec2 C = U * zoom + Coord;
        vec2 Z = C;

        while (nbIters < maxIters && all(lessThan(abs(Z), vec2(2)))) {
            Z = vec2(Z.x * Z.x - Z.y * Z.y, 2.0 * Z.x * Z.y) + C;
            nbIters++;
        }
    }

    // Coloring
    float t = nbIters / maxIters;
    O.rgb = nbIters < maxIters ? -0.5 * cos(6.2831853 * (t + vec3(-0.1, 0.0, 0.1))) + 0.5 : vec3(0.0);

    if (abs(u.x - whiteLine) < 1.0)
    	O.rgb = vec3(1.0);
}

