// # CAUSTICS 
// ############################
vec2 N22(vec2 p) {
    return fract(sin(vec2(dot(p, vec2(123.4, 748.6)), dot(p, vec2(547.3, 659.3))))*5232.85324);
}

float voronoi(vec2 p) {

    // divide the screen into a grid
    p *= 3.;
    // this is the id of a grid cell. X and y go from -3 to 3.
    vec2 id = floor(p);     
    // Coordinates inside a grid cell
    vec2 gv = fract(p);

    float minDist = 9.;

    // loop through 9 grid cells around the point and check 
    // which is the closest cell
    for (float y = -1.; y <= 1.; y++) {
        for (float x = -1.; x <= 1.; x++) {
            vec2 offset = vec2(x, y);
            vec2 n = N22(id+offset);
            // multiply because gv goes from -0.5 to .0.5
            vec2 p = .5+0.5*sin(n*iTime*4.);

            vec2 r = offset + p - gv;
            float d = dot(r, r);

            if (d < minDist) {
            	minDist = d;
            }
        }
    }

    return minDist;
}

// creates fake caustics with voronoi patterns and fBMs
float caustics(vec2 p) {
    float v = 0.0;
    float a = 0.4;
    for (int i = 0; i<3; i++) {
        v += voronoi(p)*a;
        p *= 2.0;
        a *= 0.5;
    }
    return v;
}

mat3 lookAt(vec3 origin, vec3 target, float roll) {

    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

float godRays(vec3 rayDir) {

    vec3 col = vec3(0.);
    float y = rayDir.y * 0.5 + 0.5;

    float a = atan(rayDir.x, rayDir.z);

    float rays = sin(a*28.+iTime) * sin(a*27.-iTime) * sin(a*22.);
    rays = max(0., rays);
    // cut the lower part of the rays
    rays *= smoothstep(0.2, 1., y);
    // decrease brightness
    rays *= smoothstep(.7, 0., y);

    return rays+0.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // normalize coordinates ranging from -0.5 to 0.5 and fix aspect ratio
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

    vec3 GROUND_COLOR = vec3(0.25);
    vec3 HORIZON_COLOR = vec3(0.0, 0.05, 0.2);
    vec3 LIGHT_DIR = vec3(1, 5, -4);

    // camera
    vec3 camPos = vec3(3.0*sin(iTime*0.25), 7.0, -1.*iTime);
    camPos = vec3(10.0*sin(iTime*0.2), 7., -9.);
    //camPos = vec3(3., 6., -9.);

    vec3 camTarg = vec3(1., 4., 4.);
    mat3 camMat = lookAt(camPos, camTarg, 0.0);
    vec3 rayDir = normalize(camMat * vec3(uv.xy, 1.0));

    vec3 col = vec3(1.0);

    Obj hitObj = rayMarch(camPos, rayDir);

    if (hitObj.type == WATER) {

      col = mix(vec3(1.), HORIZON_COLOR, pow(1.0 - pow(rayDir.y, 4.), 50.0));

    } else if (hitObj.type == GROUND) {

        float dif = getLight(hitObj.hitPos, LIGHT_DIR) * hitObj.hitPos.y * 0.26;
        col = vec3(dif);

        float caust = caustics(uv)*3.5;       
        col *= clamp(caust, 0.5, 1.);       

        // Use Beer's law for calculating attenuation
		vec3 attenuation = exp(-vec3(0.3, 0.15, 0.08)*hitObj.dist);
        col = mix(HORIZON_COLOR, col, attenuation);
    }

    float godRay = godRays(rayDir);

    vec3 lightColor = mix(vec3(1.), col, 1.0 - uv.y);
    vec3 finalColor = mix(col, lightColor, godRay);

    col = pow(finalColor,vec3(.454545));
 	fragColor = vec4(col,1.0);
}

// Licensed under Creative Commons CC0: https://creativecommons.org/share-your-work/public-domain/cc0/

// More 2D shapes here: https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

mat2 rotateZ(float angleDeg)
{
  // "radians — convert a quantity in degrees to radians"
  float angleRad = radians(angleDeg);
  // http://mathworld.wolfram.com/RotationMatrix.html
  mat2 rot = mat2(vec2(cos(angleRad), sin(angleRad)), vec2(-sin(angleRad), cos(angleRad)));
  return rot;
}

float circle(vec2 coord, float size)
{
  // "length — calculate the length of a vector"
  return length(coord)-size;
}

float rect(vec2 p, vec2 b)
{
    /*
    // "Traditional" CPU way-of-working could look something like this
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            plotPixel(positionX + x, positionY + y);
        }
    }

	// GPU way-of-working would then be like
	if (positionX >= x && positionX < x+width
		&& positionY >= y && positionY < y+height) {
		plotPixel(positionX, positionY);
	}
    */

  // "abs — return the absolute value of the parameter"
  vec2 d = abs(p) - b;
  // "min — return the lesser of two values"
  // "max — return the greater of two values"
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// Note that IQ provides much more efficient way to do this
// however this is to illustrate that more complex shapes
// can be created from simpler shapes
float xcross(vec2 p, vec2 b)
{
  p *= rotateZ(45.0);
  float dist = rect(p, b);

  p *= rotateZ(90.0);
  dist = min(dist, rect(p, b));

  return dist;
}

float triangle(vec2 p, vec2 h)
{
  // https://en.wikipedia.org/wiki/Steiner_tree_problem
  // "sqrt — return the square root of the parameter"
  float steinerRatio = sqrt(3.0)/2.0;
  return max(-h.y,max(abs(p).x*steinerRatio-p.y*0.5,p.y)-h.x*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // setup coordinates here
    // "uv" refers to UV mapping https://en.wikipedia.org/wiki/UV_mapping
    // just think UV as normalized coordinates in the screen
    // where x is 0.0 - 1.0 and y is 0.0 - 1.0
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 aspectRatio = vec2(iResolution.x/iResolution.y, -1.0);
    vec2 coord = aspectRatio * (uv - 0.5);

    // define shapes
    // "d" is for distance.
    // Shape is drawn in later part, when the value is negative :-)
    float d = triangle(coord + vec2(0.4,-0.03), vec2(0.12));

    d = min(d,
        // Circle with a hole
        max(circle(coord + vec2(0.12,0.0), 0.1),
        -circle(coord + vec2(0.12,0.0), 0.08))
    );
    d = min(d, xcross(coord + vec2(-0.13,0.0), vec2(0.04,0.1)));
    d = min(d, rect(coord + vec2(-0.4,0.0), vec2(0.09)));

    d = min(d, xcross(coord + vec2(-0.13,0.0), vec2(0.04,0.1)));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Apply shapes
    // " sign returns -1.0 if x is less than 0.0, 0.0 if x is equal to 0.0, and +1.0 if x is greater than 0.0. "
    col = sign(d)*col;
    vec3 shapeColor = vec3(1.0,0.0,0.0);
    if (uv.y > 0.5) {
      //antialiased version
      // "mix — linearly interpolate between two values"
      // "smoothstep performs smooth Hermite interpolation between 0 and 1 when edge0 < x < edge1. This is useful in cases where a threshold function with a smooth transition is desired."
      col = mix(col, shapeColor, 1.0-smoothstep(0.0,0.003,d));
    } else {
      //not antialiased version
      col = mix(col, shapeColor, d > 0.0 ? 0.0 : 1.0);
    }

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float scale = (iResolution.x/iResolution.y);
    vec2 uv = fragCoord/iResolution.y;
    float spread = iMouse.x / (2.0f * iResolution.x);
    vec2 p1 = vec2(spread * scale, 0.5f);
    vec2 p2 = vec2((1.0f-spread) * scale, 0.5f);
    float dist = 100.0f * (iMouse.y / iResolution.y);
    if(iMouse.xy == vec2(0)){
        dist = 100.0f;
    }
    float d1 = length(uv - p1) * dist;
    float d2 = length(uv - p2) * dist;
    float freq = 10.0f;
    float phase = 0.0f;
    float sum = sin(-freq*iTime + phase + d1) + sin(-freq*iTime + phase + d2);
    sum = sum + 2.0f;
    sum = sum / 4.0f;
    sum *= sum;
    vec3 col;
    col.r = sum;


    // Output to screen
    fragColor = vec4(col,1.0);
}

